"use client";

/**
 * Auth Components - v7.1 设计系统
 *
 * 组件列表:
 * - AuthLayout: 左右分栏布局 (PC) / 垂直布局 (Mobile)
 * - BrandVisual: 品牌视觉区 (BreathAura + 标语)
 * - AuthCard: 认证表单卡片
 */

import { memo, ReactNode } from "react";
import { cn } from "@/lib/utils";
import { Card } from "@/components/ui/Card";

// ═══════════════════════════════════════════════════════════════════════════
// AuthLayout - 认证页面布局
// PC: 左侧品牌视觉(50%) + 右侧表单(50%)
// Mobile: Logo + 表单垂直排列
// ═══════════════════════════════════════════════════════════════════════════

interface AuthLayoutProps {
  children: ReactNode;
  className?: string;
}

export const AuthLayout = memo(function AuthLayout({
  children,
  className,
}: AuthLayoutProps) {
  return (
    <div className={cn("min-h-screen bg-bg-primary", className)}>
      <div className="flex min-h-screen">
        {/* Left: Brand Visual - PC only */}
        <div className="hidden lg:flex lg:w-1/2 xl:w-[55%]">
          <BrandVisual />
        </div>

        {/* Right: Form Area */}
        <div className="flex-1 flex flex-col">
          {/* Mobile Header */}
          <header className="lg:hidden h-14 px-4 flex items-center border-b border-subtle/30">
            <div className="flex items-center gap-2">
              <span className="text-2xl">🌟</span>
              <span className="font-serif text-lg font-semibold text-text-primary">VibeLife</span>
            </div>
          </header>

          {/* Form Content */}
          <main className="flex-1 flex items-center justify-center p-4 md:p-8">
            <div className="w-full md:max-w-md">
              {children}
            </div>
          </main>

          {/* Footer */}
          <footer className="py-4 px-4 text-center">
            <p className="text-xs text-text-tertiary">
              © 2026 VibeLife. All rights reserved.
            </p>
          </footer>
        </div>
      </div>
    </div>
  );
});

// ═══════════════════════════════════════════════════════════════════════════
// BrandVisual - 品牌视觉区
// ═══════════════════════════════════════════════════════════════════════════

export const BrandVisual = memo(function BrandVisual() {
  return (
    <div className="relative w-full h-full bg-gradient-to-br from-vellum-100 via-vellum-50 to-mystic-100 overflow-hidden">
      {/* Background Pattern */}
      <div className="absolute inset-0 opacity-30">
        <div className="absolute top-1/4 left-1/4 w-96 h-96 rounded-full bg-gradient-radial from-mystic-300/50 to-transparent blur-3xl" />
        <div className="absolute bottom-1/4 right-1/4 w-80 h-80 rounded-full bg-gradient-radial from-gold-300/40 to-transparent blur-3xl" />
      </div>

      {/* Breath Aura Animation */}
      <div className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2">
        <div className="relative">
          {/* Outer ring */}
          <div className="absolute -inset-20 rounded-full bg-gradient-to-br from-mystic-400/20 to-gold-400/20 animate-pulse-soft" />
          {/* Middle ring */}
          <div className="absolute -inset-12 rounded-full bg-gradient-to-br from-mystic-300/30 to-gold-300/30 animate-pulse-soft animation-delay-150" />
          {/* Inner ring */}
          <div className="absolute -inset-4 rounded-full bg-gradient-to-br from-mystic-200/40 to-gold-200/40 animate-pulse-soft animation-delay-300" />
          {/* Center icon */}
          <div className="relative w-24 h-24 rounded-2xl bg-gradient-to-br from-mystic-500 to-gold-500 flex items-center justify-center shadow-float">
            <span className="text-5xl">🌟</span>
          </div>
        </div>
      </div>

      {/* Content */}
      <div className="absolute inset-0 flex flex-col items-center justify-center p-8 text-center">
        <div className="mt-48">
          <h1 className="font-serif text-4xl xl:text-5xl font-bold text-text-primary mb-4">
            VibeLife
          </h1>
          <p className="text-xl text-text-secondary mb-2">
            探索内心世界
          </p>
          <p className="text-base text-text-tertiary md:max-w-md">
            融合东方智慧与现代科技，为你提供个性化的命理分析与人生指引
          </p>
        </div>

        {/* Features */}
        <div className="absolute bottom-12 left-0 right-0 px-8">
          <div className="flex justify-center gap-8">
            {[
              { icon: "🌟", label: "八字命盘" },
              { icon: "🔮", label: "星座解读" },
              { icon: "💎", label: "身份画像" },
            ].map((item) => (
              <div key={item.label} className="flex flex-col items-center gap-2">
                <div className="w-12 h-12 rounded-xl bg-bg-card/80 backdrop-blur-sm shadow-card flex items-center justify-center">
                  <span className="text-2xl">{item.icon}</span>
                </div>
                <span className="text-sm text-text-secondary">{item.label}</span>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
});

// ═══════════════════════════════════════════════════════════════════════════
// AuthCard - 认证表单卡片
// ═══════════════════════════════════════════════════════════════════════════

interface AuthCardProps {
  title: string;
  subtitle?: string;
  children: ReactNode;
  footer?: ReactNode;
  className?: string;
}

export const AuthCard = memo(function AuthCard({
  title,
  subtitle,
  children,
  footer,
  className,
}: AuthCardProps) {
  return (
    <div className={cn("w-full", className)}>
      {/* Mobile Logo */}
      <div className="lg:hidden flex flex-col items-center mb-8">
        <div className="w-16 h-16 rounded-2xl bg-gradient-to-br from-mystic-500 to-gold-500 flex items-center justify-center shadow-elevated mb-4">
          <span className="text-3xl">🌟</span>
        </div>
        <h1 className="font-serif text-2xl font-bold text-text-primary">VibeLife</h1>
        <p className="text-sm text-text-secondary">探索内心世界</p>
      </div>

      {/* Card */}
      <Card variant="default" padding="lg" hover={false} className="lg:shadow-elevated">
        {/* Header */}
        <div className="mb-6">
          <h2 className="text-xl font-semibold text-text-primary mb-1">{title}</h2>
          {subtitle && (
            <p className="text-sm text-text-secondary">{subtitle}</p>
          )}
        </div>

        {/* Form Content */}
        {children}
      </Card>

      {/* Footer */}
      {footer && (
        <div className="mt-6 text-center">
          {footer}
        </div>
      )}
    </div>
  );
});

// ═══════════════════════════════════════════════════════════════════════════
// AuthDivider - 分隔线
// ═══════════════════════════════════════════════════════════════════════════

interface AuthDividerProps {
  text?: string;
  className?: string;
}

export const AuthDivider = memo(function AuthDivider({
  text = "或",
  className,
}: AuthDividerProps) {
  return (
    <div className={cn("relative my-6", className)}>
      <div className="absolute inset-0 flex items-center">
        <div className="w-full border-t border-subtle" />
      </div>
      <div className="relative flex justify-center text-xs">
        <span className="px-3 bg-bg-card text-text-tertiary">{text}</span>
      </div>
    </div>
  );
});

// ═══════════════════════════════════════════════════════════════════════════
// AuthLink - 认证页面链接
// ═══════════════════════════════════════════════════════════════════════════

interface AuthLinkProps {
  text: string;
  linkText: string;
  href: string;
  onClick?: () => void;
  className?: string;
}

export const AuthLink = memo(function AuthLink({
  text,
  linkText,
  href,
  onClick,
  className,
}: AuthLinkProps) {
  return (
    <p className={cn("text-sm text-text-secondary", className)}>
      {text}{" "}
      <a
        href={href}
        onClick={(e) => {
          if (onClick) {
            e.preventDefault();
            onClick();
          }
        }}
        className="text-accent-primary font-medium hover:underline"
      >
        {linkText}
      </a>
    </p>
  );
});

// ═══════════════════════════════════════════════════════════════════════════
// Exports
// ═══════════════════════════════════════════════════════════════════════════

export default {
  AuthLayout,
  BrandVisual,
  AuthCard,
  AuthDivider,
  AuthLink,
};

'use client'

/**
 * Onboarding Context - v2.1
 *
 * 管理整个 onboarding 流程的状态
 */

import React, { createContext, useContext, useReducer, useCallback, ReactNode } from 'react'
import type {
  OnboardingStep,
  OnboardingState,
  BirthInfo,
  ConcernType,
  PersonaType,
  BaziData,
  InterviewAnswer,
} from './types'

// ═══════════════════════════════════════════════════════════════════════════
// State & Actions
// ═══════════════════════════════════════════════════════════════════════════

const initialState: OnboardingState = {
  step: 'landing',
  birthInfo: null,
  concerns: [],
  gender: null,
  persona: null,
  baziData: null,
  interviewAnswers: [],
  letterContent: null,
}

type Action =
  | { type: 'SET_STEP'; payload: OnboardingStep }
  | { type: 'SET_BIRTH_INFO'; payload: BirthInfo }
  | { type: 'SET_CONCERNS'; payload: ConcernType[] }
  | { type: 'SET_GENDER'; payload: string }
  | { type: 'SET_PERSONA'; payload: PersonaType }
  | { type: 'SET_BAZI_DATA'; payload: BaziData }
  | { type: 'ADD_INTERVIEW_ANSWER'; payload: InterviewAnswer }
  | { type: 'SET_LETTER_CONTENT'; payload: string }
  | { type: 'RESET' }

function reducer(state: OnboardingState, action: Action): OnboardingState {
  switch (action.type) {
    case 'SET_STEP':
      return { ...state, step: action.payload }
    case 'SET_BIRTH_INFO':
      return { ...state, birthInfo: action.payload }
    case 'SET_CONCERNS':
      return { ...state, concerns: action.payload }
    case 'SET_GENDER':
      return { ...state, gender: action.payload }
    case 'SET_PERSONA':
      return { ...state, persona: action.payload }
    case 'SET_BAZI_DATA':
      return { ...state, baziData: action.payload }
    case 'ADD_INTERVIEW_ANSWER':
      return {
        ...state,
        interviewAnswers: [...state.interviewAnswers, action.payload],
      }
    case 'SET_LETTER_CONTENT':
      return { ...state, letterContent: action.payload }
    case 'RESET':
      return initialState
    default:
      return state
  }
}

// ═══════════════════════════════════════════════════════════════════════════
// Context
// ═══════════════════════════════════════════════════════════════════════════

interface OnboardingContextValue {
  state: OnboardingState
  // Navigation
  goToStep: (step: OnboardingStep) => void
  nextStep: () => void
  // Data setters
  setBirthInfo: (info: BirthInfo) => void
  setConcerns: (concerns: ConcernType[]) => void
  setGender: (gender: string) => void
  setPersona: (persona: PersonaType) => void
  setBaziData: (data: BaziData) => void
  addInterviewAnswer: (answer: InterviewAnswer) => void
  setLetterContent: (content: string) => void
  // Utils
  reset: () => void
}

const OnboardingContext = createContext<OnboardingContextValue | null>(null)

// Step order for nextStep navigation (v4.0: 90秒旅程)
// Phase 1: 相遇 (landing) -> Phase 2: 洞察 (loading) -> Phase 3: 共鸣 (aha) -> Phase 4: 期待 (conversion)
const STEP_ORDER: OnboardingStep[] = [
  'landing',    // 0-15秒: 输入生日
  'loading',    // 15-45秒: 八字排盘 + 星座分析
  'aha',        // 45-75秒: 核心洞察 + Identity Prism 预览
  'conversion', // 75-90秒: 今日运势预览 + 付费引导
]

// ═══════════════════════════════════════════════════════════════════════════
// Provider
// ═══════════════════════════════════════════════════════════════════════════

export function OnboardingProvider({ children }: { children: ReactNode }) {
  const [state, dispatch] = useReducer(reducer, initialState)

  const goToStep = useCallback((step: OnboardingStep) => {
    dispatch({ type: 'SET_STEP', payload: step })
  }, [])

  const nextStep = useCallback(() => {
    const currentIndex = STEP_ORDER.indexOf(state.step)
    if (currentIndex < STEP_ORDER.length - 1) {
      dispatch({ type: 'SET_STEP', payload: STEP_ORDER[currentIndex + 1] })
    }
  }, [state.step])

  const setBirthInfo = useCallback((info: BirthInfo) => {
    dispatch({ type: 'SET_BIRTH_INFO', payload: info })
  }, [])

  const setConcerns = useCallback((concerns: ConcernType[]) => {
    dispatch({ type: 'SET_CONCERNS', payload: concerns })
  }, [])

  const setGender = useCallback((gender: string) => {
    dispatch({ type: 'SET_GENDER', payload: gender })
  }, [])

  const setPersona = useCallback((persona: PersonaType) => {
    dispatch({ type: 'SET_PERSONA', payload: persona })
  }, [])

  const setBaziData = useCallback((data: BaziData) => {
    dispatch({ type: 'SET_BAZI_DATA', payload: data })
  }, [])

  const addInterviewAnswer = useCallback((answer: InterviewAnswer) => {
    dispatch({ type: 'ADD_INTERVIEW_ANSWER', payload: answer })
  }, [])

  const setLetterContent = useCallback((content: string) => {
    dispatch({ type: 'SET_LETTER_CONTENT', payload: content })
  }, [])

  const reset = useCallback(() => {
    dispatch({ type: 'RESET' })
  }, [])

  const value: OnboardingContextValue = {
    state,
    goToStep,
    nextStep,
    setBirthInfo,
    setConcerns,
    setGender,
    setPersona,
    setBaziData,
    addInterviewAnswer,
    setLetterContent,
    reset,
  }

  return (
    <OnboardingContext.Provider value={value}>
      {children}
    </OnboardingContext.Provider>
  )
}

// ═══════════════════════════════════════════════════════════════════════════
// Hook
// ═══════════════════════════════════════════════════════════════════════════

export function useOnboarding() {
  const context = useContext(OnboardingContext)
  if (!context) {
    throw new Error('useOnboarding must be used within OnboardingProvider')
  }
  return context
}

/**
 * Storage Utilities - Vercel Best Practices
 *
 * Caches localStorage/sessionStorage reads to avoid repeated I/O
 * Rule: js-cache-storage
 */

// Module-level cache for localStorage
const localStorageCache = new Map<string, string | null>()

// Module-level cache for sessionStorage
const sessionStorageCache = new Map<string, string | null>()

// Parsed JSON cache
const jsonCache = new Map<string, unknown>()

/**
 * Get item from localStorage with caching
 */
export function getLocalStorage(key: string): string | null {
  if (typeof window === 'undefined') return null

  if (!localStorageCache.has(key)) {
    localStorageCache.set(key, localStorage.getItem(key))
  }
  return localStorageCache.get(key) ?? null
}

/**
 * Set item in localStorage and update cache
 */
export function setLocalStorage(key: string, value: string): void {
  if (typeof window === 'undefined') return

  localStorage.setItem(key, value)
  localStorageCache.set(key, value)
  // Invalidate JSON cache for this key
  jsonCache.delete(`local:${key}`)
}

/**
 * Remove item from localStorage and cache
 */
export function removeLocalStorage(key: string): void {
  if (typeof window === 'undefined') return

  localStorage.removeItem(key)
  localStorageCache.delete(key)
  jsonCache.delete(`local:${key}`)
}

/**
 * Get parsed JSON from localStorage with caching
 */
export function getLocalStorageJSON<T>(key: string, defaultValue: T): T {
  const cacheKey = `local:${key}`

  if (jsonCache.has(cacheKey)) {
    return jsonCache.get(cacheKey) as T
  }

  const raw = getLocalStorage(key)
  if (!raw) {
    jsonCache.set(cacheKey, defaultValue)
    return defaultValue
  }

  try {
    const parsed = JSON.parse(raw) as T
    jsonCache.set(cacheKey, parsed)
    return parsed
  } catch {
    jsonCache.set(cacheKey, defaultValue)
    return defaultValue
  }
}

/**
 * Set JSON in localStorage
 */
export function setLocalStorageJSON<T>(key: string, value: T): void {
  const json = JSON.stringify(value)
  setLocalStorage(key, json)
  jsonCache.set(`local:${key}`, value)
}

/**
 * Get item from sessionStorage with caching
 */
export function getSessionStorage(key: string): string | null {
  if (typeof window === 'undefined') return null

  if (!sessionStorageCache.has(key)) {
    sessionStorageCache.set(key, sessionStorage.getItem(key))
  }
  return sessionStorageCache.get(key) ?? null
}

/**
 * Set item in sessionStorage and update cache
 */
export function setSessionStorage(key: string, value: string): void {
  if (typeof window === 'undefined') return

  sessionStorage.setItem(key, value)
  sessionStorageCache.set(key, value)
  jsonCache.delete(`session:${key}`)
}

/**
 * Get parsed JSON from sessionStorage with caching
 */
export function getSessionStorageJSON<T>(key: string, defaultValue: T): T {
  const cacheKey = `session:${key}`

  if (jsonCache.has(cacheKey)) {
    return jsonCache.get(cacheKey) as T
  }

  const raw = getSessionStorage(key)
  if (!raw) {
    jsonCache.set(cacheKey, defaultValue)
    return defaultValue
  }

  try {
    const parsed = JSON.parse(raw) as T
    jsonCache.set(cacheKey, parsed)
    return parsed
  } catch {
    jsonCache.set(cacheKey, defaultValue)
    return defaultValue
  }
}

/**
 * Clear all caches - call when storage might have changed externally
 */
export function clearStorageCache(): void {
  localStorageCache.clear()
  sessionStorageCache.clear()
  jsonCache.clear()
}

/**
 * Invalidate specific key in cache
 */
export function invalidateStorageCache(key: string): void {
  localStorageCache.delete(key)
  sessionStorageCache.delete(key)
  jsonCache.delete(`local:${key}`)
  jsonCache.delete(`session:${key}`)
}

// Listen for storage events from other tabs
if (typeof window !== 'undefined') {
  window.addEventListener('storage', (e) => {
    if (e.key) {
      invalidateStorageCache(e.key)
    } else {
      // Storage was cleared
      clearStorageCache()
    }
  })

  // Clear cache when tab becomes visible (might have changed in background)
  document.addEventListener('visibilitychange', () => {
    if (document.visibilityState === 'visible') {
      clearStorageCache()
    }
  })
}

// ============================================================================
// User-specific helpers
// ============================================================================

export interface CachedUser {
  user_id: string
  vibe_id?: string
  display_name?: string
  email?: string
  phone?: string
  nickname?: string
  avatar?: string
  birth_date?: string
  birth_time?: string
  timezone?: string
}

/**
 * Get cached user data
 */
export function getCachedUser(): CachedUser | null {
  return getLocalStorageJSON<CachedUser | null>('user', null)
}

/**
 * Set cached user data
 */
export function setCachedUser(user: CachedUser): void {
  setLocalStorageJSON('user', user)
}

/**
 * Get access token
 */
export function getAccessToken(): string | null {
  return getLocalStorage('access_token')
}

/**
