export function getVibeLifeProfile(): Record<string, unknown> {
  return getLocalStorageJSON('vibelife-profile', { basic: {}, life_context: {} })
}

'use client'

/**
 * Onboarding Error Boundary
 * 捕获 onboarding 流程中的错误,提供友好的降级体验
 */

import { Component, type ReactNode } from 'react'

interface Props {
  children: ReactNode
  fallback?: ReactNode
}

interface State {
  hasError: boolean
  error?: Error
}

export class OnboardingErrorBoundary extends Component<Props, State> {
  constructor(props: Props) {
    super(props)
    this.state = { hasError: false }
  }

  static getDerivedStateFromError(error: Error): State {
    return { hasError: true, error }
  }

  componentDidCatch(error: Error, errorInfo: React.ErrorInfo) {
    // 上报错误到监控系统
    console.error('Onboarding Error:', error, errorInfo)

    // TODO: 集成错误监控服务 (如 Sentry)
    // if (typeof window !== 'undefined' && window.Sentry) {
    //   window.Sentry.captureException(error, { extra: errorInfo })
    // }
  }

  handleRetry = () => {
    this.setState({ hasError: false, error: undefined })
  }

  handleGoHome = () => {
    window.location.href = '/'
  }

  render() {
    if (this.state.hasError) {
      if (this.props.fallback) {
        return this.props.fallback
      }

      return (
        <div className="min-h-screen bg-bg-primary flex flex-col items-center justify-center px-4">
          <div className="card w-full max-w-sm p-6 text-center">
            <div className="w-16 h-16 mx-auto mb-4 rounded-full bg-error/10 flex items-center justify-center">
              <svg
                className="w-8 h-8 text-error"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-3L13.732 4c-.77-1.333-2.694-1.333-3.464 0L3.34 16c-.77 1.333.192 3 1.732 3z"
                />
              </svg>
            </div>

            <h2 className="font-serif text-xl text-text-primary mb-2">
              哎呀，出了点问题
            </h2>
            <p className="text-sm text-text-secondary mb-6">
              别担心，这不是你的问题。请重试或返回首页。
            </p>

            <div className="flex gap-3">
              <button
                onClick={this.handleRetry}
                className="flex-1 py-3 rounded-md text-sm font-medium bg-bg-secondary text-text-primary hover:bg-bg-tertiary transition-colors"
              >
                重试
              </button>
              <button
                onClick={this.handleGoHome}
                className="flex-1 py-3 rounded-md text-sm font-medium bg-accent text-white hover:bg-accent-hover transition-colors"
              >
                返回首页
              </button>
            </div>

            {/* 开发环境显示错误详情 */}
            {process.env.NODE_ENV === 'development' && this.state.error && (
              <details className="mt-4 text-left">
                <summary className="text-xs text-text-disabled cursor-pointer">
                  错误详情 (仅开发环境可见)
                </summary>
                <pre className="mt-2 p-2 bg-bg-secondary rounded text-xs text-error overflow-auto max-h-32">
                  {this.state.error.message}
                  {'\n'}
                  {this.state.error.stack}
                </pre>
              </details>
            )}
          </div>
        </div>
      )
    }

    return this.props.children
  }
}

export default OnboardingErrorBoundary

