'use client'

/**
 * Membership Page - v5.2 性能优化版
 * 优化:
 * 1. 移除 framer-motion，添加 API 超时处理
 * 2. 提取内联组件为 memo 组件 (Vercel rule: rerender-extract-component)
 * 3. 使用 useCallback 稳定事件处理 (Vercel rule: rerender-stable-callback)
 * 4. 使用 lazy state initialization (Vercel rule: rerender-functional-setstate)
 */

import { useState, useEffect, useCallback, memo } from 'react'
import { useRouter } from 'next/navigation'
import { PCLayout } from '@/components/layout/PCLayout'
import { getLocalStorageJSON } from '@/utils/storage'

const API_BASE = process.env.NEXT_PUBLIC_API_BASE || 'http://localhost:8000/api/v1'

const FEATURES = [
  { icon: '📊', label: '每日运势(完整)' },
  { icon: '💬', label: '30次/天 AI对话' },
  { icon: '🔔', label: '智能触发提醒' },
  { icon: '💎', label: 'Identity Prism' },
  { icon: '📈', label: '完整大运/流年' },
  { icon: '💕', label: '关系配对分析' },
]

interface Plan {
  id: string
  price: number
  currency: string
  label: string
  unit: string
  save: string | null
  recommended?: boolean
}

const DEFAULT_PLANS: Plan[] = [
  { id: 'monthly', price: 9.90, currency: 'USD', label: '月付', unit: '/月', save: null },
  { id: 'quarterly', price: 24.90, currency: 'USD', label: '季付', unit: '/季', save: '省 15%', recommended: true },
  { id: 'yearly', price: 69.90, currency: 'USD', label: '年付', unit: '/年', save: '省 36%' },
]

const CN_PLANS: Plan[] = [
  { id: '1m', price: 39, currency: 'HKD', label: '月付', unit: '/月', save: null },
  { id: '3m', price: 99, currency: 'HKD', label: '季付', unit: '/季', save: '省~15%', recommended: true },
  { id: '12m', price: 298, currency: 'HKD', label: '年付', unit: '/年', save: '省~36%' },
]

// Helper function
function formatPrice(price: number, currency: string): string {
  if (currency === 'HKD') return `HK$${price}`
  return `$${price.toFixed(2)}`
}

// 提取为 memo 组件 (Vercel rule: rerender-extract-component)
interface AsideContentProps {
  region: string
}

const AsideContent = memo(function AsideContent({ region }: AsideContentProps) {
  return (
    <div className="space-y-6">
      <div className="pc-aside-section">
        <h3 className="pc-aside-title">🎁 会员专属</h3>
        <div className="space-y-3">
          <div className="p-4 bg-[var(--bg-callout)] rounded-lg">
            <p className="text-sm text-text-secondary">解锁全部高级功能，获得更深入的命理分析和个性化建议。</p>
          </div>
        </div>
      </div>
      <div className="pc-aside-section">
        <h3 className="pc-aside-title">🛡️ 保障</h3>
        <ul className="space-y-2 text-sm text-text-secondary">
          <li className="flex items-center gap-2"><span className="text-success">✓</span> 7天无理由退款</li>
          <li className="flex items-center gap-2"><span className="text-success">✓</span> 随时取消订阅</li>
          <li className="flex items-center gap-2"><span className="text-success">✓</span> 数据安全加密</li>
        </ul>
      </div>
      <div className="pc-aside-section">
