            <>
              <span className="px-3 py-1 bg-[var(--bg-secondary)] rounded">Visa/Mastercard</span>
              <span className="px-3 py-1 bg-[var(--bg-secondary)] rounded">Apple Pay</span>
            </>
          )}
        </div>
      </div>
    </div>
  )
})

interface PCContentProps {
