        <PCLayout
          breadcrumb={[
            { label: '首页', href: '/' },
            { label: '设置' }
          ]}
          aside={<AsideContent />}
          showCommandBar={false}
        >
          <PCContent />
        </PCLayout>
      </div>

      {/* 移动端: 简单单栏布局 */}
      <MobileContent />
    </>
  )
}

