'use client';

/**
 * Global Error Boundary - RSC 错误恢复
 *
 * 符合 V7 规范: async-suspense-boundaries
 * 捕获 RSC Payload 错误、hydration 错误等运行时错误
 */

import { useEffect } from 'react';
import { AlertTriangle, RefreshCw, Home } from 'lucide-react';

export default function Error({
  error,
  reset,
}: {
  error: Error & { digest?: string };
  reset: () => void;
}) {
  useEffect(() => {
    // Log error to console in development
    if (process.env.NODE_ENV === 'development') {
      console.error('App Error Boundary:', error);
    }
    // TODO: Send to error reporting service (Sentry)
  }, [error]);

  const handleReset = () => {
    reset();
  };

  const handleGoHome = () => {
    window.location.href = '/';
  };

  // 检测是否是 RSC/Hydration 相关错误
  const isRSCError = error.message?.includes('RSC') ||
                     error.message?.includes('hydrat') ||
                     error.message?.includes('Minified React error');

  return (
    <div className="min-h-screen flex items-center justify-center bg-bg-primary p-4">
      <div className="max-w-md w-full text-center">
        {/* Error icon */}
        <div className="w-16 h-16 mx-auto mb-6 rounded-full bg-error/10 flex items-center justify-center">
          <AlertTriangle className="w-8 h-8 text-error" />
        </div>

        {/* Error message */}
        <h1 className="font-serif text-2xl text-text-primary mb-2">
          {isRSCError ? '页面加载出错' : '出错了'}
        </h1>
        <p className="text-text-secondary mb-6">
          {isRSCError
            ? '页面组件加载失败，请尝试刷新页面。如果问题持续，请返回首页。'
            : '抱歉，页面遇到了���些问题。请尝试刷新页面或返回首页。'
          }
        </p>

        {/* Error details (development only) */}
        {process.env.NODE_ENV === 'development' && (
          <div className="mb-6 p-4 bg-bg-secondary rounded-lg text-left overflow-auto max-h-32">
            <code className="text-xs text-text-secondary break-all">
              {error.message}
              {error.digest && (
                <span className="block mt-2 text-text-disabled">
                  Digest: {error.digest}
                </span>
              )}
            </code>
          </div>
        )}

        {/* Action buttons */}
        <div className="flex gap-3 justify-center">
          <button
            onClick={handleReset}
            className="inline-flex items-center gap-2 px-4 py-2 bg-accent text-white rounded-lg hover:bg-accent/90 transition-colors"
          >
            <RefreshCw className="w-4 h-4" />
            重试
          </button>
          <button
            onClick={handleGoHome}
            className="inline-flex items-center gap-2 px-4 py-2 bg-bg-secondary text-text-primary rounded-lg hover:bg-bg-tertiary transition-colors"
          >
            <Home className="w-4 h-4" />
            返回首页
          </button>
        </div>
      </div>
    </div>
  );
}

