export default function HomePage() {
  const router = useRouter()
  const [isPremium] = useState(false)
  const [userName] = useState('用户')
  const [greeting] = useState(getGreeting)

  const today = useMemo(
    () => new Date().toLocaleDateString('zh-CN', { year: 'numeric', month: 'long', day: 'numeric' }),
    []
  )

  const handleNavigate = useCallback((path: string) => {
    router.push(path)
  }, [router])

  const handleFeatureClick = useCallback((id: string, available: boolean) => {
    if (available) {
      router.push(`/chat?skill=${id}`)
    }
  }, [router])

  const handleChatClick = useCallback((id: string) => {
    router.push(`/chat?id=${id}`)
  }, [router])

  const handleOmnibarFocus = useCallback(() => {
    router.push('/chat')
  }, [router])

  const mainContent = (
    <MainContent
      greeting={greeting}
      userName={userName}
      today={today}
      onNavigate={handleNavigate}
      onFeatureClick={handleFeatureClick}
      onChatClick={handleChatClick}
    />
  )

  return (
    <>
      {/* PC端: 使用 PCLayout 三栏布局 */}
      <div className="hidden lg:block">
        <PCLayout
          breadcrumb={[{ label: '首页' }]}
          aside={<AsideContent isPremium={isPremium} onNavigate={handleNavigate} />}
        >
          {mainContent}
        </PCLayout>
      </div>

      {/* 移动端: 简单单栏布局 */}
      <MobileLayout onOmnibarFocus={handleOmnibarFocus}>
        {mainContent}
      </MobileLayout>
    </>
  )
}

