  --background: 0 0% 4%;
  --foreground: 0 0% 98%;
  --card: 0 0% 9%;
  --card-foreground: 0 0% 98%;
  --muted: 0 0% 15%;
  --muted-foreground: 0 0% 64%;
  --border: 0 0% 15%;

  /* Vellum æ·±è‰²æ¨¡å¼ */
  --vellum-50: #1A1814;
  --vellum-100: #252119;
  --vellum-200: #302A1E;
  --vellum-300: #3B3323;
  --vellum-400: #463C28;
  --vellum-500: #5E5438;
  --vellum-600: #7A6E4D;
  --vellum-700: #9A8E6D;
  --vellum-800: #BAB092;
  --vellum-900: #DAD4C0;

  /* Gold æ·±è‰²æ¨¡å¼ */
  --gold-50: #1A1810;
  --gold-100: #2A2518;
  --gold-200: #3A3220;
  --gold-300: #4A3F28;
  --gold-400: #5A4C30;
  --gold-500: #7A6840;
  --gold-600: #9A8858;
  --gold-700: #BAA878;
  --gold-800: #DAC898;
  --gold-900: #EAE0C0;

  /* Mystic æ·±è‰²æ¨¡å¼ */
  --mystic-50: #18171A;
  --mystic-100: #252328;
  --mystic-200: #322F38;
  --mystic-300: #3F3B48;
  --mystic-400: #4C4758;
  --mystic-500: #5E5870;
