"use client";

import { cn } from "@/lib/utils";
import { type SkillType } from "./LuminousPaper";
import { BreathAura } from "./BreathAura";

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// VibeGlyph - LUMINOUS PAPER Design System
// å“ç‰Œå›¾æ ‡ï¼šäº”è¡Œå¾ªç¯å›¾å½¢ + å‘¼å¸å…‰æ™•
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

interface VibeGlyphProps {
  size?: "sm" | "md" | "lg" | "xl";
  skill?: SkillType;
  showAura?: boolean;
  animate?: boolean;
  className?: string;
}

const sizeConfig = {
  sm: { container: "w-16 h-16", icon: 24, aura: "sm" as const },
  md: { container: "w-24 h-24", icon: 36, aura: "md" as const },
  lg: { container: "w-32 h-32", icon: 48, aura: "lg" as const },
  xl: { container: "w-48 h-48", icon: 72, aura: "xl" as const },
};

const skillColors: Record<SkillType, { primary: string; secondary: string }> = {
  bazi: { primary: "#7A3F1A", secondary: "#B88A44" },
  zodiac: { primary: "#3C3A7A", secondary: "#A7A6F4" },
  mbti: { primary: "#0F766E", secondary: "#6EE7C3" },
  attach: { primary: "#B13C41", secondary: "#F3A6A9" },
  career: { primary: "#0E7490", secondary: "#67E8F9" },
};

// Five Elements Glyph SVG
function FiveElementsIcon({ size, skill }: { size: number; skill: SkillType }) {
  const colors = skillColors[skill];

  return (
    <svg
      width={size}
      height={size}
      viewBox="0 0 48 48"
      fill="none"
      xmlns="http://www.w3.org/2000/svg"
    >
      {/* Outer ring */}
      <circle
        cx="24"
        cy="24"
        r="20"
        stroke={colors.secondary}
        strokeWidth="1.5"
        strokeOpacity="0.3"
      />

      {/* Five elements points - pentagon arrangement */}
      <circle cx="24" cy="6" r="3" fill={colors.primary} opacity="0.9" /> {/* Water - top */}
      <circle cx="41" cy="18" r="3" fill={colors.primary} opacity="0.7" /> {/* Wood - right top */}
      <circle cx="36" cy="38" r="3" fill={colors.primary} opacity="0.8" /> {/* Fire - right bottom */}
      <circle cx="12" cy="38" r="3" fill={colors.primary} opacity="0.6" /> {/* Earth - left bottom */}
      <circle cx="7" cy="18" r="3" fill={colors.primary} opacity="0.75" /> {/* Metal - left top */}

      {/* Connecting lines */}
      <path
        d="M24 6 L41 18 L36 38 L12 38 L7 18 Z"
        stroke={colors.secondary}
        strokeWidth="1"
        strokeOpacity="0.4"
        fill="none"
      />

      {/* Inner star connections */}
      <path
        d="M24 6 L36 38 M24 6 L12 38 M41 18 L12 38 M7 18 L36 38 M41 18 L7 18"
        stroke={colors.secondary}
        strokeWidth="0.5"
        strokeOpacity="0.2"
        fill="none"
      />

      {/* Center point */}
      <circle cx="24" cy="24" r="4" fill={colors.primary} />
      <circle cx="24" cy="24" r="2" fill={colors.secondary} />
    </svg>
  );
}

export function VibeGlyph({
  size = "md",
  skill = "bazi",
  showAura = true,
  animate = true,
  className,
}: VibeGlyphProps) {
  const config = sizeConfig[size];

  return (
    <div
      className={cn(
        "vibe-glyph relative flex items-center justify-center",
        config.container,
        animate && "animate-float",
        className
      )}
    >
      {/* Breath aura background */}
      {showAura && (
        <BreathAura
          skill={skill}
          size={config.aura}
          position="center"
          intensity="medium"
          className="absolute"
        />
      )}

      {/* Icon */}
      <div className="vibe-glyph-inner relative z-10">
        <FiveElementsIcon size={config.icon} skill={skill} />
      </div>
    </div>
  );
}

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// SimpleVibeGlyph - Simpler version for smaller contexts
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

interface SimpleVibeGlyphProps {
  size?: number;
  skill?: SkillType;
  className?: string;
}

export function SimpleVibeGlyph({
  size = 24,
  skill = "bazi",
  className,
}: SimpleVibeGlyphProps) {
  const colors = skillColors[skill];

  return (
    <svg
      width={size}
      height={size}
      viewBox="0 0 24 24"
      fill="none"
      xmlns="http://www.w3.org/2000/svg"
      className={className}
    >
      <circle cx="12" cy="12" r="10" stroke={colors.secondary} strokeWidth="1" strokeOpacity="0.3" />
      <circle cx="12" cy="12" r="3" fill={colors.primary} />
      <circle cx="12" cy="12" r="1.5" fill={colors.secondary} />
    </svg>
  );
}

export default VibeGlyph;

"use client";

import { ButtonHTMLAttributes, forwardRef, memo } from "react";
import { cn } from "@/lib/utils";

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// Button - v7.1 Linear é£æ ¼è®¾è®¡ç³»ç»Ÿ
// å…‹åˆ¶ã€ç²¾è‡´ã€æµç•… + Spring åŠ¨æ•ˆ
// Vercel rule: rerender-memo
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

export interface ButtonProps extends ButtonHTMLAttributes<HTMLButtonElement> {
  variant?: "primary" | "secondary" | "ghost" | "danger" | "premium";
  size?: "sm" | "md" | "lg";
  isLoading?: boolean;
}

export const Button = memo(forwardRef<HTMLButtonElement, ButtonProps>(
  (
    {
      className,
      variant = "primary",
      size = "md",
      isLoading,
      children,
      disabled,
      ...props
    },
    ref
  ) => {
    const baseStyles = cn(
      "inline-flex items-center justify-center gap-2",
      "font-sans font-medium",
      "rounded-lg",
      "transition-all duration-fast ease-out-expo",
      "focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-offset-2 focus-visible:ring-accent",
      "disabled:pointer-events-none disabled:opacity-50",
      // v7.1 Spring åŠ¨æ•ˆ
      "active:scale-[0.97] hover:scale-[1.01]"
    );

    const variants = {
      // Primary: v7.1 é«˜çº§ç°æ£•
      primary: cn(
        "bg-accent-primary text-text-inverse",
        "hover:bg-accent-hover",
        "shadow-card hover:shadow-card-hover"
      ),
      // Secondary: æ— è¾¹æ¡†è®¾è®¡ + å¤šå±‚é˜´å½±
      secondary: cn(
        "bg-bg-card text-text-primary",
        "shadow-card",
        "hover:bg-bg-card-hover hover:shadow-card-hover"
      ),
      // Ghost: é€æ˜èƒŒæ™¯
      ghost: cn(
        "bg-transparent text-text-secondary",
        "hover:bg-bg-secondary hover:text-text-primary"
      ),
      // Danger: å±é™©æ“ä½œ
      danger: cn(
        "bg-error text-text-inverse",
        "hover:bg-error/90",
        "shadow-card"
      ),
      // Premium: é‡‘è‰²é«˜çº§æ„Ÿ
      premium: cn(
        "bg-gradient-to-r from-gold-600 to-gold-500 text-white",
        "hover:from-gold-700 hover:to-gold-600",
        "shadow-card hover:shadow-card-hover"
      ),
    };

    const sizes = {
      sm: "px-3 py-1.5 text-sm h-8",
      md: "px-4 py-2 text-base h-10",
      lg: "px-6 py-3 text-lg h-12",
    };

    return (
      <button
        ref={ref}
        className={cn(
          baseStyles,
          variants[variant],
          sizes[size],
          className
        )}
        disabled={disabled || isLoading}
        {...props}
      >
        {isLoading && (
          <svg
            className="animate-spin h-4 w-4"
            fill="none"
            viewBox="0 0 24 24"
          >
            <circle
              className="opacity-25"
              cx="12"
              cy="12"
              r="10"
              stroke="currentColor"
              strokeWidth="4"
            />
            <path
              className="opacity-75"
              fill="currentColor"
              d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4z"
            />
          </svg>
        )}
        {children}
      </button>
    );
  }
));

Button.displayName = "Button";

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// IconButton - å›¾æ ‡æŒ‰é’®
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

export interface IconButtonProps extends ButtonHTMLAttributes<HTMLButtonElement> {
  variant?: "primary" | "secondary" | "ghost";
  size?: "sm" | "md" | "lg";
}

export const IconButton = memo(forwardRef<HTMLButtonElement, IconButtonProps>(
  ({ className, variant = "ghost", size = "md", children, ...props }, ref) => {
    const variants = {
      primary: "bg-accent-primary text-text-inverse hover:bg-accent-hover shadow-card",
      secondary: "bg-bg-card text-text-primary shadow-card hover:bg-bg-card-hover hover:shadow-card-hover",
      ghost: "text-text-secondary hover:text-text-primary hover:bg-bg-secondary",
    };

    const sizes = {
      sm: "w-8 h-8",
      md: "w-10 h-10",
      lg: "w-12 h-12",
    };

    return (
      <button
        ref={ref}
        className={cn(
          "inline-flex items-center justify-center rounded-lg",
          "transition-all duration-fast ease-out-expo",
          "focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-accent",
          "disabled:pointer-events-none disabled:opacity-50",
          "active:scale-95 hover:scale-105",
          variants[variant],
          sizes[size],
          className
        )}
        {...props}
      >
        {children}
      </button>
    );
  }
));

IconButton.displayName = "IconButton";

