import type { Config } from "tailwindcss";

const config: Config = {
  darkMode: ["class"],
  content: [
    "./src/**/*.{js,ts,jsx,tsx,mdx}",
    "../../packages/ui/src/**/*.{js,ts,jsx,tsx}",
  ],
  theme: {
    screens: {
      sm: "640px",
      md: "768px",
      lg: "1024px",
      xl: "1280px",
      "2xl": "1440px",
      "3xl": "1920px",
    },
    extend: {
      // ═══════════════════════════════════════════════════════════════
      // v7.1 Linear 风格色彩系统
      // ═══════════════════════════════════════════════════════════════
      colors: {
        // 背景色系 - Linear 风格低饱和度
        bg: {
          primary: "var(--bg-primary)",
          secondary: "var(--bg-secondary)",
          tertiary: "var(--bg-tertiary)",
          card: "var(--bg-card)",
          "card-hover": "var(--bg-card-hover)",
          elevated: "var(--bg-elevated)",
        },
        // 文字色系
        text: {
          primary: "var(--text-primary)",
          secondary: "var(--text-secondary)",
          tertiary: "var(--text-tertiary)",
          disabled: "var(--text-disabled)",
          inverse: "var(--text-inverse)",
        },
        // 强调色
        accent: {
          DEFAULT: "var(--accent-primary)",
          primary: "var(--accent-primary)",
          secondary: "var(--accent-secondary)",
          hover: "var(--accent-hover)",
          premium: "var(--accent-premium)",
          muted: "rgba(120, 113, 108, 0.1)",
        },
        // 功能色
        success: "var(--color-success)",
        warning: "var(--color-warning)",
        error: "var(--color-error)",
        info: "var(--color-info)",

        // ═══════════════════════════════════════════════════════════════
        // v7.1 品牌色板 - Vellum, Gold, Mystic, Ink
        // ═══════════════════════════════════════════════════════════════
        vellum: {
          50: "var(--vellum-50)",
          100: "var(--vellum-100)",
          200: "var(--vellum-200)",
          300: "var(--vellum-300)",
          400: "var(--vellum-400)",
          500: "var(--vellum-500)",
          600: "var(--vellum-600)",
          700: "var(--vellum-700)",
          800: "var(--vellum-800)",
          900: "var(--vellum-900)",
        },
        gold: {
          50: "var(--gold-50)",
          100: "var(--gold-100)",
          200: "var(--gold-200)",
          300: "var(--gold-300)",
          400: "var(--gold-400)",
          500: "var(--gold-500)",
          600: "var(--gold-600)",
          700: "var(--gold-700)",
          800: "var(--gold-800)",
          900: "var(--gold-900)",
        },
        mystic: {
          50: "var(--mystic-50)",
          100: "var(--mystic-100)",
          200: "var(--mystic-200)",
          300: "var(--mystic-300)",
          400: "var(--mystic-400)",
          500: "var(--mystic-500)",
          600: "var(--mystic-600)",
          700: "var(--mystic-700)",
          800: "var(--mystic-800)",
          900: "var(--mystic-900)",
        },
        ink: {
          50: "var(--ink-50)",
          100: "var(--ink-100)",
          200: "var(--ink-200)",
          300: "var(--ink-300)",
          400: "var(--ink-400)",
          500: "var(--ink-500)",
          600: "var(--ink-600)",
          700: "var(--ink-700)",
          800: "var(--ink-800)",
          900: "var(--ink-900)",
        },

        // 语义色 (Tailwind 兼容)
        background: "hsl(var(--background))",
        foreground: "hsl(var(--foreground))",
        card: {
          DEFAULT: "hsl(var(--card))",
          foreground: "hsl(var(--card-foreground))",
        },
        muted: {
          DEFAULT: "hsl(var(--muted))",
          foreground: "hsl(var(--muted-foreground))",
        },
        border: "hsl(var(--border))",
        ring: "hsl(var(--ring))",
      },

      // ═══════════════════════════════════════════════════════════════
      // 字体系统 - Linear 风格 14px 基准
      // ═══════════════════════════════════════════════════════════════
      fontFamily: {
        sans: ["var(--font-sans)"],
        serif: ["var(--font-serif)"],
        mono: ["var(--font-mono)"],
      },
      fontSize: {
        xs: ["var(--text-xs)", { lineHeight: "1.4" }],
        sm: ["var(--text-sm)", { lineHeight: "1.5" }],
        base: ["var(--text-base)", { lineHeight: "1.5" }],
        lg: ["var(--text-lg)", { lineHeight: "1.5" }],
        xl: ["var(--text-xl)", { lineHeight: "1.4" }],
        "2xl": ["var(--text-2xl)", { lineHeight: "1.3" }],
        "3xl": ["var(--text-3xl)", { lineHeight: "1.25" }],
        "4xl": ["var(--text-4xl)", { lineHeight: "1.2" }],
      },

      // ═══════════════════════════════════════════════════════════════
      // 圆角系统
      // ═══════════════════════════════════════════════════════════════
      borderRadius: {
        sm: "var(--radius-sm)",
        DEFAULT: "var(--radius-md)",
        md: "var(--radius-md)",
        lg: "var(--radius-lg)",
        xl: "var(--radius-xl)",
        "2xl": "var(--radius-2xl)",
        full: "var(--radius-full)",
      },

      // ═══════════════════════════════════════════════════════════════
      // 阴影系统 - v7.1 Linear 风格多层阴影
      // ═══════════════════════════════════════════════════════════════
      boxShadow: {
        subtle: "var(--shadow-subtle)",
        soft: "var(--shadow-soft)",
        elevated: "var(--shadow-elevated)",
        float: "var(--shadow-float)",
        // v7.1 Card 专用多层阴影 - 无边框设计
        card: "var(--shadow-card)",
        "card-hover": "var(--shadow-card-hover)",
      },

      // ═══════════════════════════════════════════════════════════════
      // 边框色
      // ═══════════════════════════════════════════════════════════════
      borderColor: {
        subtle: "var(--border-subtle)",
        DEFAULT: "var(--border-default)",
        strong: "var(--border-strong)",
        focus: "var(--border-focus)",
      },

      // ═══════════════════════════════════════════════════════════════
      // 间距系统
      // ═══════════════════════════════════════════════════════════════
      spacing: {
        "4.5": "1.125rem",
        "5.5": "1.375rem",
      },

      // ═══════════════════════════════════════════════════════════════
      // 动画系统 - v7.1 Spring easing + 新动画
      // ═══════════════════════════════════════════════════════════════
      animation: {
        "fade-in": "fadeIn var(--duration-normal) var(--ease-out-expo)",
        "fade-in-up": "fadeInUp var(--duration-slow) var(--ease-out-expo)",
        "scale-in": "scaleIn var(--duration-normal) var(--ease-spring)",
        shimmer: "shimmer 2s ease-in-out infinite",
        "pulse-soft": "pulse-soft 1.4s ease-in-out infinite",
        // v7.1 新增动画
        "slide-in-up": "slideInUp var(--duration-normal) var(--ease-out-expo)",
        "slide-in-down": "slideInDown var(--duration-normal) var(--ease-out-expo)",
        "slide-in-left": "slideInLeft var(--duration-normal) var(--ease-out-expo)",
        "slide-in-right": "slideInRight var(--duration-normal) var(--ease-out-expo)",
        "spring-bounce": "springBounce 0.5s var(--ease-spring)",
        "spring-scale": "springScale 0.3s var(--ease-spring)",
      },
      keyframes: {
        fadeIn: {
          "0%": { opacity: "0" },
          "100%": { opacity: "1" },
        },
        fadeInUp: {
          "0%": { opacity: "0", transform: "translateY(8px)" },
          "100%": { opacity: "1", transform: "translateY(0)" },
        },
        scaleIn: {
          "0%": { opacity: "0", transform: "scale(0.95)" },
          "100%": { opacity: "1", transform: "scale(1)" },
        },
        shimmer: {
          "0%": { backgroundPosition: "-200% 0" },
          "100%": { backgroundPosition: "200% 0" },
        },
        "pulse-soft": {
          "0%, 100%": { opacity: "0.4", transform: "scale(0.8)" },
          "50%": { opacity: "1", transform: "scale(1)" },
        },
        // v7.1 新增关键帧
        slideInUp: {
          "0%": { opacity: "0", transform: "translateY(10px)" },
          "100%": { opacity: "1", transform: "translateY(0)" },
        },
        slideInDown: {
          "0%": { opacity: "0", transform: "translateY(-10px)" },
          "100%": { opacity: "1", transform: "translateY(0)" },
        },
        slideInLeft: {
          "0%": { opacity: "0", transform: "translateX(-10px)" },
          "100%": { opacity: "1", transform: "translateX(0)" },
        },
        slideInRight: {
          "0%": { opacity: "0", transform: "translateX(10px)" },
          "100%": { opacity: "1", transform: "translateX(0)" },
        },
        springBounce: {
          "0%": { transform: "scale(0.9)" },
          "50%": { transform: "scale(1.05)" },
          "100%": { transform: "scale(1)" },
        },
        springScale: {
          "0%": { transform: "scale(0.95)", opacity: "0" },
          "100%": { transform: "scale(1)", opacity: "1" },
        },
      },
      transitionTimingFunction: {
        spring: "var(--ease-spring)",
        "out-expo": "var(--ease-out-expo)",
        "in-out": "var(--ease-in-out)",
      },
      transitionDuration: {
        fast: "var(--duration-fast)",
        normal: "var(--duration-normal)",
        slow: "var(--duration-slow)",
      },
    },
  },
  plugins: [],
};

export default config;

