/* ═══════════════════════════════════════════════════════════════════════════
   VibeLife Design System v7.1 - Linear 风格升级
   克制、精致、流畅
   ═══════════════════════════════════════════════════════════════════════════ */

:root {
  /* ====================================================================== */
  /* 背景色系 - v7.1 Linear 风格 + 温暖东方美学                                */
  /* ====================================================================== */
  --bg-primary: #F8F7F4;        /* 柔和暖灰 - v7.1 升级 */
  --bg-secondary: #F3F2EF;
  --bg-tertiary: #EDEBE7;
  --bg-card: #FFFFFF;
  --bg-card-hover: #FAFAF8;
  --bg-elevated: #FFFFFF;

  /* ====================================================================== */
  /* 文字色系 - v7.1 精细层级 (深灰非纯黑)                                     */
  /* ====================================================================== */
  --text-primary: #262626;      /* 深灰非纯黑 - v7.1 升级 */
  --text-secondary: #525252;
  --text-tertiary: #737373;
  --text-disabled: #A3A3A3;
  --text-inverse: #FFFFFF;

  /* ====================================================================== */
  /* 强调色 - v7.1 高级灰棕                                                   */
  /* ====================================================================== */
  --accent-primary: #7A6B5A;    /* 高级灰棕 - v7.1 升级 */
  --accent-secondary: #9A8B7A;
  --accent-hover: #5A4D3E;
  --accent-premium: #B8860B;

  /* ====================================================================== */
  /* 功能色                                                                   */
  /* ====================================================================== */
  --color-success: #22C55E;
  --color-warning: #F59E0B;
  --color-error: #EF4444;
  --color-info: #3B82F6;

  /* ====================================================================== */
  /* 边框色系 - Linear 风格精致边框                                           */
  /* ====================================================================== */
  --border-subtle: rgba(0, 0, 0, 0.06);
  --border-default: rgba(0, 0, 0, 0.08);
  --border-strong: rgba(0, 0, 0, 0.12);
  --border-focus: rgba(0, 0, 0, 0.2);

  /* ====================================================================== */
  /* 阴影系统 - v7.1 Linear 风格多层阴影                                       */
  /* ====================================================================== */
  --shadow-subtle: 0 1px 2px rgba(0, 0, 0, 0.04);
  --shadow-soft:
    0 1px 3px rgba(0, 0, 0, 0.04),
    0 4px 12px rgba(0, 0, 0, 0.03);
  --shadow-elevated:
    0 2px 4px rgba(0, 0, 0, 0.04),
    0 8px 24px rgba(0, 0, 0, 0.06);
  --shadow-float:
    0 4px 8px rgba(0, 0, 0, 0.04),
    0 16px 48px rgba(0, 0, 0, 0.08);

  /* v7.1 Card 专用多层阴影 - 无边框设计 */
  --shadow-card:
    0 0 0 1px rgba(122, 107, 90, 0.04),
    0 2px 4px rgba(122, 107, 90, 0.02),
    0 4px 12px rgba(122, 107, 90, 0.04);
  --shadow-card-hover:
    0 0 0 1px rgba(122, 107, 90, 0.06),
    0 4px 8px rgba(122, 107, 90, 0.03),
    0 8px 24px rgba(122, 107, 90, 0.06);

  /* ====================================================================== */
  /* 字体系统 - Linear 风格                                                   */
  /* ====================================================================== */
  --font-sans: 'Inter', 'Noto Sans SC', system-ui, -apple-system, sans-serif;
  --font-serif: 'Noto Serif SC', 'Songti SC', Georgia, serif;
  --font-mono: 'JetBrains Mono', 'Fira Code', monospace;

  /* 字号 - 14px 基准 */
  --text-xs: 0.75rem;      /* 12px */
  --text-sm: 0.8125rem;    /* 13px */
  --text-base: 0.875rem;   /* 14px */
  --text-lg: 1rem;         /* 16px */
  --text-xl: 1.125rem;     /* 18px */
  --text-2xl: 1.25rem;     /* 20px */
  --text-3xl: 1.5rem;      /* 24px */
  --text-4xl: 2rem;        /* 32px */

  /* ====================================================================== */
  /* 圆角系统                                                                 */
  /* ====================================================================== */
  --radius-sm: 6px;
  --radius-md: 8px;
  --radius-lg: 12px;
  --radius-xl: 16px;
  --radius-2xl: 20px;
  --radius-full: 9999px;

  /* ====================================================================== */
  /* 间距系统                                                                 */
  /* ====================================================================== */
  --space-1: 0.25rem;
  --space-2: 0.5rem;
  --space-3: 0.75rem;
  --space-4: 1rem;
  --space-5: 1.25rem;
  --space-6: 1.5rem;
  --space-8: 2rem;
  --space-10: 2.5rem;
  --space-12: 3rem;

  /* ====================================================================== */
  /* 动效系统 - Spring easing                                                 */
  /* ====================================================================== */
  --ease-spring: cubic-bezier(0.34, 1.56, 0.64, 1);
  --ease-out-expo: cubic-bezier(0.16, 1, 0.3, 1);
  --ease-in-out: cubic-bezier(0.4, 0, 0.2, 1);
  --duration-fast: 150ms;
  --duration-normal: 200ms;
  --duration-slow: 300ms;

  /* ====================================================================== */
  /* Tailwind 语义变量                                                        */
  /* ====================================================================== */
  --background: 0 0% 98%;
  --foreground: 0 0% 9%;
  --card: 0 0% 100%;
  --card-foreground: 0 0% 9%;
  --muted: 0 0% 96%;
  --muted-foreground: 0 0% 45%;
  --border: 0 0% 90%;
  --ring: 0 0% 45%;

  /* ====================================================================== */
  /* 布局变量                                                                 */
  /* ====================================================================== */
  --sidebar-width: 240px;
  --sidebar-collapsed: 72px;
  --topbar-height: 56px;
  --commandbar-height: 64px;

  /* 层级 */
  --z-base: 1;
  --z-dropdown: 100;
  --z-sticky: 200;
  --z-modal-backdrop: 400;
  --z-modal: 500;
  --z-toast: 600;

  /* ====================================================================== */
  /* Vellum 色板 - 羊皮纸暖色调 (v7.1 规范)                                   */
  /* ====================================================================== */
  --vellum-50: #FDFCFA;
  --vellum-100: #FAF8F5;
  --vellum-200: #F5F2ED;
  --vellum-300: #EDE9E2;
  --vellum-400: #E2DDD4;
  --vellum-500: #D4CEC3;
  --vellum-600: #B8B1A4;
  --vellum-700: #9A9285;
  --vellum-800: #7C7568;
  --vellum-900: #5E584D;

  /* ====================================================================== */
  /* Gold 色板 - 沉稳金色 (v7.1 规范)                                         */
  /* ====================================================================== */
  --gold-50: #FDFCF8;
  --gold-100: #FAF7EE;
  --gold-200: #F2ECDA;
  --gold-300: #E6DCC0;
  --gold-400: #D4C9A0;
