  /* ====================================================================== */
  /* Mystic 色板 - 神秘紫灰调 (v7.1 规范)                                     */
  /* ====================================================================== */
  --mystic-50: #FAF9FB;
  --mystic-100: #F3F1F5;
  --mystic-200: #E5E1EB;
  --mystic-300: #D2CBDC;
  --mystic-400: #B5AAC5;
  --mystic-500: #8E7FA0;
  --mystic-600: #716480;
  --mystic-700: #564B61;
  --mystic-800: #3B3342;
  --mystic-900: #201C24;

  /* ====================================================================== */
  /* Ink 色板 - v7.1 文字墨色 (深灰非纯黑)                                     */
  /* ====================================================================== */
  --ink-50: #F8F8F8;
  --ink-100: #EFEFEF;
  --ink-200: #DCDCDC;
  --ink-300: #BDBDBD;
  --ink-400: #989898;
  --ink-500: #7C7C7C;
  --ink-600: #656565;
  --ink-700: #525252;
  --ink-800: #262626;           /* v7.1 升级 - 深灰非纯黑 */
  --ink-900: #1A1A1A;
}

/* ═══════════════════════════════════════════════════════════════════════════
   深色模式
   ═══════════════════════════════════════════════════════════════════════════ */

[data-theme="dark"], .dark {
  --bg-primary: #0A0A0A;
  --bg-secondary: #141414;
  --bg-tertiary: #1F1F1F;
  --bg-card: #171717;
