  --mystic-600: #7A7490;
  --mystic-700: #9A94B0;
  --mystic-800: #BAB4D0;
  --mystic-900: #DAD6E8;

  /* Ink 深色模式 */
  --ink-50: #1A1A1A;
  --ink-100: #2A2A2A;
  --ink-200: #3A3A3A;
  --ink-300: #4A4A4A;
  --ink-400: #5A5A5A;
  --ink-500: #7A7A7A;
  --ink-600: #9A9A9A;
  --ink-700: #BABABA;
  --ink-800: #DADADA;
  --ink-900: #F0F0F0;
}

/* ═══════════════════════════════════════════════════════════════════════════
   Base Layer
   ═══════════════════════════════════════════════════════════════════════════ */

@layer base {
  * {
    border-color: var(--border-default);
  }

  html {
    scroll-behavior: smooth;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    font-feature-settings: "cv02", "cv03", "cv04", "cv11";
  }

  body {
    background-color: var(--bg-primary);
    color: var(--text-primary);
    font-family: var(--font-sans);
    font-size: var(--text-base);
    line-height: 1.5;
    min-height: 100vh;
  }

  h1, h2, h3, h4, h5, h6 {
    font-family: var(--font-sans);
    font-weight: 600;
    letter-spacing: -0.02em;
    color: var(--text-primary);
  }

  ::selection {
    background: rgba(120, 113, 108, 0.2);
  }
}

/* ═══════════════════════════════════���═══════════════════════════════════════
   Components Layer - Linear 风格组件
   ═══════════════════════════════════════════════════════════════════════════ */

@layer components {
  /* ====================================================================== */
  /* Buttons - Linear 风格                                                   */
  /* ====================================================================== */
  .btn {
    display: inline-flex;
    align-items: center;
    justify-content: center;
    gap: 0.5rem;
    font-family: var(--font-sans);
    font-weight: 500;
    font-size: var(--text-base);
    border-radius: var(--radius-md);
    transition: all var(--duration-fast) var(--ease-out-expo);
    cursor: pointer;
  }

  .btn:focus-visible {
    outline: none;
    box-shadow: 0 0 0 2px var(--bg-primary), 0 0 0 4px var(--accent-primary);
  }

  .btn:disabled {
    pointer-events: none;
    opacity: 0.5;
  }

  .btn:active {
    transform: scale(0.98);
  }

  .btn-primary {
    background: var(--text-primary);
    color: var(--text-inverse);
    padding: 0.625rem 1rem;
    height: 40px;
  }

  .btn-primary:hover {
    background: var(--accent-hover);
  }

  .btn-secondary {
    background: var(--bg-card);
    color: var(--text-primary);
    border: 1px solid var(--border-default);
    padding: 0.625rem 1rem;
    height: 40px;
    box-shadow: var(--shadow-subtle);
  }

  .btn-secondary:hover {
    background: var(--bg-secondary);
    border-color: var(--border-strong);
  }

  .btn-ghost {
    background: transparent;
    color: var(--text-secondary);
    padding: 0.5rem 0.75rem;
  }

  .btn-ghost:hover {
    background: var(--bg-secondary);
    color: var(--text-primary);
  }

  .btn-sm {
    font-size: var(--text-sm);
    padding: 0.375rem 0.75rem;
    height: 32px;
  }

  .btn-lg {
    font-size: var(--text-lg);
    padding: 0.75rem 1.5rem;
    height: 48px;
  }

  /* ====================================================================== */
  /* Cards - Linear 风格                                                     */
  /* ====================================================================== */
  .card {
    background: var(--bg-card);
    border-radius: var(--radius-lg);
    border: 1px solid var(--border-subtle);
    box-shadow: var(--shadow-soft);
    transition: all var(--duration-normal) var(--ease-out-expo);
  }

  .card:hover {
    box-shadow: var(--shadow-elevated);
    border-color: var(--border-default);
  }

  .card-flat {
    background: var(--bg-card);
    border-radius: var(--radius-lg);
    border: 1px solid var(--border-default);
  }

  .card-elevated {
    background: var(--bg-elevated);
    border-radius: var(--radius-lg);
    box-shadow: var(--shadow-elevated);
  }

  /* ====================================================================== */
  /* Chat Bubbles - Linear 风格                                              */
  /* ====================================================================== */
  .chat-bubble-user {
    background: var(--text-primary);
    color: var(--text-inverse);
    border-radius: var(--radius-xl);
    border-bottom-right-radius: var(--radius-sm);
    padding: 0.75rem 1rem;
    max-width: 85%;
    margin-left: auto;
  }

  .chat-bubble-assistant {
    background: var(--bg-card);
    color: var(--text-primary);
    border: 1px solid var(--border-subtle);
    border-radius: var(--radius-xl);
    border-bottom-left-radius: var(--radius-sm);
    padding: 0.75rem 1rem;
    max-width: 85%;
    box-shadow: var(--shadow-subtle);
  }

  .chat-timestamp {
    font-size: var(--text-xs);
    color: var(--text-tertiary);
    margin-top: 0.25rem;
  }

  /* ====================================================================== */
  /* Input - Linear 风格                                                     */
  /* ====================================================================== */
  .input {
    width: 100%;
    background: var(--bg-card);
    color: var(--text-primary);
    border-radius: var(--radius-md);
    padding: 0.625rem 0.875rem;
    border: 1px solid var(--border-default);
    font-size: var(--text-base);
    transition: all var(--duration-fast) var(--ease-out-expo);
  }

  .input::placeholder {
    color: var(--text-disabled);
  }

  .input:focus {
    outline: none;
    border-color: var(--border-focus);
    box-shadow: 0 0 0 3px rgba(0, 0, 0, 0.05);
  }

  /* ====================================================================== */
  /* Omnibar - 聊天输入框                                                    */
  /* ====================================================================== */
  .omnibar {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    background: var(--bg-card);
    border-radius: var(--radius-2xl);
    padding: 0.5rem 0.5rem 0.5rem 1rem;
    border: 1px solid var(--border-default);
    box-shadow: var(--shadow-soft);
    transition: all var(--duration-fast) var(--ease-out-expo);
  }

  .omnibar:focus-within {
    border-color: var(--border-focus);
    box-shadow: var(--shadow-elevated);
  }

  .omnibar-input {
    flex: 1;
    background: transparent;
    border: none;
    outline: none;
    color: var(--text-primary);
    font-size: var(--text-base);
    resize: none;
  }

  .omnibar-input::placeholder {
    color: var(--text-disabled);
  }

  .omnibar-send {
    width: 36px;
    height: 36px;
    border-radius: var(--radius-full);
    background: var(--text-primary);
    color: var(--text-inverse);
    display: flex;
    align-items: center;
    justify-content: center;
    transition: all var(--duration-fast) var(--ease-out-expo);
    flex-shrink: 0;
  }

  .omnibar-send:hover {
    background: var(--accent-hover);
    transform: scale(1.05);
  }

  .omnibar-send:active {
    transform: scale(0.95);
  }

  /* ====================================================================== */
  /* Navigation                                                              */
  /* ====================================================================== */
  .nav {
    position: sticky;
    top: 0;
    z-index: var(--z-sticky);
    background: rgba(250, 250, 250, 0.8);
    backdrop-filter: blur(12px);
    -webkit-backdrop-filter: blur(12px);
    border-bottom: 1px solid var(--border-subtle);
  }

  .tab-bar {
    position: fixed;
    bottom: 0;
    left: 0;
    right: 0;
    z-index: var(--z-sticky);
    background: var(--bg-card);
    border-top: 1px solid var(--border-subtle);
    padding-bottom: env(safe-area-inset-bottom);
  }

  .tab-item {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 0.25rem;
    padding: 0.5rem 1rem;
    color: var(--text-tertiary);
    font-size: var(--text-xs);
    transition: color var(--duration-fast);
  }

  .tab-item.active {
    color: var(--text-primary);
  }
}

/* ═══════════════════════════════════════════════════════════════════════════
   Shimmer 动画 - Linear 风格微光效果
   ═══════════════════════════════════════════════════════════════════════════ */

@keyframes shimmer {
  0% {
    background-position: -200% 0;
  }
  100% {
    background-position: 200% 0;
  }
}

.animate-shimmer {
  animation: shimmer 2s ease-in-out infinite;
}

.shimmer-gradient {
  background: linear-gradient(
    90deg,
    transparent 0%,
    rgba(255, 255, 255, 0.4) 50%,
    transparent 100%
  );
  background-size: 200% 100%;
}

/* 骨架屏基础样式 */
.skeleton {
  position: relative;
  overflow: hidden;
  background-color: var(--bg-tertiary);
  border-radius: var(--radius-md);
}

.skeleton::before {
  content: '';
  position: absolute;
  inset: 0;
  background: linear-gradient(
    90deg,
    transparent 0%,
    rgba(255, 255, 255, 0.5) 50%,
    transparent 100%
  );
  background-size: 200% 100%;
  animation: shimmer 2s ease-in-out infinite;
}

/* 打字光标 */
.typing-cursor {
  display: inline-block;
  width: 2px;
  height: 1em;
  background: var(--accent-primary);
  margin-left: 2px;
  vertical-align: text-bottom;
  animation: blink 1s ease-in-out infinite;
}

@keyframes blink {
  0%, 50% { opacity: 1; }
  51%, 100% { opacity: 0; }
}

/* 流式文字加载点 */
.streaming-dots {
  display: inline-flex;
  align-items: center;
  gap: 3px;
  margin-left: 4px;
}

.streaming-dots span {
  width: 4px;
  height: 4px;
  border-radius: 50%;
  background: currentColor;
  opacity: 0.6;
  animation: pulse-soft 1.4s ease-in-out infinite;
}

.streaming-dots span:nth-child(2) {
  animation-delay: 0.2s;
}

.streaming-dots span:nth-child(3) {
  animation-delay: 0.4s;
}

@keyframes pulse-soft {
  0%, 100% { opacity: 0.4; transform: scale(0.8); }
  50% { opacity: 1; transform: scale(1); }
}

/* ═══════════════════════════════════════════════════════════════════════════
   Utilities Layer
   ═══════════════════════════════════════════════════════════════════════════ */

@layer utilities {
  .text-balance {
    text-wrap: balance;
  }

  .safe-bottom {
    padding-bottom: env(safe-area-inset-bottom);
  }

  /* 动画延迟 */
  .animation-delay-150 {
    animation-delay: 150ms;
  }

  .animation-delay-300 {
    animation-delay: 300ms;
  }

  /* Spring 动画 */
  .transition-spring {
    transition-timing-function: var(--ease-spring);
  }

  .transition-expo {
    transition-timing-function: var(--ease-out-expo);
  }
}

/* ═══════════════════════════════════════════════════════════════════════════
   Scrollbar - Linear 风格
   ═══════════════════════════════════════════════════════════════════════════ */

::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}

::-webkit-scrollbar-track {
  background: transparent;
}

::-webkit-scrollbar-thumb {
  background: var(--border-strong);
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: var(--text-disabled);
}

/* ═══════════════════════════════════════════════════════════════════════════
   Animations
   ═══════════════════════════════════════════════════════════════════════════ */

@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(8px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes scaleIn {
  from {
    opacity: 0;
    transform: scale(0.95);
  }
  to {
    opacity: 1;
    transform: scale(1);
  }
}

.fade-in {
  animation: fadeIn var(--duration-normal) var(--ease-out-expo) both;
}

.fade-in-up {
  animation: fadeInUp var(--duration-slow) var(--ease-out-expo) both;
}

.scale-in {
  animation: scaleIn var(--duration-normal) var(--ease-spring) both;
}

/* ═══════════════════════════════════════════════════════════════════════════
   PC Layout Components
   ═══════════════════════════════════════════════════════════════════════════ */

@layer components {
  .pc-layout {
    display: flex;
    min-height: 100vh;
    background: var(--bg-primary);
  }

  .pc-sidebar {
    position: fixed;
    left: 0;
    top: 0;
    bottom: 0;
    width: var(--sidebar-width);
    background: var(--bg-card);
    border-right: 1px solid var(--border-subtle);
    z-index: var(--z-sticky);
    display: flex;
    flex-direction: column;
    transition: width var(--duration-slow) var(--ease-out-expo);
  }

  .pc-sidebar.collapsed {
    width: var(--sidebar-collapsed);
  }

  .pc-nav-item {
    display: flex;
    align-items: center;
    gap: 12px;
    padding: 10px 14px;
    border-radius: var(--radius-md);
    color: var(--text-secondary);
    font-size: var(--text-base);
    transition: all var(--duration-fast) var(--ease-out-expo);
    cursor: pointer;
  }

  .pc-nav-item:hover {
    background: var(--bg-secondary);
    color: var(--text-primary);
  }

  .pc-nav-item.active {
    background: var(--bg-tertiary);
    color: var(--text-primary);
    font-weight: 500;
  }

  .pc-topbar {
    position: fixed;
    top: 0;
    left: var(--sidebar-width);
    right: 0;
    height: var(--topbar-height);
    background: rgba(250, 250, 250, 0.8);
    backdrop-filter: blur(12px);
    border-bottom: 1px solid var(--border-subtle);
    z-index: var(--z-sticky);
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 0 20px;
    transition: left var(--duration-slow) var(--ease-out-expo);
  }

  .pc-main {
    margin-left: var(--sidebar-width);
    margin-top: var(--topbar-height);
    margin-bottom: var(--commandbar-height);
    min-height: calc(100vh - var(--topbar-height) - var(--commandbar-height));
    display: flex;
    transition: margin-left var(--duration-slow) var(--ease-out-expo);
  }

  .pc-commandbar {
    position: fixed;
    bottom: 0;
    left: var(--sidebar-width);
    right: 0;
    height: var(--commandbar-height);
    background: var(--bg-primary);
    border-top: 1px solid var(--border-subtle);
    z-index: var(--z-sticky);
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 0 20px;
    transition: left var(--duration-slow) var(--ease-out-expo);
  }
}

/* 响应式 */
@media (max-width: 1279px) {
  .pc-sidebar {
    width: var(--sidebar-collapsed);
  }
  .pc-topbar {
    left: var(--sidebar-collapsed);
  }
  .pc-main {
    margin-left: var(--sidebar-collapsed);
  }
  .pc-commandbar {
    left: var(--sidebar-collapsed);
  }
}

@media (max-width: 1023px) {
  .pc-layout,
  .pc-sidebar,
  .pc-topbar,
  .pc-commandbar {
    display: none;
  }
}

/* ═══════════════════════════════════════════════════════════════════════════
   Performance Optimizations - Vercel Best Practices
   ═══════════════════════════════════════════════════════════════════════════ */

/* content-visibility for long lists (rule: rendering-content-visibility) */
.chat-message-item {
  content-visibility: auto;
  contain-intrinsic-size: 0 100px;
}

.list-item-virtualized {
  content-visibility: auto;
  contain-intrinsic-size: 0 60px;
}

/* Optimized animations - replace framer-motion for simple cases */
@keyframes slideInUp {
  from {
    opacity: 0;
    transform: translateY(10px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes slideInDown {
  from {
    opacity: 0;
    transform: translateY(-10px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes slideInLeft {
  from {
    opacity: 0;
    transform: translateX(-10px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes slideInRight {
  from {
    opacity: 0;
    transform: translateX(10px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

.animate-slide-in-up {
  animation: slideInUp var(--duration-normal) var(--ease-out-expo) both;
}

.animate-slide-in-down {
  animation: slideInDown var(--duration-normal) var(--ease-out-expo) both;
}

.animate-slide-in-left {
  animation: slideInLeft var(--duration-normal) var(--ease-out-expo) both;
}

.animate-slide-in-right {
  animation: slideInRight var(--duration-normal) var(--ease-out-expo) both;
}

/* Staggered animation delays for lists */
.stagger-item:nth-child(1) { animation-delay: 0ms; }
.stagger-item:nth-child(2) { animation-delay: 50ms; }
.stagger-item:nth-child(3) { animation-delay: 100ms; }
.stagger-item:nth-child(4) { animation-delay: 150ms; }
.stagger-item:nth-child(5) { animation-delay: 200ms; }
.stagger-item:nth-child(6) { animation-delay: 250ms; }
.stagger-item:nth-child(7) { animation-delay: 300ms; }
.stagger-item:nth-child(8) { animation-delay: 350ms; }

/* GPU-accelerated transforms for hover effects */
.hover-lift {
  transition: transform var(--duration-fast) var(--ease-out-expo),
              box-shadow var(--duration-fast) var(--ease-out-expo);
  will-change: transform;
}

.hover-lift:hover {
  transform: translateY(-2px);
  box-shadow: var(--shadow-elevated);
}

/* Reduce motion for accessibility */
@media (prefers-reduced-motion: reduce) {
  *,
  *::before,
  *::after {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
  }
}

