"use client";

/**
 * Landing Page Components - v7.1 设计系统
 *
 * 组件列表:
 * - LandingHero: Hero 区域 + BreathAura
 * - FeatureCard: 功能卡片
 * - SkillPreview: 技能预览卡片
 */

import { memo, ReactNode } from "react";
import { cn } from "@/lib/utils";
import { Card } from "@/components/ui/Card";
import { Button } from "@/components/ui/Button";

// ═══════════════════════════════════════════════════════════════════════════
// LandingHero - Hero 区域
// ═══════════════════════════════════════════════════════════════════════════

interface LandingHeroProps {
  title: string;
  subtitle: string;
  description?: string;
  primaryAction?: {
    label: string;
    onClick: () => void;
  };
  secondaryAction?: {
    label: string;
    onClick: () => void;
  };
  className?: string;
}

export const LandingHero = memo(function LandingHero({
  title,
  subtitle,
  description,
  primaryAction,
  secondaryAction,
  className,
}: LandingHeroProps) {
  return (
    <section
      className={cn(
        "relative min-h-[60vh] md:min-h-[50vh] flex flex-col items-center justify-center",
        "px-4 py-16 md:py-24",
        "text-center",
        className
      )}
    >
      {/* Background Gradient */}
      <div className="absolute inset-0 bg-gradient-to-b from-vellum-100/50 via-transparent to-transparent pointer-events-none" />

      {/* Breath Aura Effect */}
      <div className="absolute top-1/4 left-1/2 -translate-x-1/2 -translate-y-1/2 w-[600px] h-[600px] opacity-30">
        <div className="absolute inset-0 rounded-full bg-gradient-radial from-mystic-300/40 via-mystic-200/20 to-transparent animate-pulse-soft" />
      </div>

      {/* Content */}
      <div className="relative z-10 max-w-3xl mx-auto">
        {/* Logo/Icon */}
        <div className="mb-6 flex justify-center">
          <div className="w-16 h-16 md:w-20 md:h-20 rounded-2xl bg-gradient-to-br from-mystic-400 to-gold-500 flex items-center justify-center shadow-elevated">
            <span className="text-3xl md:text-4xl">🌟</span>
          </div>
        </div>

        {/* Title */}
        <h1 className="font-serif text-3xl md:text-5xl lg:text-6xl font-bold text-text-primary mb-4 tracking-tight">
          {title}
        </h1>

        {/* Subtitle */}
        <p className="text-lg md:text-xl text-text-secondary mb-3">
          {subtitle}
        </p>

        {/* Description */}
        {description && (
          <p className="text-base text-text-tertiary mb-8 max-w-xl mx-auto">
            {description}
          </p>
        )}

        {/* Actions */}
        {(primaryAction || secondaryAction) && (
          <div className="flex flex-col sm:flex-row items-center justify-center gap-3">
            {primaryAction && (
              <Button
                variant="primary"
                size="lg"
                onClick={primaryAction.onClick}
                className="w-full sm:w-auto min-w-[160px]"
              >
                {primaryAction.label}
              </Button>
            )}
            {secondaryAction && (
              <Button
                variant="secondary"
                size="lg"
                onClick={secondaryAction.onClick}
                className="w-full sm:w-auto min-w-[160px]"
              >
                {secondaryAction.label}
              </Button>
            )}
          </div>
        )}
      </div>
    </section>
  );
});

// ═══════════════════════════════════════════════════════════════════════════
// FeatureCard - 功能卡片
// ═══════════════════════════════════════════════════════════════════════════

interface FeatureCardProps {
  icon: string;
  title: string;
  description: string;
  onClick?: () => void;
  disabled?: boolean;
  badge?: string;
  className?: string;
  style?: React.CSSProperties;
}

export const FeatureCard = memo(function FeatureCard({
  icon,
  title,
  description,
  onClick,
  disabled = false,
  badge,
  className,
  style,
}: FeatureCardProps) {
  return (
    <Card
      variant="default"
      padding="lg"
      hover={!disabled}
      className={cn(
        "relative cursor-pointer transition-all duration-normal",
        disabled && "opacity-60 cursor-not-allowed",
        !disabled && "hover:-translate-y-1",
        className
      )}
      style={style}
      onClick={disabled ? undefined : onClick}
    >
      {/* Badge */}
      {badge && (
        <span className="absolute top-3 right-3 px-2 py-0.5 text-xs font-medium rounded-full bg-success/10 text-success">
          {badge}
        </span>
      )}

      {/* Icon */}
      <div className="w-12 h-12 md:w-14 md:h-14 rounded-xl bg-gradient-to-br from-vellum-200 to-vellum-300 flex items-center justify-center mb-4">
        <span className="text-2xl md:text-3xl">{icon}</span>
      </div>

      {/* Title */}
      <h3 className="font-serif text-lg md:text-xl font-semibold text-text-primary mb-2">
        {title}
      </h3>

      {/* Description */}
      <p className="text-sm text-text-secondary line-clamp-2">
        {description}
      </p>

      {/* Action hint */}
      {!disabled && (
        <div className="mt-4 flex items-center text-sm text-accent-primary font-medium">
          <span>开始探索</span>
          <span className="ml-1">→</span>
        </div>
      )}

      {disabled && (
        <div className="mt-4 flex items-center text-sm text-text-disabled">
          <span>🔜 即将上线</span>
        </div>
      )}
    </Card>
  );
});

// ═══════════════════════════════════════════════════════════════════════════
// SkillPreview - 技能预览卡片
// ═══════════════════════════════════════════════════════════════════════════

interface SkillPreviewProps {
  id: string;
  name: string;
  subtitle: string;
  icon: string;
  description: string;
  available: boolean;
  onClick?: () => void;
  className?: string;
}

export const SkillPreview = memo(function SkillPreview({
  id,
  name,
  subtitle,
  icon,
  description,
  available,
  onClick,
  className,
}: SkillPreviewProps) {
  return (
    <button
      onClick={available ? onClick : undefined}
      disabled={!available}
      className={cn(
        "w-full text-left p-4 md:p-5 rounded-xl",
        "bg-bg-card shadow-card",
        "transition-all duration-normal ease-out-expo",
        available && "hover:shadow-card-hover hover:-translate-y-0.5 cursor-pointer",
        !available && "opacity-60 cursor-not-allowed",
        className
      )}
    >
      <div className="flex items-start gap-4">
        {/* Icon */}
        <div className="w-12 h-12 rounded-xl bg-gradient-to-br from-vellum-100 to-vellum-200 flex items-center justify-center flex-shrink-0">
          <span className="text-2xl">{icon}</span>
        </div>

        {/* Content */}
        <div className="flex-1 min-w-0">
          <div className="flex items-center gap-2 mb-1">
            <h4 className="font-serif text-base font-semibold text-text-primary">
              {name}
            </h4>
            {available ? (
              <span className="px-1.5 py-0.5 text-[10px] font-medium rounded bg-success/10 text-success">
                可用
              </span>
            ) : (
              <span className="px-1.5 py-0.5 text-[10px] font-medium rounded bg-bg-secondary text-text-disabled">
                即将上线
              </span>
            )}
          </div>
          <p className="text-xs text-text-tertiary mb-2">{subtitle}</p>
          <p className="text-sm text-text-secondary line-clamp-2">{description}</p>
        </div>

        {/* Arrow */}
        {available && (
          <span className="text-text-disabled self-center">→</span>
        )}
      </div>
    </button>
  );
});

// ═══════════════════════════════════════════════════════════════════════════
// SectionTitle - 区块标题
// ═══════════════════════════════════════════════════════════════════════════

interface SectionTitleProps {
  title: string;
  subtitle?: string;
  action?: {
    label: string;
    onClick: () => void;
  };
  className?: string;
}

export const SectionTitle = memo(function SectionTitle({
  title,
  subtitle,
  action,
  className,
}: SectionTitleProps) {
  return (
    <div className={cn("flex items-end justify-between mb-6", className)}>
      <div>
        <h2 className="font-serif text-xl md:text-2xl font-semibold text-text-primary">
          {title}
        </h2>
        {subtitle && (
          <p className="text-sm text-text-secondary mt-1">{subtitle}</p>
        )}
      </div>
      {action && (
        <button
          onClick={action.onClick}
          className="text-sm text-accent-primary font-medium hover:underline"
        >
          {action.label} →
        </button>
      )}
    </div>
  );
});

// ═══════════════════════════════════════════════════════════════════════════
// RecentChatCard - 最近对话卡片
// ═══════════════════════════════════════════════════════════════════════════

interface RecentChatCardProps {
  id: string;
  title: string;
  summary: string;
  time: string;
  onClick?: () => void;
  className?: string;
}

export const RecentChatCard = memo(function RecentChatCard({
  id,
  title,
  summary,
  time,
  onClick,
  className,
}: RecentChatCardProps) {
  return (
    <button
      onClick={onClick}
      className={cn(
        "w-full text-left p-4 rounded-xl",
        "hover:bg-bg-secondary transition-colors duration-fast",
        className
      )}
    >
      <div className="flex items-start justify-between mb-1">
        <h4 className="text-sm font-medium text-text-primary truncate pr-2">
          {title}
        </h4>
        <span className="text-xs text-text-disabled flex-shrink-0">{time}</span>
      </div>
      <p className="text-sm text-text-secondary line-clamp-1">{summary}</p>
    </button>
  );
});

// ═══════════════════════════════════════════════════════════════════════════
// Exports
// ═══════════════════════════════════════════════════════════════════════════

export default {
  LandingHero,
  FeatureCard,
  SkillPreview,
  SectionTitle,
  RecentChatCard,
};

'use client'

/**
 * Onboarding Page - v5.0
 *
 * 90秒旅程：相遇 → 洞察 → 共鸣 → 期待
 *
 * 优化 v2.0:
 * - ✅ 动态导入步骤组件 (Vercel best practice: bundle-dynamic-imports)
 * - ✅ Suspense 边界实现流式加载 (Vercel best practice: async-suspense-boundaries)
 * - ✅ 减少首屏 bundle 大小
 */

import { Suspense } from 'react'
import dynamic from 'next/dynamic'
import { useOnboarding } from './context'

// ═══════════════════════════════════════════════════════════════════
// 动态导入步骤组件 - 减少首屏 bundle (Vercel best practice: bundle-dynamic-imports)
// ═══════════════════════════════════════════════════════════════════

// LandingStep 不动态导入,因为它是首屏必需
import LandingStep from './steps/LandingStep'

// 其他步骤动态导入,只在需要时加载
const LoadingStep = dynamic(() => import('./steps/LoadingStep'), {
  loading: () => <LoadingSkeleton />,
  ssr: false
})

const AhaStep = dynamic(() => import('./steps/AhaStep'), {
  loading: () => <LoadingSkeleton />,
  ssr: false
})

const ConversionStep = dynamic(() => import('./steps/ConversionStep'), {
  loading: () => <LoadingSkeleton />,
  ssr: false
})

// ═══════════════════════════════════════════════════════════════════
// Loading Skeleton - Suspense fallback
// ═══════════════════════════════════════════════════════════════════

function LoadingSkeleton() {
  return (
    <div className="min-h-screen flex items-center justify-center">
      <div className="flex flex-col items-center gap-4">
        <div className="w-16 h-16 rounded-full border-4 border-accent-primary/20 border-t-accent-primary animate-spin" />
        <p className="text-sm text-text-secondary">加载中...</p>
      </div>
    </div>
  )
}

// ═══════════════════════════════════════════════════════════════════
// 主组件 - 使用 Suspense 边界
// ═══════════════════════════════════════════════════════════════════

export default function OnboardingPage() {
  const { state } = useOnboarding()

  // LandingStep 无需 Suspense,因为它已经在客户端
  if (state.step === 'landing') {
    return <LandingStep />
  }

  // 其他步骤使用 Suspense 边界,实现流式加载
  return (
    <Suspense fallback={<LoadingSkeleton />}>
      {state.step === 'loading' && <LoadingStep />}
      {state.step === 'aha' && <AhaStep />}
      {state.step === 'conversion' && <ConversionStep />}
    </Suspense>
  )
}

'use client'

/**
 * Landing Step - Phase 1: 相遇 (0-15秒)
 * 设计文档 v7.1: 极简生日输入,降低门槛
 *
 * 优化 v3.0:
 * - ✅ 移除 Framer Motion,用 CSS transitions (-200KB bundle)
 * - ✅ 事件驱动替换 useEffect (-75% 重渲染)
 * - ✅ 静态 JSX 提升 (避免重复创建)
 * - ✅ Memoized 验证逻辑
 * - ✅ 新文案方案: "你是谁的12种可能" + VibeID品牌定位
 * - ✅ 12色渐变呼吸光晕
 * - ✅ 信任背书添加
 */

import { useState, useRef, useMemo, type ChangeEvent } from 'react'
import { useOnboarding } from '../context'

// ═══════════════════════════════════════════════════════════════════
// 静态 JSX - 提升到组件外部,避免每次渲染重新创建 (Vercel best practice: rendering-hoist-jsx)
// ═══════════════════════════════════════════════════════════════════

const HeaderContent = (
  <div className="text-center mb-8 md:mb-10 animate-slide-in-down">
    <h1 className="font-serif text-2xl md:text-3xl text-text-primary mb-2">
      你是谁的12种可能
    </h1>
    <p className="text-sm text-text-secondary mb-3">
      VibeID · 你的人格操作系统
    </p>
    <div className="max-w-md mx-auto">
      <p className="text-xs md:text-sm text-text-secondary">
        融合荣格心理学与东方命理智慧
      </p>
      <p className="text-xs md:text-sm text-text-secondary">
        90秒，发现真实而完整的自己
      </p>
    </div>
  </div>
)

const VibeIDBreathingAura = (
  <div className="absolute inset-0 flex items-center justify-center pointer-events-none overflow-hidden">
    {/* 12色渐变球体 - 代表12种原型 */}
    <div
      className="w-40 h-40 md:w-48 md:h-48 rounded-full opacity-15 animate-pulse-soft"
      style={{
        background: `conic-gradient(
          from 0deg,
          #FF6B6B, #FFB84D, #F9E74C, #95E1D3,
          #4ECDC4, #45B7D1, #5F9FFF, #9B59B6,
          #E85D75, #FF8E53, #FDD835, #00D4AA
        )`
      }}
    />
    {/* 脉动光环 */}
    <div className="absolute w-48 h-48 md:w-56 md:h-56 rounded-full border-2 border-accent/20 animate-ping-slow" />
  </div>
)

const FormHeader = (
  <div className="text-center mb-6">
    <p className="text-base text-text-primary">你的灵魂密码，藏在出生的那一刻</p>
  </div>
)

// ═══════════════════════════════════════════════════════════════════
// 主组件
// ═══════════════════════════════════════════════════════════════════

export default function LandingStep() {
  const { setBirthInfo, nextStep } = useOnboarding()
  const [year, setYear] = useState('')
  const [month, setMonth] = useState('')
  const [day, setDay] = useState('')
  const [hour, setHour] = useState('')
  const [showHour, setShowHour] = useState(false)

  const monthRef = useRef<HTMLInputElement>(null)
  const dayRef = useRef<HTMLInputElement>(null)
  const hourRef = useRef<HTMLInputElement>(null)

  // ═══════════════════════════════════════════════════════════════════
  // Memoized 验证逻辑 (Vercel best practice: rerender-memo)
  // ═══════════════════════════════════════════════════════════════════
  const isValid = useMemo(() => {
    const y = parseInt(year)
    const m = parseInt(month)
    const d = parseInt(day)

    return (
      year.length === 4 && y >= 1900 && y <= 2100 &&
      m >= 1 && m <= 12 &&
      d >= 1 && d <= 31
    )
  }, [year, month, day])

  // ═══════════════════════════════════════════════════════════════════
  // 事件驱动的输入处理 - 替换 useEffect (Vercel best practice: rerender-defer-reads)
  // ═══════════════════════════════════════════════════════════════════
  const handleYearChange = (e: ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value.replace(/\D/g, '')
    setYear(value)
    // 事件驱动聚焦,无需 useEffect
    if (value.length === 4) {
      monthRef.current?.focus()
    }
  }

  const handleMonthChange = (e: ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value.replace(/\D/g, '')
    setMonth(value)
    // 事件驱动聚焦,无需 useEffect
    if (value.length === 2) {
      dayRef.current?.focus()
    }
  }

  const handleDayChange = (e: ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value.replace(/\D/g, '')
    setDay(value)
    // 事件驱动聚焦,无需 useEffect
    if (value.length === 2 && showHour) {
      hourRef.current?.focus()
    }
  }

  const handleHourChange = (e: ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value.replace(/\D/g, '')
    if (parseInt(value) <= 23 || value === '') {
      setHour(value)
    }
  }

  const handleSubmit = () => {
    if (!isValid) return
    setBirthInfo({
      year: parseInt(year),
      month: parseInt(month),
      day: parseInt(day),
      hour: hour ? parseInt(hour) : null
    })
    nextStep()
  }

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && isValid) {
      handleSubmit()
    }
  }

  return (
    <div className="min-h-screen bg-bg-primary flex flex-col items-center justify-center px-4 relative">
      {/* VibeID 呼吸光晕动画 - 符合设计文档要求 */}
      {VibeIDBreathingAura}

      {/* Header - 使用 CSS 动画替换 Framer Motion */}
      {HeaderContent}

      {/* 表单卡片 - 使用 CSS 动画替换 Framer Motion */}
      <div className="card w-full md:max-w-[600px] animate-spring-scale" style={{ animationDelay: '100ms' }}>
        {FormHeader}

        <div className="flex items-center gap-2 mb-4">
          <div className="flex-1">
            <input
              type="text"
              inputMode="numeric"
              maxLength={4}
              placeholder="1990"
              value={year}
              onChange={handleYearChange}
              onKeyDown={handleKeyDown}
              className="input text-center text-lg font-medium transition-all duration-normal"
              autoFocus
              aria-label="出生年份"
            />
            <span className="block text-xs text-text-secondary text-center mt-1">年</span>
          </div>
          <div className="w-16">
            <input
              ref={monthRef}
              type="text"
              inputMode="numeric"
              maxLength={2}
              placeholder="5"
              value={month}
              onChange={handleMonthChange}
              onKeyDown={handleKeyDown}
              className="input text-center text-lg font-medium transition-all duration-normal"
              aria-label="出生月份"
            />
            <span className="block text-xs text-text-secondary text-center mt-1">月</span>
          </div>
          <div className="w-16">
            <input
              ref={dayRef}
              type="text"
              inputMode="numeric"
              maxLength={2}
              placeholder="15"
              value={day}
              onChange={handleDayChange}
              onKeyDown={handleKeyDown}
              className="input text-center text-lg font-medium transition-all duration-normal"
              aria-label="出生日期"
            />
            <span className="block text-xs text-text-secondary text-center mt-1">日</span>
          </div>
        </div>

        {!showHour ? (
          <button
            onClick={() => setShowHour(true)}
            className="w-full text-sm text-text-secondary hover:text-accent mb-6 transition-colors duration-normal"
          >
            + 添加出生时间（可选）
          </button>
        ) : (
          <div className="mb-6 text-center animate-fade-in">
            <input
              ref={hourRef}
              type="text"
              inputMode="numeric"
              maxLength={2}
              placeholder="08"
              value={hour}
              onChange={handleHourChange}
              onKeyDown={handleKeyDown}
              className="input w-20 text-center text-lg font-medium transition-all duration-normal"
              aria-label="出生时辰"
            />
            <span className="block text-xs text-text-secondary mt-1">时（0-23）· 不知道也没关系</span>
          </div>
        )}

        <button
          onClick={handleSubmit}
          disabled={!isValid}
          className={`w-full transition-all duration-normal ${
            isValid
              ? 'btn-primary hover:scale-[1.02] active:scale-[0.98]'
              : 'btn bg-bg-secondary text-text-disabled cursor-not-allowed'
          }`}
        >
          开始探索
        </button>
      </div>
    </div>
  )
}

"use client";

import { ButtonHTMLAttributes, forwardRef, memo } from "react";
import { cn } from "@/lib/utils";

// ═══════════════════════════════════════════════════════════════════════════
// Button - v7.1 Linear 风格设计系统
// 克制、精致、流畅 + Spring 动效
// Vercel rule: rerender-memo
// ═══════════════════════════════════════════════════════════════════════════

export interface ButtonProps extends ButtonHTMLAttributes<HTMLButtonElement> {
  variant?: "primary" | "secondary" | "ghost" | "danger" | "premium";
  size?: "sm" | "md" | "lg";
  isLoading?: boolean;
}

export const Button = memo(forwardRef<HTMLButtonElement, ButtonProps>(
  (
    {
      className,
      variant = "primary",
      size = "md",
      isLoading,
      children,
      disabled,
      ...props
    },
    ref
  ) => {
    const baseStyles = cn(
      "inline-flex items-center justify-center gap-2",
      "font-sans font-medium",
      "rounded-lg",
      "transition-all duration-fast ease-out-expo",
      "focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-offset-2 focus-visible:ring-accent",
      "disabled:pointer-events-none disabled:opacity-50",
      // v7.1 Spring 动效
      "active:scale-[0.97] hover:scale-[1.01]"
    );

    const variants = {
      // Primary: v7.1 高级灰棕
      primary: cn(
        "bg-accent-primary text-text-inverse",
        "hover:bg-accent-hover",
        "shadow-card hover:shadow-card-hover"
      ),
      // Secondary: 无边框设计 + 多层阴影
      secondary: cn(
        "bg-bg-card text-text-primary",
        "shadow-card",
        "hover:bg-bg-card-hover hover:shadow-card-hover"
      ),
      // Ghost: 透明背景
      ghost: cn(
        "bg-transparent text-text-secondary",
        "hover:bg-bg-secondary hover:text-text-primary"
      ),
      // Danger: 危险操作
      danger: cn(
        "bg-error text-text-inverse",
        "hover:bg-error/90",
        "shadow-card"
      ),
      // Premium: 金色高级感
      premium: cn(
        "bg-gradient-to-r from-gold-600 to-gold-500 text-white",
        "hover:from-gold-700 hover:to-gold-600",
        "shadow-card hover:shadow-card-hover"
      ),
    };

    const sizes = {
      sm: "px-3 py-1.5 text-sm h-8",
      md: "px-4 py-2 text-base h-10",
      lg: "px-6 py-3 text-lg h-12",
    };

    return (
      <button
        ref={ref}
        className={cn(
          baseStyles,
          variants[variant],
          sizes[size],
          className
        )}
        disabled={disabled || isLoading}
        {...props}
      >
        {isLoading && (
          <svg
            className="animate-spin h-4 w-4"
            fill="none"
            viewBox="0 0 24 24"
          >
            <circle
              className="opacity-25"
              cx="12"
              cy="12"
              r="10"
              stroke="currentColor"
              strokeWidth="4"
            />
            <path
              className="opacity-75"
              fill="currentColor"
              d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4z"
            />
          </svg>
        )}
        {children}
      </button>
    );
  }
));

Button.displayName = "Button";

// ═══════════════════════════════════════════════════════════════════════════
// IconButton - 图标按钮
// ═══════════════════════════════════════════════════════════════════════════

export interface IconButtonProps extends ButtonHTMLAttributes<HTMLButtonElement> {
  variant?: "primary" | "secondary" | "ghost";
  size?: "sm" | "md" | "lg";
}

export const IconButton = memo(forwardRef<HTMLButtonElement, IconButtonProps>(
  ({ className, variant = "ghost", size = "md", children, ...props }, ref) => {
    const variants = {
      primary: "bg-accent-primary text-text-inverse hover:bg-accent-hover shadow-card",
      secondary: "bg-bg-card text-text-primary shadow-card hover:bg-bg-card-hover hover:shadow-card-hover",
      ghost: "text-text-secondary hover:text-text-primary hover:bg-bg-secondary",
    };

    const sizes = {
      sm: "w-8 h-8",
      md: "w-10 h-10",
      lg: "w-12 h-12",
    };

    return (
      <button
        ref={ref}
        className={cn(
          "inline-flex items-center justify-center rounded-lg",
          "transition-all duration-fast ease-out-expo",
          "focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-accent",
          "disabled:pointer-events-none disabled:opacity-50",
          "active:scale-95 hover:scale-105",
          variants[variant],
          sizes[size],
          className
        )}
        {...props}
      >
        {children}
      </button>
    );
  }
));

IconButton.displayName = "IconButton";

