"use client";

import { HTMLAttributes, forwardRef, memo } from "react";
import { cn } from "@/lib/utils";

// ═══════════════════════════════════════════════════════════════════════════
// Card - v7.1 Linear 风格设计系统
// 无边框设计 + 多层阴影
// Vercel rule: rerender-memo
// ═══════════════════════════════════════════════════════════════════════════

export interface CardProps extends HTMLAttributes<HTMLDivElement> {
  variant?: "default" | "flat" | "elevated" | "glass";
  hover?: boolean;
  padding?: "none" | "sm" | "md" | "lg";
}

export const Card = memo(forwardRef<HTMLDivElement, CardProps>(
  ({ className, variant = "default", hover = true, padding = "md", children, ...props }, ref) => {
    const variants = {
      // Default: v7.1 无边框设计 + 多层阴影
      default: cn(
        "bg-bg-card",
        "shadow-card",
        hover && "hover:shadow-card-hover transition-shadow duration-normal ease-out-expo"
      ),
      // Flat: 纯色背景，微妙边框
      flat: cn(
        "bg-bg-card",
        "border border-subtle/50",
        hover && "hover:bg-bg-card-hover transition-colors duration-fast"
      ),
      // Elevated: 浮起效果
      elevated: cn(
        "bg-bg-elevated",
        "shadow-elevated",
        hover && "hover:shadow-float hover:-translate-y-0.5 transition-all duration-normal ease-out-expo"
      ),
      // Glass: 毛玻璃效果
      glass: cn(
        "bg-bg-card/80 backdrop-blur-md",
        "border border-white/20",
        "shadow-card",
        hover && "hover:bg-bg-card/90 hover:shadow-card-hover transition-all duration-normal"
      ),
    };

    const paddings = {
      none: "",
      sm: "p-3",
      md: "p-4",
      lg: "p-6",
    };

    return (
      <div
        ref={ref}
        className={cn(
          "rounded-xl",
          variants[variant],
          paddings[padding],
          className
        )}
        {...props}
      >
        {children}
      </div>
    );
  }
));

Card.displayName = "Card";

// ═══════════════════════════════════════════════════════════════════════════
// Card Sub-components
// ═══════════════════════════════════════════════════════════════════════════

export const CardHeader = memo(forwardRef<HTMLDivElement, HTMLAttributes<HTMLDivElement>>(
  ({ className, ...props }, ref) => (
    <div
      ref={ref}
      className={cn("pb-3", className)}
      {...props}
    />
  )
));
CardHeader.displayName = "CardHeader";

export const CardTitle = memo(forwardRef<HTMLHeadingElement, HTMLAttributes<HTMLHeadingElement>>(
  ({ className, ...props }, ref) => (
    <h3
      ref={ref}
      className={cn(
        "text-lg font-semibold tracking-tight text-text-primary",
        className
      )}
      {...props}
    />
  )
));
CardTitle.displayName = "CardTitle";

export const CardDescription = memo(forwardRef<HTMLParagraphElement, HTMLAttributes<HTMLParagraphElement>>(
  ({ className, ...props }, ref) => (
    <p
      ref={ref}
      className={cn("text-sm text-text-secondary mt-1", className)}
      {...props}
    />
  )
));
CardDescription.displayName = "CardDescription";

export const CardContent = memo(forwardRef<HTMLDivElement, HTMLAttributes<HTMLDivElement>>(
  ({ className, ...props }, ref) => (
    <div
      ref={ref}
      className={cn("", className)}
      {...props}
    />
  )
));
CardContent.displayName = "CardContent";

export const CardFooter = memo(forwardRef<HTMLDivElement, HTMLAttributes<HTMLDivElement>>(
  ({ className, ...props }, ref) => (
    <div
      ref={ref}
      className={cn("pt-3 flex items-center gap-3", className)}
      {...props}
    />
  )
));
CardFooter.displayName = "CardFooter";

// ═══════════════════════════════════════════════════════════════════════════
// ToolCard - 工具卡片专用
// ═══════════════════════════════════════════════════════════════════════════

export interface ToolCardProps extends HTMLAttributes<HTMLDivElement> {
  title?: string;
  icon?: React.ReactNode;
}

export const ToolCard = memo(forwardRef<HTMLDivElement, ToolCardProps>(
  ({ className, title, icon, children, ...props }, ref) => (
    <Card
      ref={ref}
      variant="default"
      padding="md"
      hover={false}
      className={cn("mt-2", className)}
      {...props}
    >
      {(title || icon) && (
        <div className="flex items-center gap-2 mb-3">
          {icon && <span className="text-text-secondary">{icon}</span>}
