import { useEffect, useState, useRef, useMemo } from 'react'
import { useOnboarding } from '../context'

const API_BASE = process.env.NEXT_PUBLIC_API_BASE || 'http://localhost:8000/api/v1'
const STEPS = ['解读八字', '分析星座', '融合洞察'] as const

