   201â†’export function ChatContainer({
   202â†’  skillId,
   203â†’  skill = "bazi",
   204â†’  conversationId,
   205â†’  voiceMode = "warm",
   206â†’  onConversationStart,
   207â†’}: ChatContainerProps) {
   208â†’  const [messages, setMessages] = useState<Message[]>([]);
   209â†’  const [isLoading, setIsLoading] = useState(false);
   210â†’  const [currentConvoId, setCurrentConvoId] = useState(conversationId);
   211â†’  const messagesEndRef = useRef<HTMLDivElement>(null);
   212â†’
   213â†’  // Map skillId to SkillType if not provided
   214â†’  const activeSkill: SkillType = skill || (skillId as SkillType) || "bazi";
