   219â†’
   220â†’  useEffect(() => {
   221â†’    scrollToBottom();
   222â†’  }, [messages]);
   223â†’
   224â†’  const handleSend = async (content: string) => {
   225â†’    // Add user message
   226â†’    const userMessage: Message = {
   227â†’      id: Date.now().toString(),
   228â†’      role: "user",
   229â†’      content,
   230â†’      timestamp: new Date().toLocaleTimeString(),
   231â†’    };
   232â†’    setMessages((prev) => [...prev, userMessage]);
   233â†’    setIsLoading(true);
   234â†’
   235â†’    try {
   236â†’      const { accessToken } = getTokens();
   237â†’
   238â†’      if (accessToken) {
   239â†’        // Create placeholder for streaming response
   240â†’        const assistantMsgId = (Date.now() + 1).toString();
   241â†’        setMessages((prev) => [...prev, {
   242â†’          id: assistantMsgId,
   243â†’          role: "assistant",
   244â†’          content: "",
   245â†’          timestamp: new Date().toLocaleTimeString(),
   246â†’        }]);
   247â†’
   248â†’        // Use streaming API
   249â†’        await streamChat(
   250â†’          {
   251â†’            message: content,
   252â†’            skill: skillId,
   253â†’            conversation_id: currentConvoId,
   254â†’            voice_mode: voiceMode,
   255â†’          },
   256â†’          // onChunk
   257â†’          (chunk) => {
   258â†’            setMessages((prev) =>
   259â†’              prev.map((msg) =>
   260â†’                msg.id === assistantMsgId
   261â†’                  ? { ...msg, content: msg.content + chunk }
   262â†’                  : msg
   263â†’              )
   264â†’            );
   265â†’          },
   266â†’          // onDone
   267â†’          (result) => {
   268â†’            if (!currentConvoId && result.conversation_id) {
   269â†’              setCurrentConvoId(result.conversation_id);
   270â†’              onConversationStart?.(result.conversation_id);
   271â†’            }
   272â†’            setIsLoading(false);
   273â†’          },
   274â†’          // onError
   275â†’          (error) => {
   276â†’            console.error("Stream error:", error);
   277â†’            setMessages((prev) =>
   278â†’              prev.map((msg) =>
   279â†’                msg.id === assistantMsgId
   280â†’                  ? { ...msg, content: "æŠ±æ­‰ï¼Œå‡ºäº†ç‚¹é—®é¢˜ã€‚è¯·ç¨åå†è¯•ã€‚" }
   281â†’                  : msg
   282â†’              )
   283â†’            );
   284â†’            setIsLoading(false);
   285â†’          }
   286â†’        );
   287â†’        return; // Early return since streaming handles setIsLoading
   288â†’      } else {
   289â†’        // Guest mode: use the public endpoint for progressive disclosure
   290â†’        const response = await sendGuestMessage(content, skillId);
   291â†’        const assistantMessage: Message = {
   292â†’          id: (Date.now() + 1).toString(),
   293â†’          role: "assistant",
   294â†’          content: response.content,
   295â†’          timestamp: new Date().toLocaleTimeString(),
   296â†’        };
   297â†’        setMessages((prev) => [...prev, assistantMessage]);
   298â†’
   299â†’        if (response.suggestion) {
   300â†’          setMessages((prev) => [
   301â†’            ...prev,
   302â†’            {
   303â†’              id: (Date.now() + 2).toString(),
   304â†’              role: "assistant",
   305â†’              content: response.suggestion,
   306â†’              timestamp: new Date().toLocaleTimeString(),
   307â†’            },
   308â†’          ]);
   309â†’        }
   310â†’      }
   311â†’    } catch (error) {
   312â†’      console.error("Chat error:", error);
   313â†’      setMessages((prev) => [...prev, {
   314â†’        id: (Date.now() + 1).toString(),
   315â†’        role: "assistant",
   316â†’        content: "æŠ±æ­‰ï¼Œå‡ºäº†ç‚¹é—®é¢˜ã€‚è¯·ç¨åå†è¯•ã€‚",
   317â†’      }]);
   318â†’    } finally {
   319â†’      setIsLoading(false);
   320â†’    }
   321â†’  };
   322â†’
   323â†’  const hasMessages = messages.length > 0;
   324â†’
   325â†’  return (
   326â†’    <div className="flex flex-col h-full" data-skill={activeSkill}>
   327â†’      {/* Messages area - always flex-1 to push input to bottom */}
   328â†’      <div className="flex-1 overflow-y-auto relative min-h-0">
   329â†’        {!hasMessages ? (
   330â†’          /* Empty state - centered within scrollable area */
   331â†’          <div className="flex flex-col items-center justify-end h-full pb-4 px-4">
   332â†’            <ChatEmptyState skill={activeSkill} onQuickPrompt={handleSend} />
   333â†’          </div>
