   348â†’                      type={message.insight.insight_type}
   349â†’                      title={message.insight.title}
   350â†’                      content={message.insight.content}
   351â†’                      onSave={() => console.log("Save insight:", message.insight?.id)}
   352â†’                      onShare={() => console.log("Share insight:", message.insight?.id)}
   353â†’                    />
   354â†’                  </div>
   355â†’                )}
   356â†’              </div>
   357â†’            ))}
   358â†’            {isLoading && (
   359â†’              <ChatMessage
   360â†’                role="assistant"
   361â†’                content=""
   362â†’                isStreaming
   363â†’              />
   364â†’            )}
   365â†’            <div ref={messagesEndRef} />
   366â†’          </div>
   367â†’        )}
   368â†’      </div>
   369â†’
   370â†’      {/* Input - fixed at bottom */}
   371â†’      <div className="flex-shrink-0">
   372â†’        <ChatInput onSend={handleSend} disabled={isLoading} />
   373â†’      </div>
