function useDailyGreeting() {
  return useMemo(() => {
    const now = new Date();
    const hour = now.getHours();

    // åŸºäºæ—¶é—´çš„é—®å€™è¯­
    const greetings = {
      morning: "æ—©å®‰ï¼Œæ„¿ä½ ä»Šæ—¥èƒ½é‡æ»¡æ»¡",
      afternoon: "åˆåæ—¶å…‰ï¼Œé€‚åˆæ²‰æ·€æ€è€ƒ",
      evening: "æ™šé—´é™è°§ï¼Œå›é¡¾ä»Šæ—¥æ”¶è·",
    };

    let greeting = greetings.morning;
    if (hour >= 12 && hour < 18) greeting = greetings.afternoon;
    else if (hour >= 18) greeting = greetings.evening;

    // èŠ‚æ°”ï¼ˆç®€åŒ–ç‰ˆï¼Œå®é™…åº”æ ¹æ®æ—¥æœŸè®¡ç®—ï¼‰
    const solarTerms = ["å°å¯’", "å¤§å¯’", "ç«‹æ˜¥", "é›¨æ°´", "æƒŠè›°", "æ˜¥åˆ†"];
    const termIdx = Math.floor((now.getMonth() * 2 + Math.floor(now.getDate() / 15)) % 6);

    return {
      greeting,
      solarTerm: solarTerms[termIdx],
      termDescription: "ä¸‡ç‰©ç”Ÿé•¿",
      todayTip: "ä»Šæ—¥å®œä¸“æ³¨å½“ä¸‹ï¼Œä¸€æ­¥ä¸€è„šå°",
    };
  }, []);
}

