// äº”è¡Œç±»å‹
type Element = "wood" | "fire" | "earth" | "metal" | "water";

// å¤©å¹²åœ°æ”¯æ•°æ®
interface Pillar {
  stem: string;
  branch: string;
  element: Element;
  tenGod?: string;
}

interface BaziData {
  year: Pillar;
  month: Pillar;
  day: Pillar;
  hour?: Pillar;
}

interface ChartPanelProps {
  skill: SkillType;
  /** å…«å­—æ•°æ® (ä»… bazi skill) */
  baziData?: BaziData;
  /** Now/Next æ•°æ® (æ–°æ ¼å¼) */
  nowNextData?: {
    now: {
      theme: string;
      warning?: string;
      action?: string;
    };
    next: {
      startDate: string;
      theme: string;
    };
  };
  /** Kçº¿ç®€è¦æ•°æ® */
  klinePreview?: {
    currentScore: number;
    trend: "up" | "down" | "stable";
    period: string;
  };
  /** å®Œæ•´ K çº¿æ•°æ® */
  klineData?: FortuneDataPoint[];
  /** Identity Prism æ•°æ® */
  prismContent?: PrismContent;
  /** æ˜¯å¦æœ‰å®Œæ•´æŠ¥å‘Š */
  hasReport?: boolean;
  /** æ˜¯å¦å±•å¼€ K çº¿ */
  showFullKLine?: boolean;
  onViewKLine?: () => void;
  onGenerateReport?: () => void;
  onDownloadReport?: () => void;
  className?: string;
}

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// Skill-specific Content
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

const SKILL_CHART_CONFIG: Record<SkillType, {
  klineLabel: string;
  reportLabel: string;
  identityLabel: string;
}> = {
  bazi: {
    klineLabel: "äººç”Ÿ K çº¿",
    reportLabel: "å…«å­—å®Œæ•´æŠ¥å‘Š",
    identityLabel: "å‘½ç›˜èº«ä»½è§†è§’",
  },
  zodiac: {
    klineLabel: "æ˜Ÿè±¡å‘¨æœŸ",
    reportLabel: "æ˜Ÿç›˜å®Œæ•´æŠ¥å‘Š",
    identityLabel: "æ˜Ÿç›˜èº«ä»½è§†è§’",
  },
  mbti: {
    klineLabel: "æˆé•¿è½¨è¿¹",
    reportLabel: "äººæ ¼å®Œæ•´æŠ¥å‘Š",
    identityLabel: "äººæ ¼èº«ä»½è§†è§’",
  },
  attach: {
    klineLabel: "å…³ç³»è½¨è¿¹",
    reportLabel: "ä¾æ‹æ¨¡å¼æŠ¥å‘Š",
    identityLabel: "ä¾æ‹èº«ä»½è§†è§’",
  },
  career: {
    klineLabel: "èŒä¸šè½¨è¿¹",
    reportLabel: "èŒä¸šå‘å±•æŠ¥å‘Š",
    identityLabel: "èŒä¸šèº«ä»½è§†è§’",
  },
};

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// Sub-components
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

function NowNextCard({ data }: { data: ChartPanelProps["nowNextData"] }) {
  if (!data) {
    return (
      <div className="glass-card rounded-xl p-4 text-center text-muted-foreground text-sm">
        å®Œæˆæ›´å¤šå¯¹è¯åï¼Œè¿™é‡Œä¼šæ˜¾ç¤ºä½ çš„å½“å‰é˜¶æ®µå’Œä¸‹ä¸€æ­¥å»ºè®®
      </div>
    );
  }

  return <NowNextCardComponent now={data.now} next={data.next} />;
}

function KLinePreviewCard({
  data,
  label,
  onClick,
}: {
  data?: ChartPanelProps["klinePreview"];
  label: string;
  onClick?: () => void;
}) {
  const getTrendIcon = () => {
    if (!data) return null;
    switch (data.trend) {
      case "up":
        return <TrendingUp className="w-4 h-4 text-emerald-500" />;
      case "down":
        return <TrendingUp className="w-4 h-4 text-red-500 rotate-180" />;
      default:
        return <div className="w-4 h-0.5 bg-muted-foreground" />;
    }
  };

  return (
    <button
      onClick={onClick}
      className="w-full glass-card rounded-xl p-4 text-left hover:shadow-md transition-all group"
    >
      <div className="flex items-center justify-between mb-3">
        <div className="flex items-center gap-2">
          <Calendar className="w-4 h-4 text-skill-primary" />
          <span className="text-sm font-medium text-foreground">{label}</span>
        </div>
        <ChevronRight className="w-4 h-4 text-muted-foreground group-hover:translate-x-1 transition-transform" />
      </div>

      {data ? (
        <div className="flex items-center justify-between">
          <div>
            <div className="text-2xl font-serif font-bold text-foreground">
              {data.currentScore}
            </div>
            <div className="text-xs text-muted-foreground">{data.period}</div>
          </div>
          <div className="flex items-center gap-1">
            {getTrendIcon()}
            <span className={cn(
              "text-sm font-medium",
              data.trend === "up" && "text-emerald-500",
              data.trend === "down" && "text-red-500",
              data.trend === "stable" && "text-muted-foreground"
            )}>
              {data.trend === "up" ? "ä¸Šå‡" : data.trend === "down" ? "ä¸‹é™" : "å¹³ç¨³"}
            </span>
          </div>
        </div>
      ) : (
        <p className="text-sm text-muted-foreground">
          æŸ¥çœ‹ä½ çš„è¿åŠ¿èµ°åŠ¿å›¾
        </p>
      )}
    </button>
  );
}

function ReportCard({
  label,
  hasReport,
  onGenerate,
  onDownload,
}: {
  label: string;
  hasReport?: boolean;
  onGenerate?: () => void;
  onDownload?: () => void;
}) {
  return (
    <div className="glass-card rounded-xl p-4">
