      {hasReport ? (
        <div className="space-y-2">
          <p className="text-sm text-muted-foreground">
            ä½ çš„ä¸“å±æŠ¥å‘Šå·²ç”Ÿæˆ
          </p>
          <div className="flex gap-2">
            <button
              onClick={onDownload}
              className="flex-1 flex items-center justify-center gap-2 px-3 py-2 bg-skill-primary text-white rounded-lg text-sm font-medium hover:opacity-90 transition-opacity"
            >
              <Download className="w-4 h-4" />
              ä¸‹è½½æŠ¥å‘Š
            </button>
          </div>
        </div>
      ) : (
        <div className="space-y-2">
          <p className="text-sm text-muted-foreground">
            ç”Ÿæˆä¸€ä»½ä¸“å±äºä½ çš„æ·±åº¦åˆ†ææŠ¥å‘Š
          </p>
          <button
            onClick={onGenerate}
            className="w-full flex items-center justify-center gap-2 px-3 py-2 bg-skill-primary text-white rounded-lg text-sm font-medium hover:opacity-90 transition-opacity"
          >
            <Sparkles className="w-4 h-4" />
            ç”ŸæˆæŠ¥å‘Š
          </button>
        </div>
      )}
    </div>
  );
}

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// ChartPanel Component
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

export function ChartPanel({
  skill,
  baziData,
  nowNextData,
  klinePreview,
  klineData,
  prismContent,
  hasReport,
  showFullKLine = false,
  onViewKLine,
  onGenerateReport,
  onDownloadReport,
  className,
}: ChartPanelProps) {
  const config = SKILL_CHART_CONFIG[skill];

  return (
    <div className={cn("h-full overflow-y-auto p-5 space-y-6", className)}>
      {/* Bazi Chart Section (ä»… bazi skill) */}
      {skill === "bazi" && baziData && (
        <section>
          <h2 className="font-serif text-lg font-semibold text-foreground mb-3">
            å…«å­—å‘½ç›˜
          </h2>
          <div className="glass-card rounded-xl p-4">
            <BaziChart
              pillars={baziData}
              showTenGods={true}
              showElements={true}
              size="md"
              variant="full"
            />
          </div>
        </section>
      )}

      {/* Identity Prism Section */}
      {prismContent && (
        <section>
          <h2 className="font-serif text-lg font-semibold text-foreground mb-3">
            {config.identityLabel}
          </h2>
          <IdentityPrism content={prismContent} />
        </section>
      )}

      {/* Now/Next Section */}
      <section>
        <h2 className="font-serif text-lg font-semibold text-foreground mb-3">
          å½“å‰ä¸ä¸‹ä¸€æ­¥
        </h2>
        <NowNextCard data={nowNextData} />
      </section>

      {/* K-Line Section */}
      <section>
        <h2 className="font-serif text-lg font-semibold text-foreground mb-3">
          {config.klineLabel}
        </h2>
        {klineData && klineData.length > 0 ? (
          /* Full K-Line Chart */
          <LifeKLine
            data={klineData}
            dimension="year"
            height={280}
            onPointClick={(point) => console.log("Clicked:", point)}
