interface AppShellProps {
  skill: SkillType;
  children: ReactNode;
  /** å³ä¾§ Panel å†…å®¹ - æ ¹æ® activeTab ä¼ å…¥ä¸åŒå†…å®¹ */
  panelContent?: ReactNode;
  /** åˆå§‹æ¿€æ´»çš„ Tab */
  defaultTab?: TabType;
}

export function AppShell({
  skill,
  children,
  panelContent,
  defaultTab = "chat",
}: AppShellProps) {
  const [activeTab, setActiveTab] = useState<TabType>(defaultTab);
  const [voiceMode, setVoiceMode] = useState<"warm" | "sarcastic">("warm");

  const contextValue: AppShellContextValue = {
    skill,
    activeTab,
    setActiveTab,
    voiceMode,
    setVoiceMode,
  };

  return (
    <AppShellContext.Provider value={contextValue}>
      <div className="h-screen flex flex-col bg-background">
        {/* Mobile Header - ä»…ç§»åŠ¨ç«¯æ˜¾ç¤º */}
        <div className="lg:hidden">
          <MobileHeader skill={skill} />
        </div>

        {/* Main Content Area */}
        <div className="flex-1 flex overflow-hidden">
          {/* Left NavBar - ä»… PC ç«¯æ˜¾ç¤º */}
          <div className="hidden lg:block">
            <NavBar
              skill={skill}
              activeTab={activeTab}
              onTabChange={setActiveTab}
            />
          </div>

          {/* Center - ä¸»å†…å®¹åŒºåŸŸ (PCç«¯å§‹ç»ˆæ˜¯Chatï¼Œç§»åŠ¨ç«¯æ ¹æ®Tabåˆ‡æ¢) */}
          <main className="flex-1 overflow-hidden">
