            <div className="hidden lg:block h-full">
              {children}
            </div>

            {/* ç§»åŠ¨ç«¯: æ ¹æ® Tab åˆ‡æ¢å†…å®¹ */}
            <div className="lg:hidden h-full">
              <MobileContent
                activeTab={activeTab}
                chatContent={children}
                panelContent={panelContent}
              />
            </div>
          </main>

          {/* Right Panel - ä»… PC ç«¯æ˜¾ç¤º */}
