interface MobileContentProps {
  activeTab: TabType;
  chatContent: ReactNode;
  panelContent?: ReactNode;
}

function MobileContent({ activeTab, chatContent, panelContent }: MobileContentProps) {
  // ç§»åŠ¨ç«¯æ ¹æ® Tab å®Œå…¨åˆ‡æ¢å†…å®¹
  switch (activeTab) {
