      return (
        <div className="h-full overflow-y-auto">
          {panelContent || (
            <div className="flex items-center justify-center h-full text-muted-foreground">
              {activeTab === "chart" && "å›¾è¡¨/æŠ¥å‘Š"}
              {activeTab === "journey" && "å›é¡¾/æ—¶é—´çº¿"}
              {activeTab === "me" && "ä¸ªäººè®¾ç½®"}
            </div>
          )}
        </div>
      );
    default:
      return null;
  }
}

