"use client";

import { useState, KeyboardEvent, useRef, useEffect } from "react";
import { cn } from "@/lib/utils";
import { Send, Mic, Paperclip } from "lucide-react";

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// ChatInput - LUMINOUS PAPER Design System
// Omnibar ç»ç’ƒèƒ¶å›Šæ ·å¼ï¼šfrosted glass è¾“å…¥æ¡†
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

export interface ChatInputProps {
  onSend: (message: string) => void;
  onFileSelect?: (file: File) => void;
  disabled?: boolean;
  placeholder?: string;
  showMediaButtons?: boolean;
  className?: string;
}

export function ChatInput({
  onSend,
  onFileSelect,
  disabled,
  placeholder = "æœ‰ä»€ä¹ˆæƒ³é—®çš„...",
  showMediaButtons = true,
  className,
}: ChatInputProps) {
  const [message, setMessage] = useState("");
  const textareaRef = useRef<HTMLTextAreaElement>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);

  // Auto-resize textarea
  useEffect(() => {
    const textarea = textareaRef.current;
    if (textarea) {
      textarea.style.height = "auto";
      textarea.style.height = `${Math.min(textarea.scrollHeight, 120)}px`;
    }
  }, [message]);

  const handleSend = () => {
    if (message.trim() && !disabled) {
      onSend(message.trim());
      setMessage("");
      // Reset height
      if (textareaRef.current) {
        textareaRef.current.style.height = "auto";
      }
    }
  };

  const handleKeyDown = (e: KeyboardEvent<HTMLTextAreaElement>) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  const canSend = message.trim().length > 0 && !disabled;

  const handleFileClick = () => {
    fileInputRef.current?.click();
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file && onFileSelect) {
      onFileSelect(file);
    }
    // Reset input
    if (fileInputRef.current) {
      fileInputRef.current.value = "";
    }
  };

  return (
    <div className={cn("p-4 md:px-6 pb-6", className)}>
      {/* Hidden file input */}
      <input
        ref={fileInputRef}
        type="file"
        accept="image/*,.pdf,.txt,.docx"
        onChange={handleFileChange}
        className="hidden"
        disabled={disabled}
      />

      {/* Gradient fade above input */}
      <div className="h-8 bg-gradient-to-t from-background to-transparent -mt-8 mb-4 pointer-events-none" />

      <div className="max-w-3xl mx-auto">
        <div className="omnibar shadow-lg shadow-black/5">
          {/* Media buttons (optional) */}
          {showMediaButtons && (
            <>
              <button
                type="button"
                onClick={handleFileClick}
                className="omnibar-btn"
                aria-label="ä¸Šä¼ æ–‡ä»¶"
                disabled={disabled}
              >
                <Paperclip className="w-5 h-5" />
              </button>
              <button
                type="button"
                className="omnibar-btn"
                aria-label="è¯­éŸ³è¾“å…¥"
                disabled={disabled}
              >
                <Mic className="w-5 h-5" />
              </button>
            </>
          )}

          {/* Input */}
          <textarea
            ref={textareaRef}
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            onKeyDown={handleKeyDown}
            placeholder={placeholder}
            disabled={disabled}
            rows={1}
            className="omnibar-input"
            style={{ minHeight: "24px", maxHeight: "120px" }}
          />

          {/* Send button with animation */}
          <button
            type="button"
            onClick={handleSend}
            disabled={!canSend}
            className={cn(
              "omnibar-send transition-all duration-300",
              canSend
                ? "scale-100 opacity-100"
                : "scale-90 opacity-50 cursor-not-allowed"
            )}
            aria-label="å‘é€æ¶ˆæ¯"
          >
            <Send className="w-5 h-5" />
          </button>
        </div>

        {/* Hint text */}
        <p className="text-center text-xs text-muted-foreground/60 mt-3">
          æŒ‰ Enter å‘é€ï¼ŒShift+Enter æ¢è¡Œ
        </p>
      </div>
    </div>
  );
}

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// QuickReplies - Suggested quick reply buttons
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

interface QuickRepliesProps {
  replies: string[];
  onSelect: (reply: string) => void;
  className?: string;
}

export function QuickReplies({ replies, onSelect, className }: QuickRepliesProps) {
  if (replies.length === 0) return null;

  return (
    <div className={cn("flex flex-wrap gap-2 px-4 pb-2", className)}>
      {replies.map((reply, index) => (
        <button
          key={index}
          onClick={() => onSelect(reply)}
          className={cn(
            "px-3 py-1.5 text-sm rounded-full",
            "bg-muted/50 text-muted-foreground",
            "border border-border/50",
            "hover:bg-muted hover:text-foreground",
            "transition-colors duration-200"
          )}
        >
          {reply}
        </button>
      ))}
    </div>
  );
}

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// FloatingOmnibar - Floating version for landing pages
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

interface FloatingOmnibarProps {
  onSend: (message: string) => void;
  placeholder?: string;
  className?: string;
}

export function FloatingOmnibar({
  onSend,
  placeholder = "è¾“å…¥ä½ çš„å‡ºç”Ÿæ—¥æœŸå¼€å§‹...",
  className,
}: FloatingOmnibarProps) {
  const [value, setValue] = useState("");

  const handleSubmit = () => {
    if (value.trim()) {
      onSend(value.trim());
      setValue("");
    }
  };

  return (
    <div className={cn("glass-card p-6 max-w-lg mx-auto", className)}>
      <div className="omnibar">
        <input
          type="text"
          value={value}
          onChange={(e) => setValue(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && handleSubmit()}
          placeholder={placeholder}
          className="omnibar-input"
        />
        <button
          onClick={handleSubmit}
          disabled={!value.trim()}
          className={cn(
            "omnibar-send",
            !value.trim() && "opacity-50 cursor-not-allowed"
          )}
        >
          <Send className="w-5 h-5" />
        </button>
      </div>
    </div>
  );
}

