// components/layout/ResizablePanel.tsx (æ–°å»º)
"use client";
import { useState, useRef, useCallback } from "react";
import { cn } from "@/lib/utils";
import { PanelRightClose, PanelRight, MessageSquare, LineChart, Clock, User } from "lucide-react";

interface ResizablePanelProps {
  children: React.ReactNode;
  defaultWidth?: number;
  minWidth?: number;
  maxWidth?: number;
  onCollapse?: (collapsed: boolean) => void;
}

export function ResizablePanel({
  children,
  defaultWidth = 450,
  minWidth = 320,
  maxWidth = 600,
  onCollapse,
}: ResizablePanelProps) {
  const [width, setWidth] = useState(defaultWidth);
  const [isCollapsed, setIsCollapsed] = useState(false);
  const [isDragging, setIsDragging] = useState(false);
  const panelRef = useRef<HTMLDivElement>(null);

  const handleMouseDown = useCallback((e: React.MouseEvent) => {
    e.preventDefault();
    setIsDragging(true);

    const startX = e.clientX;
    const startWidth = width;

    const handleMouseMove = (e: MouseEvent) => {
      const delta = startX - e.clientX;
      const newWidth = Math.min(maxWidth, Math.max(minWidth, startWidth + delta));
      setWidth(newWidth);
    };

    const handleMouseUp = () => {
      setIsDragging(false);
      document.removeEventListener("mousemove", handleMouseMove);
      document.removeEventListener("mouseup", handleMouseUp);
    };

    document.addEventListener("mousemove", handleMouseMove);
    document.addEventListener("mouseup", handleMouseUp);
  }, [width, minWidth, maxWidth]);

  const toggleCollapse = () => {
    setIsCollapsed(!isCollapsed);
    onCollapse?.(!isCollapsed);
  };

  if (isCollapsed) {
    // æŠ˜å çŠ¶æ€ï¼šæ˜¾ç¤ºå›¾æ ‡æ¡
    return (
      <aside className="hidden lg:flex flex-col w-14 flex-shrink-0 border-l border-border bg-background/50">
        <button
          onClick={toggleCollapse}
          className="p-3 hover:bg-muted/50 transition-colors"
          title="å±•å¼€é¢æ¿"
        >
          <PanelRight className="w-5 h-5 text-muted-foreground" />
        </button>
        <div className="flex flex-col items-center gap-1 p-2">
          <button className="p-2 rounded-lg hover:bg-muted/50" title="èŠå¤©">
            <MessageSquare className="w-5 h-5 text-muted-foreground" />
          </button>
          <button className="p-2 rounded-lg hover:bg-muted/50" title="å›¾è¡¨">
            <LineChart className="w-5 h-5 text-muted-foreground" />
          </button>
          <button className="p-2 rounded-lg hover:bg-muted/50" title="å›é¡¾">
            <Clock className="w-5 h-5 text-muted-foreground" />
          </button>
          <button className="p-2 rounded-lg hover:bg-muted/50" title="æˆ‘çš„">
            <User className="w-5 h-5 text-muted-foreground" />
          </button>
        </div>
      </aside>
    );
  }

  return (
    <aside
      ref={panelRef}
      className="hidden lg:block flex-shrink-0 border-l border-border overflow-y-auto relative"
      style={{ width: `${width}px` }}
    >
      {/* æ‹–æ‹½æ‰‹æŸ„ */}
      <div
        onMouseDown={handleMouseDown}
        className={cn(
          "absolute left-0 top-0 bottom-0 w-1 cursor-col-resize hover:bg-skill-primary/30 transition-colors",
          isDragging && "bg-skill-primary/50"
        )}
      />

      {/* æŠ˜å æŒ‰é’® */}
      <button
        onClick={toggleCollapse}
        className="absolute top-3 left-3 p-1.5 rounded-md hover:bg-muted/50 transition-colors z-10"
        title="æŠ˜å é¢æ¿"
      >
        <PanelRightClose className="w-4 h-4 text-muted-foreground" />
      </button>

      {/* å†…å®¹åŒº */}
      <div className="pt-12">
        {children}
      </div>
    </aside>
  );
}
