          />

          {/* Name */}
          <span
            className="text-xs uppercase tracking-wider mb-1"
            style={{ color: 'var(--text-tertiary)' }}
          >
            {name_en}
          </span>
          <h4
            className="serif-heading mb-2"
            style={{
              fontFamily: 'var(--font-serif)',
              fontSize: '16px',
              fontWeight: 600,
              color: 'var(--text-primary)',
