/** @type {import('next').NextConfig} */
const nextConfig = {
  reactStrictMode: true,
  transpilePackages: ["@vibelife/ui"],
  images: {
    domains: ["localhost"],
  },
  async rewrites() {
    return [
      {
        source: "/api/:path*",
        destination: `${process.env.NEXT_PUBLIC_API_BASE || "http://localhost:8000/api/v1"}/:path*`,
      },
    ];
  },
};

module.exports = nextConfig;

import type { Config } from "tailwindcss";

const config: Config = {
  darkMode: ["class"],
  content: [
    "./src/**/*.{js,ts,jsx,tsx,mdx}",
    "../../packages/ui/src/**/*.{js,ts,jsx,tsx}",
  ],
  theme: {
    extend: {
      // ═══════════════════════════════════════════════════════════════
      // LUMINOUS PAPER Design System - Color Tokens
      // ═══════════════════════════════════════════════════════════════
      colors: {
        // Brand Core Colors
        ink: "#1C1A17",
        "antique-gold": "#B88A44",
        vellum: "#FBF7F1",

        // Semantic Colors (CSS Variable based for theme switching)
        background: "hsl(var(--background))",
        foreground: "hsl(var(--foreground))",
        card: {
          DEFAULT: "hsl(var(--card))",
          foreground: "hsl(var(--card-foreground))",
        },
        muted: {
          DEFAULT: "hsl(var(--muted))",
          foreground: "hsl(var(--muted-foreground))",
        },
        accent: {
          DEFAULT: "hsl(var(--accent))",
          foreground: "hsl(var(--accent-foreground))",
        },
        border: "hsl(var(--border))",
        ring: "hsl(var(--ring))",

        // Skill-specific Colors (LUMINOUS PAPER)
        bazi: {
          primary: "#7A3F1A",
          secondary: "#B88A44",
          glow: "rgba(122, 63, 26, 0.15)",
          bg: "#FFFBF5",
        },
        zodiac: {
          primary: "#3C3A7A",
          secondary: "#A7A6F4",
          glow: "rgba(60, 58, 122, 0.12)",
          bg: "#F8F8FF",
        },
        mbti: {
          primary: "#0F766E",
          secondary: "#6EE7C3",
          glow: "rgba(15, 118, 110, 0.12)",
          bg: "#F0FDF9",
        },
        attach: {
          primary: "#B13C41",
          secondary: "#F3A6A9",
          glow: "rgba(177, 60, 65, 0.12)",
          bg: "#FFF5F5",
        },
        career: {
          primary: "#0E7490",
          secondary: "#67E8F9",
          glow: "rgba(14, 116, 144, 0.12)",
          bg: "#F0FDFF",
        },

        // Five Elements Colors (五行色)
        element: {
          water: "hsl(210 70% 45%)",   // 水 - 平静
          fire: "hsl(0 80% 55%)",      // 火 - 热情
          earth: "hsl(40 70% 50%)",    // 土 - 喜悦
          wood: "hsl(120 50% 40%)",    // 木 - 成长
          metal: "hsl(30 5% 85%)",     // 金 - 稳重
        },
      },

      // ═══════════════════════════════════════════════════════════════
      // Typography System
      // ═══════════════════════════════════════════════════════════════
      fontFamily: {
        serif: ["var(--font-fraunces)", "Noto Serif SC", "Source Han Serif SC", "serif"],
        sans: ["var(--font-bricolage)", "Noto Sans SC", "system-ui", "sans-serif"],
        display: ["var(--font-fraunces)", "Noto Serif SC", "serif"],
      },
      fontSize: {
        "hero": ["clamp(2rem, 5vw, 3.5rem)", { lineHeight: "1.1", letterSpacing: "-0.025em" }],
        "section": ["1.875rem", { lineHeight: "1.2", letterSpacing: "-0.02em" }],
        "card-title": ["1.125rem", { lineHeight: "1.4", letterSpacing: "-0.01em" }],
      },

      // ═══════════════════════════════════════════════════════════════
      // Spacing & Border Radius
      // ═══════════════════════════════════════════════════════════════
      borderRadius: {
        xs: "0.375rem",   // 6px
        sm: "0.5rem",     // 8px
        md: "0.75rem",    // 12px
        lg: "1rem",       // 16px
        xl: "1.5rem",     // 24px
        "2xl": "2rem",    // 32px
        full: "9999px",
      },

      // ═══════════════════════════════════════════════════════════════
      // Box Shadow System
      // ═══════════════════════════════════════════════════════════════
      boxShadow: {
        "glass": "0 4px 24px rgba(28, 26, 23, 0.06), inset 0 1px 0 rgba(255, 255, 255, 0.1)",
        "glass-hover": "0 8px 32px rgba(28, 26, 23, 0.1), inset 0 1px 0 rgba(255, 255, 255, 0.15)",
        "card": "0 2px 12px rgba(28, 26, 23, 0.04)",
        "card-hover": "0 4px 20px rgba(28, 26, 23, 0.08)",
        "insight": "0 2px 16px rgba(184, 138, 68, 0.12)",
        "glow-gold": "0 0 20px rgba(184, 138, 68, 0.3)",
        "glow-skill": "0 0 30px var(--skill-glow, rgba(122, 63, 26, 0.15))",
      },

      // ═══════════════════════════════════════════════════════════════
      // Animation System
      // ═══════════════════════════════════════════════════════════════
      animation: {
        "fade-in": "fadeIn 0.4s ease-out",
        "fade-in-up": "fadeInUp 0.4s ease-out",
        "scale-in": "scaleIn 0.3s ease-out",
        "slide-right": "slideRight 0.3s ease-out",
        "float": "float 20s ease-in-out infinite",           // 修正: 原 6s
        "breath": "breath 16s ease-in-out infinite",         // 修正: 原 4s (禅意呼吸)
        "breath-slow": "breath 20s ease-in-out infinite",    // 新增: 更慢的呼吸
        "pulse-slow": "pulse 3s cubic-bezier(0.4, 0, 0.6, 1) infinite",
        "shimmer": "shimmer 2s linear infinite",
        "ink-reveal": "inkReveal 0.6s cubic-bezier(0.16, 1, 0.3, 1) forwards", // 新增
      },
      keyframes: {
        fadeIn: {
          "0%": { opacity: "0" },
          "100%": { opacity: "1" },
        },
        fadeInUp: {
          "0%": { opacity: "0", transform: "translateY(20px)" },
          "100%": { opacity: "1", transform: "translateY(0)" },
        },
        scaleIn: {
          "0%": { opacity: "0", transform: "scale(0.95)" },
          "100%": { opacity: "1", transform: "scale(1)" },
        },
        slideRight: {
          "0%": { opacity: "0", transform: "translateX(-20px)" },
          "100%": { opacity: "1", transform: "translateX(0)" },
        },
        float: {
          "0%, 100%": { transform: "translateY(0)" },
          "50%": { transform: "translateY(-10px)" },
        },
        breath: {
          "0%, 100%": { opacity: "0.4", transform: "scale(1)" },
          "50%": { opacity: "0.55", transform: "scale(1.015)" },  // 修正: 更微妙的变化
        },
        inkReveal: {
          "0%": { filter: "blur(4px)", opacity: "0" },
          "100%": { filter: "blur(0)", opacity: "1" },
        },
        shimmer: {
          "0%": { backgroundPosition: "-200% 0" },
          "100%": { backgroundPosition: "200% 0" },
        },
      },
      transitionTimingFunction: {
        "out-expo": "cubic-bezier(0.16, 1, 0.3, 1)",
        "spring": "cubic-bezier(0.34, 1.56, 0.64, 1)",
      },

      // ═══════════════════════════════════════════════════════════════
      // Backdrop Blur
      // ═══════════════════════════════════════════════════════════════
      backdropBlur: {
        xs: "4px",
        sm: "8px",
        md: "12px",
        lg: "16px",
        xl: "20px",
        "2xl": "24px",
      },
    },
  },
  plugins: [],
};

export default config;
