"use client";

/**
 * NavBar - PCç«¯å·¦ä¾§å¯¼èˆªæ  v9.0
 * Based on: vibelife Dashboard â†’ Chat æ•´åˆæ–¹æ¡ˆ
 *
 * V9 å˜æ›´:
 * - å¯¼èˆªå…¥å£ä» 5 ä¸ªç®€åŒ–ä¸º 3 ä¸ª: Chat / Journey / Me
 * - Dashboard å·²æ•´åˆåˆ° Chat ä¸­
 * - Discover ç§»åˆ° Me é¡µé¢
 * - Chat ä½œä¸ºé»˜è®¤é¦–é¡µ
 *
 * è§„æ ¼ï¼š
 * - æ”¶èµ·æ€ï¼ˆé»˜è®¤ï¼‰ï¼š64pxï¼Œä»…å›¾æ ‡ï¼Œhover æ˜¾ç¤º tooltip
 * - å±•å¼€æ€ï¼š200pxï¼Œå›¾æ ‡ + æ–‡å­—æ ‡ç­¾
 * - ç”¨æˆ·å¯é€šè¿‡æŒ‰é’®åˆ‡æ¢ æ”¶èµ·/å±•å¼€
 * - è®°ä½ç”¨æˆ·åå¥½ï¼ˆlocalStorageï¼‰
 */

import { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { cn } from "@/lib/utils";
import {
  MessageSquare,
  LayoutDashboard,
  Compass,
  Sparkles,
  User,
  ChevronLeft,
  ChevronRight,
} from "lucide-react";
import type { LucideIcon } from "lucide-react";
import { VibeGlyph, type SkillType } from "@/components/core";
import { type TabType } from "./AppShell";
import { NotificationBell } from "./NotificationBell";

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// Types & Config
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

interface NavBarProps {
  skill: SkillType;
  activeTab: TabType;
  onTabChange: (tab: TabType) => void;
  className?: string;
}

interface NavItem {
  id: TabType;
  icon: LucideIcon;
  label: string;
  description?: string;
}

// V9.1: 4 navigation items - Chat / Journey / Discover / Me
const NAV_ITEMS: NavItem[] = [
  {
    id: "chat",
    icon: MessageSquare,
    label: "å¯¹è¯",
    description: "ä¸ Vibe å¯¹è¯",
  },
  {
    id: "journey",
    icon: Compass,
    label: "æ—…ç¨‹",
    description: "æˆ‘çš„æ—…ç¨‹",
  },
  {
    id: "discover",
    icon: Sparkles,
    label: "æ¢ç´¢",
    description: "å‘ç°æ›´å¤š Skills",
  },
  {
    id: "me",
    icon: User,
    label: "æˆ‘çš„",
    description: "è®¾ç½®/è®¢é˜…",
  },
];

const STORAGE_KEY = "vibelife-navbar-expanded";

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// NavBar Component
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

export function NavBar({ skill, activeTab, onTabChange, className }: NavBarProps) {
  const [isExpanded, setIsExpanded] = useState(false);
  const [hoveredItem, setHoveredItem] = useState<string | null>(null);

  // ä» localStorage æ¢å¤çŠ¶æ€
  useEffect(() => {
    const saved = localStorage.getItem(STORAGE_KEY);
    if (saved !== null) {
      setIsExpanded(saved === "true");
    }
  }, []);

  // ä¿å­˜çŠ¶æ€åˆ° localStorage
  const toggleExpanded = () => {
    const newValue = !isExpanded;
    setIsExpanded(newValue);
    localStorage.setItem(STORAGE_KEY, String(newValue));
  };

  return (
    <motion.nav
      initial={false}
      animate={{ width: isExpanded ? 200 : 64 }}
      transition={{ duration: 0.2, ease: "easeInOut" }}
      className={cn(
        "h-full flex flex-col bg-card border-r border-border",
        className
      )}
    >
      {/* Logo Area */}
      <div className="h-16 flex items-center justify-center border-b border-border">
        <VibeGlyph
          size="sm"
          skill={skill}
          showAura={false}
          animate={false}
        />
        <AnimatePresence>
          {isExpanded && (
            <motion.span
              initial={{ opacity: 0, width: 0 }}
              animate={{ opacity: 1, width: "auto" }}
              exit={{ opacity: 0, width: 0 }}
              className="ml-2 font-serif font-semibold text-foreground overflow-hidden whitespace-nowrap"
            >
              VibeLife
            </motion.span>
          )}
        </AnimatePresence>
      </div>

      {/* Nav Items */}
      <div className="flex-1 py-4 space-y-1">
        {NAV_ITEMS.map((item) => {
          const Icon = item.icon;
          const isActive = activeTab === item.id;

          return (
            <div key={item.id} className="relative px-2">
              <button
                onClick={() => onTabChange(item.id)}
                onMouseEnter={() => setHoveredItem(item.id)}
                onMouseLeave={() => setHoveredItem(null)}
                aria-label={item.label}
                title={item.description}
                className={cn(
                  "w-full flex items-center gap-3 px-3 py-2.5 rounded-lg transition-all duration-200",
                  isActive
                    ? "bg-skill-primary/10 text-skill-primary"
                    : "text-muted-foreground hover:bg-muted hover:text-foreground"
                )}
              >
                <Icon className="w-5 h-5 flex-shrink-0" />
                <AnimatePresence>
                  {isExpanded && (
                    <motion.span
                      initial={{ opacity: 0, width: 0 }}
                      animate={{ opacity: 1, width: "auto" }}
                      exit={{ opacity: 0, width: 0 }}
                      className="text-sm font-medium overflow-hidden whitespace-nowrap"
                    >
                      {item.label}
                    </motion.span>
                  )}
                </AnimatePresence>
              </button>

              {/* Tooltip - ä»…æ”¶èµ·çŠ¶æ€æ—¶æ˜¾ç¤º */}
              {!isExpanded && hoveredItem === item.id && (
                <motion.div
                  initial={{ opacity: 0, x: -4 }}
                  animate={{ opacity: 1, x: 0 }}
                  className="absolute left-full top-1/2 -translate-y-1/2 ml-2 z-50"
                >
                  <div className="bg-foreground text-background px-3 py-1.5 rounded-md text-sm whitespace-nowrap shadow-lg">
                    {item.label}
                    <div className="absolute left-0 top-1/2 -translate-y-1/2 -translate-x-1 w-2 h-2 bg-foreground rotate-45" />
                  </div>
                </motion.div>
              )}
            </div>
          );
        })}
      </div>

      {/* Notification Bell */}
      <div className="px-2 py-2 border-t border-border">
        <div className={cn("flex items-center", isExpanded ? "justify-start px-3" : "justify-center")}>
          <NotificationBell />
          <AnimatePresence>
            {isExpanded && (
              <motion.span
                initial={{ opacity: 0, width: 0 }}
                animate={{ opacity: 1, width: "auto" }}
                exit={{ opacity: 0, width: 0 }}
                className="ml-3 text-sm text-muted-foreground overflow-hidden whitespace-nowrap"
              >
                é€šçŸ¥
              </motion.span>
            )}
          </AnimatePresence>
        </div>
      </div>

      {/* Expand/Collapse Toggle */}
      <div className="p-2 border-t border-border">
        <button
          onClick={toggleExpanded}
          className="w-full flex items-center justify-center gap-2 px-3 py-2 rounded-lg text-muted-foreground hover:bg-muted hover:text-foreground transition-colors"
        >
          {isExpanded ? (
            <>
              <ChevronLeft className="w-4 h-4" />
              <span className="text-xs">æ”¶èµ·</span>
            </>
          ) : (
            <ChevronRight className="w-4 h-4" />
          )}
        </button>
      </div>
    </motion.nav>
  );
}

