"use client";

import { useEffect, useState, useRef } from "react";
import { useRouter } from "next/navigation";
import { Bell, MessageCircle } from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";
import { cn } from "@/lib/utils";
import { getTokens } from "@/lib/api";

interface QuickAction {
  label: string;
  prompt: string;
}

interface NotificationContent {
  body?: string;
  fortune_hint?: string;
  action_tip?: string;
  suggested_prompt?: string;
  quick_actions?: QuickAction[];
}

interface Notification {
  id: string;
  type: string;
  skill_id?: string;
  title: string;
  content: string | NotificationContent;
  is_read: boolean;
  created_at: string;
}

export function NotificationBell({ className }: { className?: string }) {
  const router = useRouter();
  const [notifications, setNotifications] = useState<Notification[]>([]);
  const [unreadCount, setUnreadCount] = useState(0);
  const [isOpen, setIsOpen] = useState(false);
  const containerRef = useRef<HTMLDivElement>(null);

  // è·å–é€šçŸ¥å†…å®¹æ–‡æœ¬
  const getContentText = (content: string | NotificationContent): string => {
    if (typeof content === "string") return content;
    return content.body || content.fortune_hint || "";
  };

  // è·å–å¿«æ·æ“ä½œ
  const getQuickActions = (n: Notification): QuickAction[] => {
    if (typeof n.content === "object" && n.content.quick_actions) {
      return n.content.quick_actions;
    }
    return [];
  };

  // ç‚¹å‡»å¿«æ·æ“ä½œï¼Œè·³è½¬åˆ°èŠå¤©é¡µé¢
  const handleQuickAction = async (n: Notification, action: QuickAction) => {
    // æ ‡è®°ä¸ºå·²è¯»
    if (!n.is_read) {
      await markAsRead(n.id);
    }
    // è·³è½¬åˆ°èŠå¤©é¡µé¢ï¼Œå¸¦ä¸Š skill å’Œ prompt å‚æ•°
    const skillId = n.skill_id || "bazi";
    router.push(`/chat?skill=${skillId}&prompt=${encodeURIComponent(action.prompt)}`);
    setIsOpen(false);
  };

  useEffect(() => {
    async function fetchNotifications() {
      try {
        const { accessToken } = getTokens();
        if (!accessToken) return;

        const res = await fetch("/api/notifications?limit=10", {
          headers: { Authorization: `Bearer ${accessToken}` },
        });
        if (res.ok) {
          const data = await res.json();
          setNotifications(data.items || []);
          setUnreadCount(data.unread_count || data.items?.filter((n: Notification) => !n.is_read).length || 0);
        }
      } catch (err) {
        console.error("Failed to fetch notifications:", err);
      }
    }
    fetchNotifications();
  }, []);

  useEffect(() => {
    function handleClickOutside(e: MouseEvent) {
      if (containerRef.current && !containerRef.current.contains(e.target as Node)) {
        setIsOpen(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  const markAsRead = async (id: string) => {
    try {
      const { accessToken } = getTokens();
      await fetch(`/api/notifications/${id}/read`, {
        method: "POST",
        headers: { Authorization: `Bearer ${accessToken}` },
      });
      setNotifications((prev) =>
        prev.map((n) => (n.id === id ? { ...n, is_read: true } : n))
      );
      setUnreadCount((c) => Math.max(0, c - 1));
    } catch (err) {
      console.error("Failed to mark notification as read:", err);
    }
  };

  return (
    <div ref={containerRef} className={cn("relative", className)}>
      <button
        onClick={() => setIsOpen(!isOpen)}
        className="relative p-2 rounded-full hover:bg-muted/50 transition-colors"
      >
        <Bell className="w-5 h-5 text-muted-foreground" />
        {unreadCount > 0 && (
          <span className="absolute -top-0.5 -right-0.5 w-4 h-4 bg-red-500 text-white text-[10px] font-medium rounded-full flex items-center justify-center">
            {unreadCount > 9 ? "9+" : unreadCount}
          </span>
        )}
      </button>

      <AnimatePresence>
        {isOpen && (
          <motion.div
            initial={{ opacity: 0, y: 8, scale: 0.95 }}
            animate={{ opacity: 1, y: 0, scale: 1 }}
            exit={{ opacity: 0, y: 8, scale: 0.95 }}
            transition={{ duration: 0.15 }}
            className="absolute right-0 top-full mt-2 w-80 max-h-96 overflow-y-auto bg-card border border-border rounded-xl shadow-lg z-50"
          >
            <div className="p-3 border-b border-border">
              <h3 className="font-semibold text-foreground">é€šçŸ¥</h3>
            </div>

            {notifications.length === 0 ? (
              <div className="p-6 text-center text-muted-foreground text-sm">
                æš‚æ— é€šçŸ¥
              </div>
            ) : (
              <div className="divide-y divide-border">
                {notifications.map((n) => {
                  const quickActions = getQuickActions(n);
                  return (
                    <div
                      key={n.id}
                      className={cn(
                        "p-3 transition-colors",
                        !n.is_read && "bg-muted/10"
                      )}
                    >
                      <button
                        onClick={() => !n.is_read && markAsRead(n.id)}
                        className="w-full text-left hover:bg-muted/30 rounded-lg p-1 -m-1"
                      >
                        <div className="flex items-start gap-2">
                          {!n.is_read && (
                            <span className="w-2 h-2 mt-1.5 rounded-full bg-skill-primary flex-shrink-0" />
                          )}
                          <div className="flex-1 min-w-0">
                            <p className="font-medium text-sm text-foreground truncate">
                              {n.title}
                            </p>
                            <p className="text-xs text-muted-foreground line-clamp-2 mt-0.5">
                              {getContentText(n.content)}
                            </p>
                          </div>
                        </div>
                      </button>

                      {/* å¯¹è¯å¼•å¯¼å¿«æ·æ“ä½œ */}
                      {quickActions.length > 0 && (
                        <div className="mt-2 flex flex-wrap gap-1.5 pl-4">
                          {quickActions.map((action) => (
                            <button
                              key={action.label}
                              onClick={() => handleQuickAction(n, action)}
                              className="inline-flex items-center gap-1 px-2 py-1 text-xs font-medium text-skill-primary bg-skill-primary/10 hover:bg-skill-primary/20 rounded-full transition-colors"
                            >
                              <MessageCircle className="w-3 h-3" />
                              {action.label}
                            </button>
                          ))}
                        </div>
                      )}
                    </div>
                  );
                })}
              </div>
            )}
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}

export default NotificationBell;

# React Best Practices

**Version 0.1.0**  
Vercel Engineering  
January 2026

> **Note:**  
> This document is mainly for agents and LLMs to follow when maintaining,  
> generating, or refactoring React and Next.js codebases at Vercel. Humans  
> may also find it useful, but guidance here is optimized for automation  
> and consistency by AI-assisted workflows.

---

## Abstract

Comprehensive performance optimization guide for React and Next.js applications, designed for AI agents and LLMs. Contains 40+ rules across 8 categories, prioritized by impact from critical (eliminating waterfalls, reducing bundle size) to incremental (advanced patterns). Each rule includes detailed explanations, real-world examples comparing incorrect vs. correct implementations, and specific impact metrics to guide automated refactoring and code generation.

---

## Table of Contents

1. [Eliminating Waterfalls](#1-eliminating-waterfalls) â€” **CRITICAL**
   - 1.1 [Defer Await Until Needed](#11-defer-await-until-needed)
   - 1.2 [Dependency-Based Parallelization](#12-dependency-based-parallelization)
   - 1.3 [Prevent Waterfall Chains in API Routes](#13-prevent-waterfall-chains-in-api-routes)
   - 1.4 [Promise.all() for Independent Operations](#14-promiseall-for-independent-operations)
   - 1.5 [Strategic Suspense Boundaries](#15-strategic-suspense-boundaries)
2. [Bundle Size Optimization](#2-bundle-size-optimization) â€” **CRITICAL**
   - 2.1 [Avoid Barrel File Imports](#21-avoid-barrel-file-imports)
   - 2.2 [Conditional Module Loading](#22-conditional-module-loading)
   - 2.3 [Defer Non-Critical Third-Party Libraries](#23-defer-non-critical-third-party-libraries)
   - 2.4 [Dynamic Imports for Heavy Components](#24-dynamic-imports-for-heavy-components)
