"use client";

import { useEffect, useState, useRef } from "react";
import { useRouter } from "next/navigation";
import { Bell, MessageCircle } from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";
import { cn } from "@/lib/utils";
import { getTokens } from "@/lib/api";

interface QuickAction {
  label: string;
  prompt: string;
}

interface NotificationContent {
  body?: string;
  fortune_hint?: string;
  action_tip?: string;
  suggested_prompt?: string;
  quick_actions?: QuickAction[];
}

interface Notification {
  id: string;
  type: string;
  skill_id?: string;
  title: string;
  content: string | NotificationContent;
  is_read: boolean;
  created_at: string;
}

export function NotificationBell({ className }: { className?: string }) {
  const router = useRouter();
  const [notifications, setNotifications] = useState<Notification[]>([]);
  const [unreadCount, setUnreadCount] = useState(0);
  const [isOpen, setIsOpen] = useState(false);
  const containerRef = useRef<HTMLDivElement>(null);

  // è·å–é€šçŸ¥å†…å®¹æ–‡æœ¬
  const getContentText = (content: string | NotificationContent): string => {
    if (typeof content === "string") return content;
    return content.body || content.fortune_hint || "";
  };

  // è·å–å¿«æ·æ“ä½œ
  const getQuickActions = (n: Notification): QuickAction[] => {
    if (typeof n.content === "object" && n.content.quick_actions) {
      return n.content.quick_actions;
    }
    return [];
  };

  // ç‚¹å‡»å¿«æ·æ“ä½œï¼Œè·³è½¬åˆ°èŠå¤©é¡µé¢
  const handleQuickAction = async (n: Notification, action: QuickAction) => {
    // æ ‡è®°ä¸ºå·²è¯»
    if (!n.is_read) {
      await markAsRead(n.id);
    }
    // è·³è½¬åˆ°èŠå¤©é¡µé¢ï¼Œå¸¦ä¸Š skill å’Œ prompt å‚æ•°
    const skillId = n.skill_id || "bazi";
    router.push(`/chat?skill=${skillId}&prompt=${encodeURIComponent(action.prompt)}`);
    setIsOpen(false);
  };

  useEffect(() => {
    async function fetchNotifications() {
      try {
        const { accessToken } = getTokens();
        if (!accessToken) return;

        const res = await fetch("/api/notifications?limit=10", {
          headers: { Authorization: `Bearer ${accessToken}` },
        });
        if (res.ok) {
          const data = await res.json();
          setNotifications(data.items || []);
          setUnreadCount(data.unread_count || data.items?.filter((n: Notification) => !n.is_read).length || 0);
        }
      } catch (err) {
        console.error("Failed to fetch notifications:", err);
      }
    }
    fetchNotifications();
  }, []);

  useEffect(() => {
    function handleClickOutside(e: MouseEvent) {
      if (containerRef.current && !containerRef.current.contains(e.target as Node)) {
        setIsOpen(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

