// V9.1: 4 entry points - Chat / Journey / Discover / Me
export type TabType = "chat" | "journey" | "discover" | "me";

interface AppShellContextValue {
  skill: SkillType;
  activeTab: TabType;
  setActiveTab: (tab: TabType) => void;
