## 三、完整代码实现

```typescript
// apps/web/src/app/onboarding/page.tsx
'use client'

import { useState, useRef } from 'react'
import { useRouter } from 'next/navigation'
import { motion } from 'framer-motion'
import './onboarding.css'

export default function OnboardingPage() {
  const router = useRouter()

  // 表单状态
  const [formData, setFormData] = useState({
    year: '',
    month: '',
    day: '',
    hour: '',
    location: '',
    intro: ''
  })

  // Input refs (用于自动聚焦)
  const monthRef = useRef<HTMLInputElement>(null)
  const dayRef = useRef<HTMLInputElement>(null)
  const hourRef = useRef<HTMLInputElement>(null)

  // 验证逻辑
  const isValid =
    formData.year.length === 4 &&
    parseInt(formData.year) >= 1900 &&
    parseInt(formData.year) <= 2100 &&
    parseInt(formData.month) >= 1 &&
    parseInt(formData.month) <= 12 &&
    parseInt(formData.day) >= 1 &&
    parseInt(formData.day) <= 31

  // 输入处理 + 自动聚焦
  const handleYearChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value.replace(/\D/g, '')
    setFormData({ ...formData, year: value })
    if (value.length === 4) {
      monthRef.current?.focus()
    }
  }

  const handleMonthChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value.replace(/\D/g, '')
    setFormData({ ...formData, month: value })
    if (value.length === 2) {
      dayRef.current?.focus()
    }
  }

  const handleDayChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value.replace(/\D/g, '')
    setFormData({ ...formData, day: value })
    if (value.length === 2) {
      hourRef.current?.focus()
    }
  }

  const handleHourChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value.replace(/\D/g, '')
    if (parseInt(value) <= 23 || value === '') {
      setFormData({ ...formData, hour: value })
    }
  }

  // 提交处理
  const handleSubmit = async () => {
    if (!isValid) return

    try {
      // 调用API计算VibeID
      const response = await fetch('/api/v1/vibe-id/calculate', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      })

      if (!response.ok) throw new Error('计算失败')

      const result = await response.json()

      // 跳转到结果页
      router.push(`/onboarding/result?id=${result.vibeId}`)
    } catch (error) {
      console.error('Error:', error)
      // TODO: 显示错误提示
    }
  }

  // 回车提交
  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && isValid) {
      handleSubmit()
    }
  }

  return (
    <div className="onboarding-page">
      {/* 标题 */}
      <motion.div
        className="onboarding-title"
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      >
        <h1>你的灵魂密码，</h1>
        <h2>藏在出生的那一刻。</h2>
      </motion.div>

      {/* 表单卡片 */}
      <motion.div
        className="form-card"
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8, delay: 0.2 }}
      >
        {/* 出生时间 (必填) */}
        <div className="form-field">
          <label className="field-label">
            出生时间 <span className="required">*</span>
          </label>
          <div className="birthday-inputs">
            <div className="input-wrapper">
              <input
                type="text"
                inputMode="numeric"
                maxLength={4}
                placeholder="1990"
                value={formData.year}
                onChange={handleYearChange}
                onKeyDown={handleKeyDown}
                className="input-field year-input"
                autoFocus
              />
              <span className="input-suffix">年</span>
            </div>
            <div className="input-wrapper">
              <input
                ref={monthRef}
                type="text"
                inputMode="numeric"
                maxLength={2}
                placeholder="5"
                value={formData.month}
                onChange={handleMonthChange}
                onKeyDown={handleKeyDown}
                className="input-field small-input"
              />
              <span className="input-suffix">月</span>
            </div>
            <div className="input-wrapper">
              <input
                ref={dayRef}
                type="text"
                inputMode="numeric"
                maxLength={2}
                placeholder="15"
                value={formData.day}
                onChange={handleDayChange}
                onKeyDown={handleKeyDown}
                className="input-field small-input"
              />
              <span className="input-suffix">日</span>
            </div>
            <div className="input-wrapper">
              <input
                ref={hourRef}
                type="text"
                inputMode="numeric"
                maxLength={2}
                placeholder="08"
                value={formData.hour}
                onChange={handleHourChange}
                onKeyDown={handleKeyDown}
                className="input-field small-input"
              />
              <span className="input-suffix">时</span>
            </div>
          </div>
        </div>

        {/* 分隔线 */}
        <div className="divider" />

        {/* 出生地点 (可选) */}
        <div className="form-field">
          <label className="field-label">出生地点（可选）</label>
          <div className="input-with-icon">
            <input
              type="text"
              placeholder="北京"
              value={formData.location}
              onChange={(e) => setFormData({ ...formData, location: e.target.value })}
              onKeyDown={handleKeyDown}
              className="input-field"
            />
            <span className="input-icon">🔍</span>
          </div>
          <p className="field-hint">用于精确计算上升星座</p>
        </div>

        {/* 分隔线 */}
        <div className="divider" />

        {/* 自我介绍 (可选) */}
        <div className="form-field">
          <label className="field-label">用一句话介绍自己（可选）</label>
          <input
            type="text"
            placeholder="一个追求美的产品设计师"
            maxLength={50}
            value={formData.intro}
            onChange={(e) => setFormData({ ...formData, intro: e.target.value })}
            onKeyDown={handleKeyDown}
            className="input-field"
          />
          <p className="field-hint">帮助我们更精准地理解你</p>
        </div>

        {/* 分隔线 */}
        <div className="divider" />

        {/* 提交按钮 */}
        <motion.button
          onClick={handleSubmit}
          disabled={!isValid}
          className={`submit-button ${isValid ? 'enabled' : 'disabled'}`}
          whileHover={isValid ? { scale: 1.02 } : {}}
          whileTap={isValid ? { scale: 0.98 } : {}}
        >
          开始计算 →
        </motion.button>

        {/* 隐私提示 */}
        <p className="privacy-hint">
          🔒 你的信息完全保密，仅用于生成专属 VibeID
        </p>
      </motion.div>

      {/* 返回链接 */}
      <motion.button
        onClick={() => router.back()}
        className="back-button"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.5 }}
      >
        ← 返回
      </motion.button>
    </div>
  )
}
```

