### 2.5 原型卡片组件

```tsx
// components/landing/ArchetypeCard.tsx

'use client'

import { motion } from 'framer-motion'
import { useState } from 'react'

interface ArchetypeCardProps {
  number: string
  name_cn: string
  name_en: string
  tagline: string
  color: string
  onClick?: () => void
}

export function ArchetypeCard({
  number,
  name_cn,
  name_en,
  tagline,
  color,
  onClick
}: ArchetypeCardProps) {
  const [isHovered, setIsHovered] = useState(false)

  return (
    <motion.div
      className="relative w-[180px] h-[280px] cursor-pointer flex-shrink-0"
      onHoverStart={() => setIsHovered(true)}
      onHoverEnd={() => setIsHovered(false)}
      whileHover={{
        y: -8,
        scale: 1.02,
      }}
      onClick={onClick}
    >
      {/* 外框 */}
      <div
        className="absolute inset-0 rounded-xl border-2 transition-colors duration-300"
        style={{
          borderColor: isHovered ? color : 'rgb(var(--border))',
          backgroundColor: 'rgb(var(--card))',
          boxShadow: isHovered ? 'var(--shadow-elevated)' : 'var(--shadow-sm)',
        }}
      >
        {/* 内框装饰 */}
        <div className="absolute inset-2 border border-[rgb(var(--border))] rounded-lg opacity-50" />

        {/* 内容 */}
        <div className="relative h-full flex flex-col items-center justify-center p-4">
          {/* 罗马数字 */}
          <span className="text-caption serif-heading text-[rgb(var(--muted))] mb-2">
            {number}
          </span>

          {/* 原型符号区域 */}
