### 2.6 四维节点组件

```tsx
// components/landing/DimensionNode.tsx

'use client'

import { motion } from 'framer-motion'
import { useState } from 'react'

interface DimensionNodeProps {
  name_cn: string
  name_en: string
  icon: string
  description: string
  color: string
  position: 'top' | 'left' | 'right' | 'bottom'
}

export function DimensionNode({
  name_cn,
  name_en,
  icon,
  description,
  color,
  position
}: DimensionNodeProps) {
  const [isHovered, setIsHovered] = useState(false)

  return (
    <motion.div
      className="relative"
      onHoverStart={() => setIsHovered(true)}
      onHoverEnd={() => setIsHovered(false)}
    >
      {/* 节点 */}
      <motion.div
        className="w-20 h-20 md:w-24 md:h-24 rounded-full flex flex-col items-center justify-center cursor-pointer"
        style={{
          backgroundColor: isHovered ? `${color}30` : `${color}15`,
          border: `2px solid ${isHovered ? color : 'transparent'}`,
        }}
        whileHover={{ scale: 1.1 }}
        transition={{ duration: 0.2 }}
      >
        <span className="text-2xl mb-1">{icon}</span>
        <span className="text-caption font-medium">{name_cn}</span>
      </motion.div>

      {/* 悬浮提示 */}
      {isHovered && (
        <motion.div
          initial={{ opacity: 0, y: 10 }}
          animate={{ opacity: 1, y: 0 }}
          className="absolute z-10 w-48 p-3 bg-[rgb(var(--card))] rounded-lg shadow-lg border border-[rgb(var(--border))]"
          style={{
            top: position === 'top' ? 'calc(100% + 8px)' : undefined,
            bottom: position === 'bottom' ? 'calc(100% + 8px)' : undefined,
            left: position === 'left' ? 'calc(100% + 8px)' : '50%',
            transform: position === 'left' || position === 'right' ? 'translateX(0)' : 'translateX(-50%)',
          }}
        >
          <p className="text-small font-medium mb-1">{name_en}</p>
          <p className="text-caption text-[rgb(var(--muted))]">{description}</p>
        </motion.div>
      )}
    </motion.div>
  )
}
