```tsx
// components/landing/CTAButton.tsx

'use client'

import { motion } from 'framer-motion'
import Link from 'next/link'

interface CTAButtonProps {
  href: string
  children: React.ReactNode
  variant?: 'primary' | 'secondary'
  size?: 'default' | 'large'
}

export function CTAButton({
  href,
  children,
  variant = 'primary',
  size = 'default'
}: CTAButtonProps) {
  const baseStyles = 'inline-flex items-center justify-center rounded-xl font-medium transition-all duration-300'

  const variants = {
    primary: 'bg-[rgb(var(--primary))] text-[rgb(var(--primary-foreground))] hover:shadow-lg',
    secondary: 'bg-[rgb(var(--secondary))] text-[rgb(var(--secondary-foreground))] border border-[rgb(var(--border))] hover:bg-[rgb(var(--background))]',
  }

  const sizes = {
    default: 'px-6 py-3 text-body',
    large: 'px-8 py-4 text-large',
  }

  return (
    <motion.div
      whileHover={{ y: -2 }}
      whileTap={{ scale: 0.98 }}
    >
      <Link
        href={href}
        className={`${baseStyles} ${variants[variant]} ${sizes[size]}`}
      >
        {children}
      </Link>
    </motion.div>
  )
}
