"""
Unified Tool Registry v6 - 统一工具注册表

解决工具注册的硬编码问题：
1. 从 YAML 加载工具定义 (Single Source of Truth)
2. 使用 @tool_handler 装饰器动态注册执行器
3. 消除 if-elif 分发，使用注册表查找

V6.1 更新：
- 全局工具从 skills/core/tools/ 加载
- 移除对 GLOBAL_TOOLS 硬编码的依赖

新增工具只需修改 2 处：
1. skills/{skill_id}/tools/tools.yaml - 工具定义
2. skills/{skill_id}/tools/handlers.py - @tool_handler 执行器

用法：
    from services.agent.tool_registry import tool_handler, ToolRegistry

    @tool_handler("show_bazi_chart")
    async def execute_show_bazi_chart(args: Dict, context: ToolContext) -> Dict:
        return {"chart": ...}

    # CoreAgent 中使用
    tools = ToolRegistry.get_tools_for_skill("bazi")
    result = await ToolRegistry.execute("show_bazi_chart", args, context)
"""
import logging
import importlib
import asyncio
from pathlib import Path
from typing import Dict, Any, List, Optional, Callable, Awaitable, Union
from dataclasses import dataclass, field
from functools import wraps

from .tool_schema import ToolDef, load_skill_tools, get_global_tools

logger = logging.getLogger(__name__)

SKILLS_DIR = Path(__file__).parent.parent.parent / "skills"


@dataclass
class ToolContext:
    """工具执行上下文"""
    user_id: str
    user_tier: str = "free"
    profile: Dict[str, Any] = field(default_factory=dict)
    skill_data: Dict[str, Any] = field(default_factory=dict)
    skill_id: Optional[str] = None
    scenario_id: Optional[str] = None
    conversation_id: Optional[str] = None


# 工具执行器类型: 同步或异步函数
ToolHandler = Callable[[Dict[str, Any], ToolContext], Union[Dict[str, Any], Awaitable[Dict[str, Any]]]]


class UnifiedToolRegistry:
    """
    统一工具注册表

    - 工具定义从 YAML 加载
    - 执行器通过 @tool_handler 装饰器注册
    - 支持全局工具和 Skill 专属工具
    """

    # 工具定义缓存: {tool_name: ToolDef}
    _tool_defs: Dict[str, ToolDef] = {}

    # 工具执行器: {tool_name: handler_function}
    _handlers: Dict[str, ToolHandler] = {}

    # 已加载的 Skill
    _loaded_skills: set = set()

    # 是否已初始化
    _initialized: bool = False

    @classmethod
    def initialize(cls):
        """初始化注册表，加载全局工具"""
        if cls._initialized:
            return

        # 从 core skill 加载全局工具定义
        global_tools = get_global_tools()
        for tool in global_tools:
            tool.is_global = True
            cls._tool_defs[tool.name] = tool

        # 加载 core skill 的 handlers
        cls._load_handlers_module("core")

        # 自动发现并加载所有 Skill 的工具
        cls._auto_discover_skills()

        cls._initialized = True
        logger.info(f"ToolRegistry initialized: {len(cls._tool_defs)} tools, {len(cls._handlers)} handlers")

    @classmethod
    def _auto_discover_skills(cls):
        """自动发现并加载所有 Skill"""
        if not SKILLS_DIR.exists():
            logger.warning(f"Skills directory not found: {SKILLS_DIR}")
            return

        for skill_dir in SKILLS_DIR.iterdir():
            if skill_dir.is_dir() and skill_dir.name != "__pycache__":
                skill_id = skill_dir.name
                cls.load_skill(skill_id)

    @classmethod
    def load_skill(cls, skill_id: str):
        """加载 Skill 的工具定义和执行器"""
        if skill_id in cls._loaded_skills:
            return

        # 1. 从 YAML 加载工具定义
        tools = load_skill_tools(skill_id)
        for tool in tools:
            tool.skill_id = skill_id  # 标记所属 Skill
            cls._tool_defs[tool.name] = tool

        # 2. 尝试加载执行器模块
        cls._load_handlers_module(skill_id)

        cls._loaded_skills.add(skill_id)
        logger.debug(f"Loaded skill '{skill_id}': {len(tools)} tools")

    @classmethod
    def _load_handlers_module(cls, skill_id: str):
        """加载 Skill 的 handlers 模块"""
        handlers_path = SKILLS_DIR / skill_id / "tools" / "handlers.py"
        if not handlers_path.exists():
            logger.debug(f"No handlers module for skill '{skill_id}'")
            return

        try:
            module_name = f"skills.{skill_id}.tools.handlers"
            importlib.import_module(module_name)
            logger.debug(f"Loaded handlers module: {module_name}")
        except ImportError as e:
            logger.warning(f"Failed to import handlers for '{skill_id}': {e}")

    @classmethod
    def register_handler(cls, tool_name: str, handler: ToolHandler):
        """注册工具执行器"""
        cls._handlers[tool_name] = handler
        logger.debug(f"Registered handler: {tool_name}")

    @classmethod
    def get_tool_def(cls, tool_name: str) -> Optional[ToolDef]:
        """获取工具定义"""
        cls.initialize()
        return cls._tool_defs.get(tool_name)

    @classmethod
    def get_tools_for_skill(cls, skill_id: str) -> List[Dict[str, Any]]:
        """获取 Skill 的所有工具 (OpenAI 格式)

        V6.2 更新：按需加载全局工具
        - 如果 SKILL.md 中声明了 global_tools，只加载声明的全局工具
        - 如果没有声明，加载所有全局工具（向后兼容）
        """
        cls.initialize()

        tools = []

        # 获取 Skill 声明需要的全局工具
        from .skill_loader import get_skill_global_tools
        declared_global_tools = get_skill_global_tools(skill_id)

        # 全局工具（从 core skill 加载）
        all_global_tools = get_global_tools()
        for tool in all_global_tools:
            # 如果声明了 global_tools，只加载声明的；否则加载全部
            if declared_global_tools:
                if tool.name in declared_global_tools:
                    tools.append(tool.to_openai_format())
            else:
                # 向后兼容：没有声明则加载全部
                tools.append(tool.to_openai_format())

        # Skill 专属工具（跳过 core，因为已经加载了）
        if skill_id != "core":
            for name, tool in cls._tool_defs.items():
                if getattr(tool, 'skill_id', None) == skill_id:
                    tools.append(tool.to_openai_format())

        # v7.3: 外部工具（从其他 skill 导入）
        from .skill_loader import get_skill_external_tools
        external_tool_names = get_skill_external_tools(skill_id)
        if external_tool_names:
            for name, tool in cls._tool_defs.items():
                if name in external_tool_names:
                    tools.append(tool.to_openai_format())

        return tools

    @classmethod
    def get_all_tools(cls) -> List[Dict[str, Any]]:
        """获取所有已注册的工具 (OpenAI 格式)"""
        cls.initialize()
        return [tool.to_openai_format() for tool in cls._tool_defs.values()]

    @classmethod
    def get_card_type(cls, tool_name: str) -> Optional[str]:
        """获取工具对应的卡片类型"""
        tool = cls.get_tool_def(tool_name)
        return tool.card_type if tool else None

    @classmethod
    def has_handler(cls, tool_name: str) -> bool:
        """检查是否有注册的执行器"""
        cls.initialize()
        return tool_name in cls._handlers

    @classmethod
    async def execute(
        cls,
        tool_name: str,
        args: Dict[str, Any],
        context: ToolContext
    ) -> Dict[str, Any]:
        """执行工具"""
        cls.initialize()

        if tool_name not in cls._handlers:
            logger.warning(f"No handler for tool: {tool_name}")
            return {"error": f"Tool handler not found: {tool_name}"}

        handler = cls._handlers[tool_name]

        try:
            # 支持同步和异步处理器
            result = handler(args, context)
            if asyncio.iscoroutine(result):
                result = await result
            return result
        except Exception as e:
            logger.error(f"Tool execution failed: {tool_name}, error: {e}")
            return {"error": str(e)}

    @classmethod
    def get_schema(cls, skill_id: Optional[str] = None) -> Dict[str, Any]:
        """获取工具 Schema (用于前端)"""
        cls.initialize()

        global_tool_names = {t.name for t in get_global_tools()}

        # v7.3: 获取外部工具名
        external_tool_names = set()
        if skill_id:
            from .skill_loader import get_skill_external_tools
            external_tool_names = set(get_skill_external_tools(skill_id))

        tools = []
        for name, tool in cls._tool_defs.items():
            if skill_id and getattr(tool, 'skill_id', None) != skill_id:
                if name not in global_tool_names and name not in external_tool_names:
                    continue

            tools.append({
                "name": tool.name,
                "description": tool.description,
                "tool_type": tool.tool_type,
                "card_type": tool.card_type,
                "fallback_type": tool.fallback_type,  # V7.1: 降级类型
                "card_props": tool.card_props,  # 新增: 表单配置等
                "parameters": [
                    {
                        "name": p.name,
                        "type": p.type,
                        "required": p.required,
                        "description": p.description,
                        "enum": p.enum,
                        "default": p.default,
                    }
                    for p in tool.parameters
                ],
                "when_to_call": tool.when_to_call,
            })

        return {
            "version": "7.1",
            "skill_id": skill_id,
            "tools": tools,
        }

    @classmethod
    def clear(cls):
        """清除所有注册 (用于测试)"""
        cls._tool_defs.clear()
        cls._handlers.clear()
        cls._loaded_skills.clear()
        cls._initialized = False


def tool_handler(tool_name: str):
    """
    工具执行器装饰器

    用法:
        @tool_handler("show_bazi_chart")
        async def execute_show_bazi_chart(args: Dict, context: ToolContext) -> Dict:
            return {"chart": ...}
    """
    def decorator(func: ToolHandler) -> ToolHandler:
        @wraps(func)
        async def wrapper(args: Dict[str, Any], context: ToolContext) -> Dict[str, Any]:
            result = func(args, context)
            if asyncio.iscoroutine(result):
                return await result
            return result

        # 注册到 Registry
        UnifiedToolRegistry.register_handler(tool_name, wrapper)
        return wrapper

    return decorator


# 便捷别名
ToolRegistry = UnifiedToolRegistry

"""
Tool Schema v6 - 统一工具定义系统

从 YAML 文件加载工具定义，自动生成：
- Python OpenAI 格式工具定义
- TypeScript 类型定义（可选）

V6.1 更新：
- 移除 GLOBAL_TOOLS 硬编码
- 全局工具从 skills/core/tools/tools.yaml 加载
"""
import yaml
import logging
from pathlib import Path
from typing import Dict, Any, List, Optional
from dataclasses import dataclass, field
from functools import lru_cache

logger = logging.getLogger(__name__)

SKILLS_DIR = Path(__file__).parent.parent.parent / "skills"


@dataclass
class ToolParam:
    """工具参数"""
    name: str
    type: str
    required: bool = False
    description: str = ""
    default: Any = None
    enum: Optional[List[str]] = None


@dataclass
class ToolDef:
    """工具定义"""
    name: str
    description: str
    tool_type: str  # collect, compute, display, search
    parameters: List[ToolParam] = field(default_factory=list)
    card_type: Optional[str] = None
    fallback_type: Optional[str] = None  # V7.1: 无专用卡片时的降级类型
    card_props: Optional[Dict] = None
    when_to_call: Optional[str] = None
    implementation: Optional[str] = None
    skill_id: Optional[str] = None  # 所属 Skill ID
    is_global: bool = False  # 是否为全局工具

    def to_openai_format(self) -> Dict[str, Any]:
        """转换为 OpenAI 工具格式"""
        properties = {}
        required = []

        for param in self.parameters:
            prop = {"type": param.type, "description": param.description}
            if param.enum:
                prop["enum"] = param.enum
            if param.default is not None:
                prop["default"] = param.default
            properties[param.name] = prop
            if param.required:
                required.append(param.name)

        return {
            "type": "function",
            "function": {
                "name": self.name,
                "description": self.description,
                "parameters": {
                    "type": "object",
                    "properties": properties,
                    "required": required
                }
            },
            "_metadata": {
                "tool_type": self.tool_type,
                "card_type": self.card_type,
                "fallback_type": self.fallback_type,  # V7.1
                "when_to_call": self.when_to_call
            }
        }


def _parse_yaml_tool(tool_data: Dict, tool_type: str) -> ToolDef:
    """解析 YAML 工具定义"""
    params = []
    for p in tool_data.get("parameters", []):
        params.append(ToolParam(
            name=p["name"],
            type=p.get("type", "string"),
            required=p.get("required", False),
            description=p.get("description", ""),
            default=p.get("default"),
            enum=p.get("enum")
        ))

    return ToolDef(
        name=tool_data["name"],
        description=tool_data.get("description", ""),
        tool_type=tool_type,
        parameters=params,
        card_type=tool_data.get("card_type"),
        fallback_type=tool_data.get("fallback_type"),  # V7.1
        card_props=tool_data.get("card_props"),
        when_to_call=tool_data.get("when_to_call"),
        implementation=tool_data.get("implementation")
    )


@lru_cache(maxsize=16)
def load_skill_tools(skill_id: str) -> List[ToolDef]:
    """从 YAML 加载 Skill 工具定义"""
    tools_file = SKILLS_DIR / skill_id / "tools" / "tools.yaml"
    if not tools_file.exists():
        logger.debug(f"Tools file not found: {tools_file}")
        return []

    try:
        with open(tools_file, "r", encoding="utf-8") as f:
            data = yaml.safe_load(f)

        tools = []
        for tool_type in ["collect", "compute", "display", "search"]:
            for tool_data in data.get(tool_type, []):
                tool = _parse_yaml_tool(tool_data, tool_type)
                tool.skill_id = skill_id
                tool.is_global = (skill_id == "core")
                tools.append(tool)

        logger.debug(f"Loaded {len(tools)} tools for skill: {skill_id}")
        return tools
    except Exception as e:
        logger.error(f"Failed to load tools for {skill_id}: {e}")
        return []


@lru_cache(maxsize=1)
def get_global_tools() -> List[ToolDef]:
    """获取全局工具（从 core skill 加载）"""
    return load_skill_tools("core")


def get_skill_tools_openai(skill_id: str) -> List[Dict[str, Any]]:
    """获取 Skill 工具的 OpenAI 格式"""
    tools = load_skill_tools(skill_id)
    return [t.to_openai_format() for t in tools]


def get_global_tools_openai() -> List[Dict[str, Any]]:
    """获取全局工具的 OpenAI 格式"""
    return [t.to_openai_format() for t in get_global_tools()]


def get_all_tools_for_skill(skill_id: str) -> List[Dict[str, Any]]:
    """获取 Skill 的所有工具（全局 + Skill 专属）"""
    tools = get_global_tools_openai()
    if skill_id != "core":
        tools.extend(get_skill_tools_openai(skill_id))
    return tools


def get_tool_card_type(tool_name: str, skill_id: Optional[str] = None) -> Optional[str]:
    """获取工具对应的卡片类型"""
    # 先查全局工具
    for tool in get_global_tools():
        if tool.name == tool_name:
            return tool.card_type

    # 再查 Skill 工具
    if skill_id:
        for tool in load_skill_tools(skill_id):
            if tool.name == tool_name:
                return tool.card_type

    return None


def clear_tool_cache():
    """清除工具缓存"""
    load_skill_tools.cache_clear()
    get_global_tools.cache_clear()
    logger.info("Tool cache cleared")

"""
JungAstro Skill 工具执行器

架构说明：
- 复用 zodiac skill 的计算服务 (calculate_zodiac)
- 添加荣格心理学解读层 (JungianInterpreter)
- 定义独立的卡片类型 (jungastro_*)

数据获取方式：
- 从 context.profile 获取用户出生信息
- 从 context.skill_data 获取已计算的星盘数据
- 调用 zodiac 的计算函数进行实时计算
"""

import logging
from datetime import datetime
from typing import Dict, Any, Optional

from services.agent.tool_registry import tool_handler, ToolContext

logger = logging.getLogger(__name__)


# ═══════════════════════════════════════════════════════════════════════════════
# 服务层导入 - 复用 zodiac 计算能力
# ═══════════════════════════════════════════════════════════════════════════════

def get_zodiac_chart(context: ToolContext) -> Optional[Dict[str, Any]]:
    """
    从 context 获取用户星盘数据

    优先级：
    1. context.skill_data["zodiac"]["chart"] - 已计算的完整星盘
    2. 使用 birth_info 实时计算
    """
    profile = context.profile or {}
    skill_data = context.skill_data or {}

    # 优先从 skill_data 读取已计算的星盘
    zodiac_data = skill_data.get("zodiac", {})
    if zodiac_data.get("chart"):
        return zodiac_data["chart"]

    # 否则尝试实时计算
    birth_info = profile.get("birth_info", {})
    birth_date = birth_info.get("date")

    if not birth_date:
        return None

    try:
        from skills.zodiac.services import calculate_zodiac
        chart = calculate_zodiac(
            birth_date=birth_date,
            birth_time=birth_info.get("time", "12:00"),
            birth_place=birth_info.get("place", "Shanghai")
        )
        return chart
    except Exception as e:
        logger.error(f"Failed to calculate zodiac chart: {e}")
        return None


def get_jungian_interpreter():
    """获取荣格心理解读器"""
    try:
        from skills.jungastro.services.interpreter import JungianInterpreter
        return JungianInterpreter()
    except ImportError:
        logger.warning("JungianInterpreter not available")
        return None


# ═══════════════════════════════════════════════════════════════════════════════
# 工具执行器
# ═══════════════════════════════════════════════════════════════════════════════

@tool_handler("collect_birth_info")
async def handle_collect_birth_info(args: Dict[str, Any], context: ToolContext) -> Dict[str, Any]:
    """收集用户出生信息用于荣格心理占星分析"""
    fields = [
        {"id": "birthDate", "label": "出生日期", "type": "date", "required": True, "placeholder": ""},
        {"id": "birthTime", "label": "出生时间", "type": "time", "required": True, "placeholder": ""},
        {"id": "birthPlace", "label": "出生地点", "type": "location", "required": True, "placeholder": "城市名"},
    ]

    return {
        "status": "collecting",
        "cardType": "collect_form",
        "formType": "birth_info",
        "title": "请填写出生信息",
        "description": "准确的出生信息是进行荣格心理占星分析的基础",
        "fields": fields,
    }


@tool_handler("show_psychological_portrait")
async def handle_show_psychological_portrait(
    args: Dict[str, Any],
    context: ToolContext
) -> Dict[str, Any]:
    """
    展示心理画像 - V7 委托模式

    复用 zodiac 计算器获取星盘数据，
    然后用 JungianInterpreter 进行心理学解读。
    """
    from services.agent.tool_registry import ToolRegistry

    focus = args.get("focus", "full")

    # 1. 获取用户星盘数据
    chart_data = get_zodiac_chart(context)

    if not chart_data:
        return {
            "status": "error",
            "error": "需要出生信息才能进行心理画像分析",
            "action": "collect_birth_info"
        }

    # 2. 荣格心理解读
    portrait = {
        "ego": _analyze_ego(chart_data),           # 太阳分析
        "unconscious": _analyze_unconscious(chart_data),  # 月亮分析
        "persona": _analyze_persona(chart_data),   # 上升分析
        "functions": _analyze_functions(chart_data),  # 功能分布
    }

    # 3. 如果有解读器，使用更深度的分析
    interpreter = get_jungian_interpreter()
    if interpreter:
        portrait = interpreter.analyze_psychological_portrait(chart_data, focus)

    logger.info(f"Showing psychological portrait via show_card for user {context.user_id}")

    return await ToolRegistry.execute(
        "show_card",
        {
            "card_type": "custom",
            "data_source": {"type": "inline", "data": portrait},
            "options": {"componentId": "jungastro_portrait"}
        },
        context
    )


@tool_handler("show_shadow_analysis")
async def handle_show_shadow_analysis(
    args: Dict[str, Any],
    context: ToolContext
) -> Dict[str, Any]:
    """
    展示阴影分析 - V7 委托模式

    分析困难相位、冥王星配置、12宫内容等。
    """
    from services.agent.tool_registry import ToolRegistry

    chart_data = get_zodiac_chart(context)

    if not chart_data:
        return {
            "status": "error",
            "error": "需要出生信息才能进行阴影分析",
            "action": "collect_birth_info"
        }

    shadow = {
        "difficult_aspects": _find_difficult_aspects(chart_data),
        "pluto_analysis": _analyze_pluto(chart_data),
        "twelfth_house": _analyze_twelfth_house(chart_data),
        "saturn_analysis": _analyze_saturn(chart_data),
        "shadow_patterns": [],
        "integration_path": ""
    }

    logger.info(f"Showing shadow analysis via show_card for user {context.user_id}")

    return await ToolRegistry.execute(
        "show_card",
        {
            "card_type": "custom",
            "data_source": {"type": "inline", "data": shadow},
            "options": {"componentId": "jungastro_shadow"}
        },
        context
    )


@tool_handler("show_individuation_path")
async def handle_show_individuation_path(
    args: Dict[str, Any],
    context: ToolContext
) -> Dict[str, Any]:
    """
    展示个体化路径 - V7 委托模式

    分析土星周期、外行星行运、成长方向。
    """
    from services.agent.tool_registry import ToolRegistry

    chart_data = get_zodiac_chart(context)

    if not chart_data:
        return {
            "status": "error",
            "error": "需要出生信息才能分析个体化路径",
            "action": "collect_birth_info"
        }

