import React, { createContext, useContext, useReducer, useCallback, ReactNode } from 'react'
import type {
  OnboardingStep,
  OnboardingState,
  BirthInfo,
  ConcernType,
  PersonaType,
  BaziData,
  InterviewAnswer,
