const STEP_ORDER: OnboardingStep[] = [
  'landing',       // 0-15ç§’: è¾“å…¥ç”Ÿæ—¥ï¼ˆdankoe è·³è¿‡ï¼‰
  'loading',       // 15-45ç§’: è®¡ç®—ï¼ˆdankoe è·³è¿‡ï¼‰
  'embeddedChat',  // é¦–æ¬¡ç›¸é‡å¯¹è¯
  'conversion',    // å¯¹è¯ç»“æŸå: ä»˜è´¹å¼•å¯¼
]

// dankoe å˜ä½“çš„æ­¥éª¤é¡ºåº
const DANKOE_STEP_ORDER: OnboardingStep[] = [
  'embeddedChat',  // ç›´æ¥è¿›å…¥å¯¹è¯
  'conversion',    // å¯¹è¯ç»“æŸå: ä»˜è´¹å¼•å¯¼
]

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
