  // å¤„ç†å¯¹è¯å¼€å§‹
  const handleConversationStart = useCallback((id: string) => {
    setConversationId(id);
  }, []);

  // åˆå§‹æ¶ˆæ¯ - æ ¹æ®å˜ä½“ä¸åŒ
  // dankoe: ç›´æ¥å¼€å§‹å¯¹è¯
  // å…¶ä»–: è§¦å‘ onboarding skill
  const initialPrompt = useMemo(() => {
    if (hasStartedRef.current) return undefined;
    return variant === 'dankoe' ? 'æˆ‘å‡†å¤‡å¥½äº†' : 'å¼€å§‹';
  }, [variant]);

  useEffect(() => {
    hasStartedRef.current = true;
  }, []);

  // ç›‘å¬ localStorage ä¸­çš„ onboarding_completed æ ‡è®°
  // åç«¯åœ¨æ‰¿è¯ºæ¶ˆæ¯åä¼šè®¾ç½®æ­¤æ ‡è®°
  useEffect(() => {
    const checkComplete = () => {
      if (completionDetectedRef.current) return;

      const completed = localStorage.getItem('vibelife_onboarding_chat_completed');
      if (completed === 'true') {
        completionDetectedRef.current = true;
        localStorage.removeItem('vibelife_onboarding_chat_completed');
        // å»¶è¿Ÿè·³è½¬ï¼Œè®©ç”¨æˆ·çœ‹åˆ°æœ€åçš„æ‰¿è¯ºæ¶ˆæ¯
        setTimeout(() => nextStep(), 2000);
      }
    };

    // æ¯ç§’æ£€æŸ¥ä¸€æ¬¡
    const interval = setInterval(checkComplete, 1000);
    return () => clearInterval(interval);
  }, [nextStep]);

  // æ£€æµ‹ DOM ä¸­çš„æ¶ˆæ¯å†…å®¹æ˜¯å¦åŒ…å«å®Œæˆå…³é”®è¯
  // è¿™æ˜¯ä¸€ä¸ªå¤‡ç”¨æ£€æµ‹æœºåˆ¶
  useEffect(() => {
    const checkMessageContent = () => {
      if (completionDetectedRef.current) return;

      // æŸ¥æ‰¾æœ€åä¸€æ¡ assistant æ¶ˆæ¯
      const messages = document.querySelectorAll('[data-role="assistant"]');
      if (messages.length === 0) return;

      const lastMessage = messages[messages.length - 1];
      const content = lastMessage?.textContent || '';

      // æ£€æŸ¥æ˜¯å¦åŒ…å«å®Œæˆå…³é”®è¯
      const hasCompletionKeyword = COMPLETION_KEYWORDS.some(keyword =>
        content.includes(keyword)
      );

      if (hasCompletionKeyword) {
        completionDetectedRef.current = true;
        // è®¾ç½® localStorage æ ‡è®°
        localStorage.setItem('vibelife_onboarding_chat_completed', 'true');
        // å»¶è¿Ÿè·³è½¬
        setTimeout(() => nextStep(), 3000);
      }
    };

    // ä½¿ç”¨ MutationObserver ç›‘å¬æ¶ˆæ¯å˜åŒ–
    const observer = new MutationObserver(checkMessageContent);
    const chatContainer = document.querySelector('[role="log"]');

    if (chatContainer) {
      observer.observe(chatContainer, {
        childList: true,
        subtree: true,
        characterData: true,
      });
    }

    return () => observer.disconnect();
  }, [nextStep]);

  // å¦‚æœæ²¡æœ‰å…«å­—æ•°æ®ï¼Œæ˜¾ç¤ºé”™è¯¯
  if (!state.baziData) {
    return (
      <LuminousPaper skill="bazi" variant="default" className="min-h-screen">
        <div className="flex items-center justify-center h-screen">
          <div className="text-center">
            <p className="text-text-secondary mb-4">æ•°æ®åŠ è½½ä¸­...</p>
            <button
              onClick={() => window.location.reload()}
              className="text-accent hover:underline"
            >
              åˆ·æ–°é¡µé¢
            </button>
          </div>
        </div>
      </LuminousPaper>
    );
  }

  return (
    <LuminousPaper skill="bazi" variant="default" className="min-h-screen">
      {/* å‘¼å¸å…‰æ™•èƒŒæ™¯ - ä¸ /chat é¡µé¢ä¸€è‡´ */}
      <BreathAura
        skill="bazi"
        intensity="low"
        className="fixed inset-0 pointer-events-none"
      />

      {/* åµŒå…¥å¼ Chat - å®Œå…¨å¤ç”¨ ChatContainer */}
      <div className="relative z-10 h-screen flex flex-col">
        <ChatContainer
          skillId="onboarding"
          skill="bazi"  // UI ä¸»é¢˜
          initialPrompt={initialPrompt}
          onConversationStart={handleConversationStart}
          onInitialPromptSent={() => {
            // åˆå§‹æ¶ˆæ¯å·²å‘é€ï¼Œå¯ä»¥æ·»åŠ è¿½è¸ªé€»è¾‘
          }}
        />
      </div>
    </LuminousPaper>
  );
}

