    const observer = new MutationObserver(checkMessageContent);
    const chatContainer = document.querySelector('[role="log"]');

    if (chatContainer) {
      observer.observe(chatContainer, {
        childList: true,
        subtree: true,
        characterData: true,
      });
    }

    return () => observer.disconnect();
  }, [nextStep]);

  // dankoe ä¸éœ€è¦å‘½ç›˜æ•°æ®ï¼Œå…¶ä»–å˜ä½“éœ€è¦
  const needsBaziData = variant !== 'dankoe';
  if (needsBaziData && !baziData) {
    return (
      <LuminousPaper
        skill={theme.skill as 'bazi' | 'zodiac' | 'tarot' | 'lifecoach'}
        variant="default"
        className="min-h-screen"
      >
        <div className="flex items-center justify-center h-screen">
          <div className="text-center">
            <p className="text-text-secondary mb-4">æ•°æ®åŠ è½½ä¸­...</p>
            <button
              onClick={() => window.location.reload()}
              className="text-accent hover:underline"
            >
              åˆ·æ–°é¡µé¢
            </button>
          </div>
        </div>
      </LuminousPaper>
    );
  }

  return (
    <LuminousPaper
      skill={theme.skill as 'bazi' | 'zodiac' | 'tarot' | 'lifecoach'}
      variant="default"
      className="min-h-screen"
    >
      {/* å‘¼å¸å…‰æ™•èƒŒæ™¯ - å˜ä½“ç‰¹å®šä¸»é¢˜ */}
      <BreathAura
        skill={theme.skill as 'bazi' | 'zodiac' | 'tarot' | 'lifecoach'}
        intensity="low"
        className="fixed inset-0 pointer-events-none"
      />

      {/* åµŒå…¥å¼ Chat - ä¼ é€’ variant åˆ° onboarding skill */}
      <div className="relative z-10 h-screen flex flex-col">
        <ChatContainer
          skillId="onboarding"
          skill={theme.skill as 'bazi' | 'zodiac' | 'tarot' | 'lifecoach'}
          initialPrompt={initialPrompt}
          // ä¼ é€’ variant å…ƒæ•°æ®ç»™åç«¯
          metadata={{ variant }}
          onConversationStart={handleConversationStart}
          onInitialPromptSent={() => {
            // åˆå§‹æ¶ˆæ¯å·²å‘é€
          }}
        />
      </div>
    </LuminousPaper>
