    32â†’    const response = await fetch(backendUrl, {
    33â†’      method: "GET",
    34â†’      headers,
    35â†’    });
    36â†’
    37â†’    const data = await response.json();
    38â†’
    39â†’    return NextResponse.json(data, { status: response.status });
