    12â†’
    13â†’    const response = await fetch(`${API_INTERNAL}/api/v1/account/profile`, {
    14â†’      method: "GET",
    15â†’      headers: {
    16â†’        "Content-Type": "application/json",
    17â†’        Authorization: auth,
    18â†’      },
