    38â†’    }
    39â†’
    40â†’    const body = await request.json();
    41â†’
    42â†’    const response = await fetch(`${API_INTERNAL}/api/v1/account/profile`, {
    43â†’      method: "PUT",
    44â†’      headers: {
    45â†’        "Content-Type": "application/json",
    46â†’        Authorization: auth,
    47â†’      },
    48â†’      body: JSON.stringify(body),
    49â†’    });
    50â†’
    51â†’    const data = await response.json();
    52â†’    return NextResponse.json(data, { status: response.status });
    53â†’  } catch (error) {
    54â†’    console.error("Profile PUT error:", error);
    55â†’    return NextResponse.json(
    56â†’      { detail: "Internal server error" },
    57â†’      { status: 500 }
    58â†’    );
    59â†’  }
    60â†’}
