"use client";

/**
 * ChatContainer - v5.2 性能优化版
 *
 * 优化点:
 * 1. 使用 content-visibility 优化长消息列表 (Vercel rule: rendering-content-visibility)
 * 2. Memoize ChatEmptyState 组件 (Vercel rule: rerender-extract-component)
 * 3. 使用缓存的 localStorage 工具 (Vercel rule: js-cache-storage)
 */

import { useRef, useEffect, useMemo, useCallback, useState, memo } from "react";
import type { Message } from "ai/react";
import { toast } from "sonner";
import { ChatMessage } from "./ChatMessage";
import { ChatInput } from "./ChatInput";
import { InsightCard, InsightType } from "@/components/insight/InsightCard";
import { VibeGlyph, BreathAura, type SkillType } from "@/components/core";
import { DailyGreeting } from "@/components/greeting/DailyGreeting";
import { ChatEmptyStateWithDashboard } from "./ChatEmptyStateWithDashboard";
import { useVibeChat, type SkillId } from "@/hooks/useVibeChat";
import { getLocalStorageJSON, getLocalStorage, setLocalStorage } from "@/utils/storage";
import type { DashboardDTO } from "@/types/dashboard";

interface InsightData {
  id: string;
  insight_type: InsightType;
  title: string;
  content: string;
}

/**
 * Extract text content from AI SDK Message
 */
function getMessageContent(message: Message): string {
  return message.content || '';
}

export type VoiceMode = "warm" | "sarcastic" | "wise";

export interface ChatContainerProps {
  /**
   * Skill ID - 可选
   * - 传入时：后端直接使用该 skill
   * - 不传时：后端 CoreAgent 通过 LLM 自动路由
   */
  skillId?: string;
  /**
   * Scenario/Rule ID - 可选
   * - 传入时：后端直接加载对应的 Rule 文件
   * - 不传时：后端根据 tags 匹配或使用默认 scenario
   */
  scenario?: string;
  /**
   * 用于 UI 展示的 skill（主题、空状态等）
   * 当 skillId 不传时，此字段仍可用于 UI 展示
   */
  skill?: SkillType;
  conversationId?: string;
  voiceMode?: VoiceMode;
  onConversationStart?: (id: string) => void;
  /**
   * 从 URL 传入的初始消息（如从通知跳转）
   */
  initialPrompt?: string | null;
  /**
   * 初始消息发送后的回调
   */
  onInitialPromptSent?: () => void;
  /**
   * Dashboard 数据（用于空状态）
   */
  dashboardData?: DashboardDTO | null;
  /**
   * Dashboard 数据加载状态
   */
  isDashboardLoading?: boolean;
  /**
   * 签到回调
   */
  onCheckIn?: () => Promise<void>;
  /**
   * 杠杆打勾回调
   */
  onToggleLever?: (leverId: string) => Promise<void>;
  /**
   * 大石头打勾回调
   */
  onToggleRock?: (rockId: string) => Promise<void>;
}

// ═══════════════════════════════════════════════════════════════════════════
// ChatEmptyState - LUMINOUS PAPER Design System
// 空状态：居中 VibeGlyph + 技能感知文案 + 呼吸光晕
// ═══════════════════════════════════════════════════════════════════════════

