() => {
  // 检查消息列表
  const messageList = document.querySelector('[role="log"][aria-label="对话消息"]');
  if (!messageList) return { error: 'Message list not found' };

  // 检查是否有消息
  const messages = messageList.querySelectorAll('[role="article"], .message, [class*="ChatMessage"]');

  // 检查是否有空状态组件
  const emptyState = messageList.querySelector('[class*="ChatEmptyState"]');
  const dailyGreeting = messageList.querySelector('[class*="DailyGreeting"]');
  const vibeGlyph = messageList.querySelector('[class*="VibeGlyph"]');

  // 检查可见内容
  const visibleContent = messageList.innerText.substring(0, 500);

  return {
    hasMessages: messages.length > 0,
    messageCount: messages.length,
    hasEmptyState: !!emptyState,
    hasDailyGreeting: !!dailyGreeting,
    hasVibeGlyph: !!vibeGlyph,
    messageListHTML: messageList.innerHTML.substring(0, 1000),
    visibleContent
  };
}

