"use client";

/**
 * EnhancedFortuneCard - 个性化今日能量盘
 *
 * 优化点：
 * 1. 根据用户当前关注领域个性化
 * 2. 行动导向的建议（而非通用描述）
 * 3. 温暖的渐变背景
 * 4. 能量条替代星级评分
 */

import { useMemo } from "react";
import { cn } from "@/lib/utils";

interface FocusedArea {
  name: string;
  score: number;
  emoji: string;
  tip: string;
}

interface FortuneData {
  headline: string;          // "今天适合推进你的创作项目"
  insights: string[];        // 行动建议
  rating: {
    overall: number;
    focused_area?: FocusedArea;
  };
}

interface EnhancedFortuneCardProps {
  data: FortuneData;
  className?: string;
}

export function EnhancedFortuneCard({ data, className }: EnhancedFortuneCardProps) {
  const { headline, insights, rating } = data;

  // 如果没有 focused_area，使用整体评分
  const displayArea = rating.focused_area || {
    name: "综合能量",
    score: rating.overall,
    emoji: "🌟",
    tip: headline
  };

  return (
    <div className={cn(
      "bg-gradient-to-br from-amber-50 to-orange-50 rounded-lg p-3 mb-3 border border-amber-100",
      className
    )}>
      {/* 个性化标题 */}
      <div className="flex items-center gap-2 mb-2">
        <span className="text-lg">{displayArea.emoji}</span>
        <span className="text-sm font-semibold text-amber-900">
          {displayArea.tip || headline}
        </span>
      </div>

      {/* 当前关注领域的能量评分 */}
      <div className="flex items-center gap-2 mb-3">
        <span className="text-xs text-amber-700 min-w-[60px]">
          {displayArea.name}
        </span>
        <EnergyBar value={displayArea.score} max={5} />
        <span className="text-xs font-medium text-amber-900">
          {displayArea.score}/5
        </span>
      </div>

      {/* 行动建议 */}
      <div className="space-y-1">
        {insights.map((insight, i) => (
          <div key={i} className="flex items-start gap-2 text-xs text-amber-800">
            <span className="text-amber-500 mt-0.5">•</span>
            <span>{insight}</span>
          </div>
        ))}
      </div>
    </div>
  );
}

// 能量条组件
function EnergyBar({ value, max }: { value: number; max: number }) {
  const percentage = (value / max) * 100;

  return (
    <div className="flex-1 h-2 bg-amber-200 rounded-full overflow-hidden">
      <div
        className="h-full bg-gradient-to-r from-amber-400 to-orange-500 rounded-full transition-all duration-500"
        style={{ width: `${percentage}%` }}
      />
    </div>
  );
}

"use client";

/**
 * ConversationalLifecoachCard - 对话式 Lifecoach 进度卡片
 *
 * 优化点：
 * 1. 对话式文案（而非任务列表）
 * 2. 情感化进度反馈
 * 3. 突出未完成的行动项
 * 4. 北极星提醒
 */

import { useMemo } from "react";
import { cn } from "@/lib/utils";

interface Rock {
  id: string;
  text: string;
  role?: string;
  completed: boolean;
}

interface Lever {
  id: string;
  text: string;
  completed: boolean;
}

interface LifecoachData {
  northStar?: string;
  weekRocks: Rock[];
  todayLevers: Lever[];
}

interface ConversationalLifecoachCardProps {
  data: LifecoachData;
  onShowMore?: () => void;
  className?: string;
}

export function ConversationalLifecoachCard({
  data,
  onShowMore,
  className
}: ConversationalLifecoachCardProps) {
  const { northStar, weekRocks, todayLevers } = data;

  const completedRocks = useMemo(() =>
    weekRocks.filter(r => r.completed).length,
    [weekRocks]
  );

  const completedLevers = useMemo(() =>
    todayLevers.filter(l => l.completed).length,
    [todayLevers]
  );

  // 生成情感化进度反馈
  const progressMessage = useMemo(() => {
    return generateProgressMessage(
      completedRocks,
      weekRocks.length,
      completedLevers,
      todayLevers.length
    );
  }, [completedRocks, weekRocks.length, completedLevers, todayLevers.length]);

  // 未完成的今日杠杆
  const uncompletedLevers = useMemo(() =>
    todayLevers.filter(l => !l.completed),
    [todayLevers]
  );

  return (
    <div className={cn(
      "bg-gradient-to-br from-green-50 to-emerald-50 rounded-lg p-3 border border-green-100",
      className
    )}>
      {/* 北极星提醒 */}
      {northStar && (
        <div className="flex items-start gap-2 mb-2">
          <span className="text-base">⭐</span>
          <p className="text-xs text-green-800 flex-1">
            记得吗？你想 <strong>{northStar}</strong>
          </p>
        </div>
      )}

      {/* 进度反馈（对话式） */}
      <p className="text-sm text-green-900 mb-3 leading-relaxed">
        {progressMessage}
      </p>

      {/* 本周重要事（只显示前 2 个） */}
      {weekRocks.length > 0 && (
        <div className="mb-3">
          <p className="text-xs text-green-700 mb-1.5 font-medium">本周你在推进的事：</p>
          <div className="space-y-1">
            {weekRocks.slice(0, 2).map(rock => (
              <div key={rock.id} className="flex items-center gap-2 text-xs text-green-800">
                <span className={rock.completed ? "text-green-500" : "text-green-300"}>
                  {rock.completed ? "✓" : "•"}
                </span>
                <span className={rock.completed ? "line-through opacity-60" : ""}>
                  {rock.text}
                </span>
                {rock.role && (
                  <span className="text-green-600 text-[10px] opacity-70">
                    ({rock.role})
                  </span>
                )}
              </div>
            ))}
          </div>

          {weekRocks.length > 2 && onShowMore && (
            <button
              onClick={onShowMore}
              className="text-xs text-green-600 mt-1.5 hover:text-green-700 transition-colors"
            >
              还有 {weekRocks.length - 2} 件事 →
            </button>
          )}
        </div>
      )}

      {/* 今日重点（高亮未完成的） */}
      {uncompletedLevers.length > 0 && (
        <div className="bg-white/60 rounded-lg px-3 py-2 backdrop-blur-sm">
          <p className="text-xs text-green-700 mb-1.5 font-medium">
            今天最重要的{uncompletedLevers.length > 1 ? `${uncompletedLevers.length}件事` : ''}：
          </p>
          <div className="space-y-1">
            {uncompletedLevers.map(lever => (
              <div key={lever.id} className="flex items-center gap-2 text-xs text-green-900">
                <span className="text-green-500">→</span>
                <span className="font-medium">{lever.text}</span>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* 已完成提示 */}
      {uncompletedLevers.length === 0 && todayLevers.length > 0 && (
        <div className="bg-green-100/50 rounded-lg px-3 py-2 text-center">
          <p className="text-xs text-green-800">
            ✨ 今日计划全部完成！你太棒了！
          </p>
        </div>
      )}
    </div>
  );
}

// 生成情感化进度反馈
function generateProgressMessage(
  completedRocks: number,
  totalRocks: number,
  completedLevers: number,
  totalLevers: number
): string {
  const rockProgress = totalRocks > 0 ? completedRocks / totalRocks : 0;
  const leverProgress = totalLevers > 0 ? completedLevers / totalLevers : 0;

  // 高进度（>60%）
  if (rockProgress >= 0.6 && leverProgress >= 0.6) {
    return "这周推进得很稳！保持这个节奏 💪";
  }

  // 本周进度好，今日还没开始
  if (rockProgress >= 0.5 && totalLevers > 0 && completedLevers === 0) {
    return "本周进度不错！今天也来点行动吧 🌱";
  }

  // 今日完成度高
  if (leverProgress >= 0.6) {
    return "今天状态很好！继续保持 ✨";
  }

  // 中等进度（30-60%）
  if (rockProgress >= 0.3 || leverProgress >= 0.3) {
    return "还有一些重要的事等着你，今天可以推一把 🎯";
  }

  // 今日还没开始
  if (totalLevers > 0 && completedLevers === 0) {
    return "今天还没开始行动？从最小的一步开始吧 🌱";
  }

  // 默认
  return "本周的重点都在这里，准备好了吗？";
}

