input.content:
"use client";

/**
 * DynamicQuickActions - 动态快捷操作按钮
 *
 * 根据用户状态自动调整显示的按钮：
 * 1. 未签到 → 显示"开始今天"
 * 2. 有未完成杠杆 → 显示具体行动项
 * 3. 周五/周六/周日 → 显示"周复盘"
 * 4. 无北极星 → 显示"找到方向"
 */

import { useMemo, useCallback } from "react";
import { useRouter } from "next/navigation";
import { cn } from "@/lib/utils";
import { toast } from "sonner";

interface StatusData {
  streak: number;
  checkedIn: boolean;
  energy?: number;
}

interface LifecoachData {
  northStar?: string;
  todayLevers: Array<{ id: string; text: string; completed: boolean }>;
}

interface QuickAction {
  id: string;
  icon: string;
  label: string;
  prompt?: string;      // 发送到对话的 prompt
  route?: string;       // 或跳转到特定页面
  handler?: () => Promise<void>; // 或执行特定操作
}

interface DynamicQuickActionsProps {
  status: StatusData;
  lifecoach: LifecoachData;
  onCheckIn: () => Promise<void>;
  onSendPrompt?: (prompt: string) => void;
  className?: string;
}

export function DynamicQuickActions({
  status,
  lifecoach,
  onCheckIn,
  onSendPrompt,
  className
}: DynamicQuickActionsProps) {
  const router = useRouter();

  // 根据用户状态动态生成按钮
  const actions = useMemo(() => {
    const list: QuickAction[] = [];

    // 1. 签到（未签到时显示）
    if (!status.checkedIn) {
      list.push({
        id: "checkin",
        icon: "🌅",
        label: "开始今天",
        handler: async () => {
          await onCheckIn();
          toast.success(`签到成功！连续 ${status.streak + 1} 天 🔥`);
        }
      });
    }

    // 2. 今日行动（有未完成杠杆时）
    const uncompletedLevers = lifecoach.todayLevers.filter(l => !l.completed);
    if (uncompletedLevers.length > 0) {
      const firstLever = uncompletedLevers[0];
      // 截取前 8 个字符
      const shortText = firstLever.text.length > 8
        ? firstLever.text.slice(0, 8) + "..."
        : firstLever.text;

      list.push({
        id: "today_action",
        icon: "💪",
        label: shortText,
        prompt: `帮我完成：${firstLever.text}`
      });
    }

    // 3. 周复盘（周五/周六/周日显示）
    const today = new Date().getDay();
    if ([5, 6, 0].includes(today)) {
      list.push({
        id: "weekly_review",
        icon: "📝",
        label: "周复盘",
        route: "/chat?skill=lifecoach&scenario=weekly-review&prompt=周复盘"
      });
    }

    // 4. 设定目标（没有北极星时）
    if (!lifecoach.northStar) {
      list.push({
        id: "set_vision",
        icon: "⭐",
        label: "找到方向",
        route: "/chat?skill=lifecoach&scenario=dankoe&prompt=我想找到人生方向"
      });
    }

    // 5. 默认：随便聊聊（作为兜底）
    if (list.length < 3) {
      list.push({
        id: "chat",
        icon: "💬",
        label: "随便聊聊",
        prompt: "我想和你聊聊"
      });
    }

    // 最多显示 3 个按钮
    return list.slice(0, 3);
  }, [status, lifecoach]);

  const handleAction = useCallback(async (action: QuickAction) => {
    if (action.handler) {
      // 执行自定义操作（如签到）
      await action.handler();
    } else if (action.route) {
      // 跳转到特定页面
      router.push(action.route);
    } else if (action.prompt && onSendPrompt) {
      // 发送 prompt 到对话
      onSendPrompt(action.prompt);
    }
  }, [router, onSendPrompt]);

  return (
    <div className={cn("flex gap-2", className)}>
      {actions.map((action) => (
        <button
          key={action.id}
          onClick={() => handleAction(action)}
          className="flex-1 px-3 py-2 rounded-xl bg-gradient-to-r from-emerald-500 to-green-600 text-white text-sm font-medium shadow-sm hover:shadow-md hover:scale-105 transition-all duration-200"
        >
          <span className="mr-1">{action.icon}</span>
          <span>{action.label}</span>
        </button>
      ))}
    </div>
  );
}

